css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://learn2.acseducation.edu.au/grade/report/grader/") {
  div.gradeparent{
    position:relative;
    top:-50px;
    left:0px;
  }
  div.urlselect{
    position: absolute;
    top:-0px;
    left:250px;
    z-index:2;
  }
  div.header.user.cell.c0{
    width:auto!important;
    height:25px!important;
    line-height:24px!important;
    overflow:hidden;
    padding:0px!important;
    margin:0px!important;
    padding-top:2px!important;
    padding-bottom:2px!important;
  } 
  div.header.user.cell.c0:hover{
    position:relative;
    width:400px!important;
    height:24px!important;
    line-height:22px!important;
    overflow:hidden;
    background: rgba(23,34,125,.21)!important;
    z-index:10;
  } 

  th.header.user.cell.c0{
    width:400px!important;
    height:2px!important;
    overflow:hidden;
    white-space: nowrap;
    padding:0px!important;;
    margin:0px!important;;
  }
  td, tr{margin:0px!important;;padding:0px!important;}
  td{padding-top:2px!important;padding-bottom:2px!important;}
  tr:hover{
    background-color: rgba(23,34,125,.21)!important;
    background: rgba(23,34,125,.21)!important;
  }
  
}
