css:
@-moz-document domain("imageform.se") {
/*--------------------------------------------------*/
/*Title: Image & Form - Dark Mode (Work In Progress)*/
/*                                                  */
/*Version: 1.0.1                                    */
/*Last Update: [2017-10-02]  (yyyy-MM-dd)           */
/*                                                  */
/*Coded By: Pierre-Marc Coursol de Carufel          */
/*--------------------------------------------------*/

/* Dark Scrollbar - Chrome -------------------------*/
*::-webkit-scrollbar {
    width: 10px ! important;
    height: 10px ! important;
    background: #1e1e1e ! important;
    border: 1px solid #252525 ! important;
}

*::-webkit-scrollbar-button {
display: none ! important;
}

*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
border: 0 ! important;
box-shadow: none ! important;
}

*::-webkit-scrollbar-thumb {
    min-height: 28px ! important;
    background: #3a3a3a ! important;
}

*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
background: #292929 ! important;
}
/*--------------------------------------------------*/

/*Page Content --------------------------------------*/
/*Backgrounds*/
.template-page.content, .main_color, .av_default_container_wrap, .container_wrap{
    background-color: #161616 !important;
}

/*header*/
div#header_main, .avia-menu.av-main-nav-wrap, div#header_main_alternate, ul#mobile-advanced, .inner_tooltip, input#s, .avia-search-tooltip.avia-tt {
    background-color: #101010 !important;
}

input#s, span.ajax_search_title, span.ajax_search_excerpt{
    color: white !important;
}

/*header navbar - mobile*/
a#advanced_menu_toggle, a#advanced_menu_hide {
    background-color: #646464;
    border-color: black;
}

/*header navbar - mobile - mouse hover*/
a#advanced_menu_toggle:hover, a#advanced_menu_hide:hover, li.menu-item > a:hover {
    background-color: #323232 !important;
    border-color: black;
}

/*#323232*/

/*Text*/
h1, h2, h3, h4, h5, h6, a, p, strong, time.av-magazine-time.updated, .av-magazine-content.entry-content, .avia_textblock {
    color: white !important;
}
/*--------------------------------------------------*/

/*Games Img Selection*/
img.avia_start_animation{
    background-color: #101010 !important;
    border-color: black !important;
}

/*Pagination*/
nav.pagination > a{
    background-color: #646464 !important;
    color: white !important;
}

nav.pagination > span.current{
    background-color: #646464 !important;
    color: #b4b4ff !important;
}

/*Contact Form*/
input.text_input, textarea.text_area{
    background-color: #282828 !important;
    color: white !important;
}

/*Header Img Fix*/
main.template-page.content.av-content-full.alpha.units {
    background-color: transparent !important;
}

/*Twitter Color Fix*/
a.twitter.avia-team-icon {
    background-color: #1ca1f2 !important;
}

a.avia-team-icon {
    background-color: gray !important;
}

/*Email Sended*/
h3.avia-form-success {
    color: black !important;
}
}
