    body {
        color: #000;
        line-height: 1.6;
        hyphens: auto;
        background-color: #d8d8d8;
    }
    
    body, a, div, .section .section-title h2 {
        font-family: Merriweather Sans, Verdana, sans-serif;
    }
    
    .content-body p {
        line-height: 1.5;
    }

    .main {
        background-color: #fff;
        box-shadow: 0px 0px 10px rgb(85, 85, 85);
        padding-bottom: 10px;
        max-width: 1200px;
    }
    
    .posts-page { 
        margin-bottom: 10px;
    }
    
    .header-wrapper {
        margin-bottom: 0;
        box-shadow: 0px 0px 10px rgb(85, 85, 85);
        background-color: #fff;
    }
    
    .header > div:first-child {
        background-color: #fff !important;
    }
    
    .posts-page .posts-page-content-header {
        max-width: 800px;
    }
    
    .posts-page .posts-page-content-body {
        width: 660px;
        text-align: justify;
    }
    
    .comments-node p {
        text-align: left;
        margin: 1em 0 !important;;
    }
    
    .posts-page-content-header-title h1 {
        line-height: 1.3;
    }
    
    .posts-page .posts-page-content-header .posts-page-content-header-voting .vote .downvote-button span.material-icons,
    .posts-page .posts-page-content-header .posts-page-content-header-voting .vote .upvote-button span.material-icons,
    .posts-page .posts-page-content-footer .posts-page-content-footer-voting .vote .downvote-button span.material-icons,
    .posts-page .posts-page-content-footer .posts-page-content-footer-voting .vote .upvote-button span.material-icons {
        color: #ccc !important;
    }
    
    .comments-item-vote .vote .downvote-button .material-icons,
    .comments-item-vote .vote .upvote-button .material-icons {
        color: #aaa !important;
    }
    
    .posts-page .posts-page-content-footer .posts-page-content-footer-voting .downvoted .downvote-button span.material-icons {
        color: #802727 !important;
        text-shadow: 0 0 20px #802727;
    }

    .posts-page .posts-page-content-footer .posts-page-content-footer-voting .upvoted .upvote-button span.material-icons {
        color: #028800 !important;
        text-shadow: 0 0 20px #028800;
    }

    .posts-page .posts-page-content-header .posts-page-content-header-voting .downvoted .downvote-button span.material-icons {
        color: #802727 !important;
        text-shadow: 0 0 10px #802727;
    }
    
    .posts-page .posts-page-content-header .posts-page-content-header-voting .upvoted .upvote-button span.material-icons {
        color: #028800 !important;
        text-shadow: 0 0 10px #028800;
    }
    
    .comments-item {
        color: #000;
    }
    
    .comments-item-vote .upvoted .upvote-button span.material-icons {
        color: #028800 !important;
        text-shadow: 0 0 3px #028800, 0 0 5px #028800;
    }
    
    .comments-item-vote .downvoted .downvote-button span.material-icons {
        color: #802727 !important;
        text-shadow: 0 0 3px #802727, 0 0 5px #802727;
    }
    
    .comments-node {
        margin-top: 2em;
    }
    
    .comments-node .comments-node {
        margin-top: 1.25em;
    }
    
    .comments-item-text {
        padding-right: 10px;
        text-align: justify;
    }
    
    .comments-item-text p {
        line-height: 1.45 !important;
    }
    
    .comments-item-meta {
        background-color: #dae6d7;
        padding-left: 6px;
        font-family: Trebuchet MS, Helvetica, sans-serif;
        margin-left: -15px;
    }
    
    .comments-item-meta div, .comments-item-meta a {
        font-family: Trebuchet MS, Helvetica, sans-serif;
    }
    
    .comments-item .comments-item-meta a.users-name {
        color: #000;
        font-size: 1.1em;
    }
    
    .comments-item .comments-item-bottom {
        font-family: Trebuchet MS, Helvetica, sans-serif;
    }
    
    .comments-node, .comments-node:hover {
        border-left-color: transparent;
        padding-bottom: 4px;
        outline: 1px solid #ccc;
    }
    
    .comments-node .comments-node {
        border-left-color: transparent;
        background-color: #eaf4e8;
    }
    
    .comments-node .comments-node .comments-node {
        border-left-color: transparent;
        background-color: #fff;
    }
    
    .comments-node .comments-node .comments-node .comments-node {
        border-left-color: transparent;
        background-color: #eaf4e8;
    }
    
    .comments-node .comments-node .comments-node .comments-node .comments-node {
        border-left-color: transparent;
        background-color: #fff;
    }
    
    .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node {
        border-left-color: transparent;
        background-color: #eaf4e8;
    }
    
    .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node {
        border-left-color: transparent;
        background-color: #fff;
    }
    
    .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node {
        border-left-color: transparent;
        background-color: #eaf4e8;
    }
    
    .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node {
        border-left-color: transparent;
        background-color: #fff;
    }
    
    .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node {
        border-left-color: transparent;
        background-color: #eaf4e8;
    }
    
    .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node .comments-node {
        border-left-color: transparent;
        background-color: #fff;
    }
    
    .posts-page-comments-title-component .comments-views-dropdown {
        color: green;
    }
    
    .section .section-title .highlight-date-selector-date {
        color: green;
    }
    
    * {
        -webkit-transition: none !important;
        transition: none !important;
    }
    
    .posts-comments-thread-linked-comment .comment-wrapper {
        margin-top: 0;
    }
    
    .comment-new .comments-node, .comment-new .comments-node:hover {
        border-left-color: transparent;
        outline: 1px solid green;
        box-shadow: 0 0 2px #56a400, 0 0 4px #56a400, 0 0 8px #56a400;
    }
    
    .content-body blockquote {
        font-size: 0.9em;
        color: #000;
        border-left: 5px solid #cdd7cb;
        margin-left: 2px !important;
        margin-bottom: 1em;
        padding-left: 10px;
        padding-top: 2px;
        padding-bottom: 2px;
    }
    
    .content-body blockquote p {
        font-size: 1em !important;
    }
    
    .content-body blockquote p:last-of-type {
        margin-bottom: 0 !important;
    }
    
    .content-body blockquote + p {
        margin-top: 1em;
    }
    
    .content-body h1 {
        text-align: left;
        line-height: 1.2;
        font-style: italic;
        font-weight: bold;
    }
    
    .content-body h3 {
        margin-top: 2em;
        text-align: left;
    }
    
    .content-body h2 {
        margin-top: 1.5em;
        font-style: normal;
        font-weight: bold;
        text-align: left;
    }
    
    .comments-item {
        margin-bottom: 2px;
    }
    
    .comments-item .comments-item-body .comments-item-text ul {
        padding-left: 37px !important;
    }
    
    .comments-item .comments-item-body .comments-item-text ol {
        padding-left: 40px !important;
    }
    
    .comments-node li {
        padding: 0;
    }
    
    .comments-node li:last-of-type p:last-of-type {
        margin-bottom: 0 !important;
    }
}
