css:
@-moz-document domain("web.kiwari.id"), domain("qisme-web-stag.herokuapp.com"), domain("qisme-web.herokuapp.com") {
body {
    background: #bdc3c7;
}
.qisme-container {
    min-height: 0;
    background: #fafafa;
    width: 1010px;
    /*
    opacity: 0.7;
    filter: alpha(opacity=60);
    */
}
#app.application--light {
    background: url(https://images.unsplash.com/photo-1465311530779-5241f5a29892) fixed center !important;
    background-size: cover !important;
    background-color: none;
}
.sidebar__header {
    background: #34495e;
    color: #ffffff;
}
.sidebar__content {
    background: #ecf0f1;
    opacity: 1;
}
.sidebar__content div {
    /*background: #ecf0f1 !important;*/
    
    max-width: 316px !important;
}
.sidebar__footer {
    background: #34495e;
    color: #ffffff;
}
.conversation-detail__header {
    background: #34495e;
    color: #ffffff;
}
ul#messages__comments {
    background: #ededed;
}
.qcw-header {
    line-height: 1.5em;
}
.list__tile__content {
    opacity: 1;
}
.list__tile__title {
    color: #2c3e50;
}
.list__tile__sub-title {
    color: #7f8c8d !important;
}
.comment--me .qcw-comment__message {
    background: #fffbda;
}
.comment--parent.comment--me .qcw-comment__message:after,
.comment--parent .qcw-comment__message:after {
    border-top-color: #fffbda;
}
.qcw-comment__message {
    background: #bdc3c7;
    opacity: 1;
    color: #2c3e50;
}
.comment--parent .qcw-comment__message:after {
    border-top-color: #bdc3c7;
}
.reply-wrapper {
    background: #ecf0f1;
}
.reply-wrapper:after {
    background: #34495e;
}
.reply-sender {
    color: #34495e;
    font-weight: 500;
}
ul#messages__comments::-webkit-scrollbar-thumb {
    background: #34495e;
}
.list__tile {
    font-size: 14px;
}
.qcw-comment__content {
    font-size: 12px;
}
}
