css:
@-moz-document url-prefix("https://cyanmod.pl/") {
/* tlo */
body, .td-container-wrap {
	background-color: #000;
}
/* tekst */
.widget a, .td-post-author-name a, [class*="td-icon-"], .td-author-url a {
	color: #eee;
}
body, .td-page-content, .td-post-content, .wpb_text_column, .td_block_text_with_title, .woocommerce #tab-description, .mce-content-body, .td-post-small-box a, .td-post-next-prev-content a, .td-author-name a,
h1 > a, h2 > a, h3 > a, h4 > a, h5 > a, h6 > a, h1, h2, h3, h4, h5, h6,
.td-more-articles-box-title, .author-box-wrap .td-social-icon-wrap .td-icon-font, .td-mega-span h3 a {
	color: #ccc;
}

.td-instagram-button {
	border: 1px solid #151515;
	color: #ccc;
}
.td-instagram-button:hover {
	border-color: #444;
}
.td_ajax_load_more {
	border: 1px solid #151515;
}
.td-load-more-wrap a {
	color: #ccc;
}
.widget_recent_comments .recentcomments, .td_wp_recentcomments .recentcomments {
	border-bottom: 1px dashed #151515;
}
.widget_recent_comments, .td_wp_recentcomments {
	color: #bbb;
}
.td-post-sharing-bottom, .author-box-wrap, .td-next-prev-wrap a, .td-post-small-box a {
	border: 1px solid #151515;
}
.td-more-articles-box {
	background-color: #111;
	border-color: #151515;
}
/* top menu */
.td-mega-menu ul.sub-menu {
	border: 1px solid #151515;
}
.td_mega_menu_sub_cats:after {
	background-color: #151515;
}
.td_mega_menu_sub_cats {
	background-color: #111;
}
.td_mega_menu_sub_cats a {
	color: #ddd;
}
.td-container-border {
	background: #080808;
}
.td_mega_menu_sub_cats .cur-sub-cat {
	background-color: #222;
	border-top: 1px solid #151515;
	border-bottom: 1px solid #151515;
}
.td_mega_menu_sub_cats .cur-sub-cat:before {
	background-color: #222;
}
}

@-moz-document url-prefix("https://disqus.com/embed/comments/?base=default&f=cyanogenmodpl") {
/* DISQUS */
body, .tab-conversation.active>a, .comment__footer .author, .comment__header .author, .nav-tab>a, .dropdown-toggle, .discovery-post .top-comment p, .discovery-col-header h2 {
	color: #fff!important;
}
.nav-primary {
	border-bottom: 2px solid rgba(255, 255, 255, 0.08);
}
.disqus-footer__wrapper {
	border-top: 2px solid rgba(255, 255, 255, 0.08);
}
h5, h6, .comment__footer .vote-down, .comment__footer a, .comment__header .vote-down, .comment__header a {
	color: rgba(255,255,255,.5);
}
.comment__footer .vote-down:hover, .comment__footer a:hover, .comment__header .vote-down:hover, .comment__header a:hover {
	color: rgba(255,255,255,.85);
}
.discovery-post .top-comment .user {
	color: #288ce4;
}
.discovery-post .meta {
	color: #777;
}
.discovery-col-header h2 strong {
	color: #ff8888;
}
.disqus-footer__link {
	color: rgba(255,255,255,.5);
}
.disqus-footer__logo .disqus-footer__link {
	background-position: 0 -54.5px;
}
}
