css:
@-moz-document url-prefix("https://www.waze.com/forum/") {
.waze-header {
	background-color: #bedce5;
	box-shadow: 0 1px 3px 0 #000;
}
.waze-header .navigation {
	position: inherit;
	float: right;
}
.waze-header-contents a,
.waze-header .navigation {
	color: black;
}
.waze-header-logo {
	background-image: url(https://editor-assets.waze.com/beta/img/wazelogo073d2c7b73471e7fdbe282ec26b41c85.png);
}
#content_wrapper,
.waze-header-contents {
	width: 1200px;
}
h2 {
	font-size: 2.5em;
}
.headerbar {
	background: none;
	color: black;
}
.navbar {
	background-color: #FFF;
	border: 1px solid #000;
	border-radius: 10px;
}
.headerbar::after {
	content: '';
	clear: both;
	display: block;
}
.navbar ul.navlinks {
	border-bottom-color: #AAA;
}
input {
	border-radius: 3px;
	padding: 5px !important;
}
input.search {
	padding-left: 17px !important;
	background-position: left 4px !important;
}
#search-box a:visited,
#search-box a:link {
	color: #105289;
}
span.corners-top,
span.corners-bottom {
	display: none;
}
span.corners-top,
span.corners-bottom,
span.corners-bottom span,
span.corners-top span,
ul.forums,
.forabg,
.forumbg {
	background: none;
}
.forabg,
.forumbg {
	background-color: #EEE;
	border-radius: 10px;
	border: 1px solid #555;
	margin: 10px 0;
	padding: 0;
}
.forabg .inner .row,
.forabg .inner .header,
.forumbg .inner .row,
.forumbg .inner .header {
	padding: 3px 5px;
}
.forabg .header,
.forumbg .header {
	background-color: #CCC;
	padding-top: 5px !important;
	border-top-right-radius: 10px;
	border-top-left-radius: 10px;
}
.forabg .header a:link,
.forabg .header a:visited {
	color: #000;
}
li.header dt,
li.header dd {
	color: #000;
}
li.row {
	border-top-color: #000;
	border-bottom: none;
	padding: 13px 0;
}
li.row:last-child {
	border-bottom-right-radius: 10px;
	border-bottom-left-radius: 10px;
}
.forumbg li.row:nth-child(odd) {
	background-color: #FAFAFA;
}
.forumbg li.row:nth-child(even) {
	background-color: #EEE;
}
li.row:hover {
	background-color: #DDD !important;
}
a.forumtitle {
	font-size: 1.5em;
	font-weight: normal;
}
a.topictitle {
	font-size: 1.4em;
	font-weight: normal;
}
.pagination span strong,
.pagination span a,
.pagination span a:link,
.pagination span a:active {
	margin: 0;
	padding: 3px 5px;
}
.pagination span strong:first-child,
.pagination span a:first-child {
	border-top-left-radius: 5px;
	border-bottom-left-radius: 5px;
}
.pagination span strong:last-child,
.pagination span a:last-child {
	border-top-right-radius: 5px;
	border-bottom-right-radius: 5px;
}
}
