css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://learn2.acseducation.edu.au/mod/forum") {

   /* leave space at bottom for teacher's absolute-positioned post */
  .col-md-9 {
    padding-bottom:170px;
    
  }
  /*teacher's post */
  .discussionname::after{
    content:"The starter post has been moved to the bottom of this page"; 
    border: thin blue solid;
    border-radius: 5px;
    height:22px;
    line-height:20px;
    display:block;
    position:absolute;
    top:42px;
    left:110px;
    padding:0px  10px 0px 10px;
    background-color:rgba(240,240,256,.8);
    font-size:.8em;
  }
  .forumpost.clearfix.firstpost.starter {
    height:131px;
    overflow:hidden;
    background-color:rgb(245,245,255);
    border:1px rbga(240,240,240,1) solid;
    position:absolute;
    overflow:scroll;
    bottom:0px;
    z-index:9999;
    -webkit-box-shadow: -8px -8px 30px -10px rgba(0,0,0,0.45);
    box-shadow: -8px -8px 30px -10px rgba(0,0,0,0.45);
  }
  .forumpost.clearfix.firstpost.starter:hover {
    height:360px;
    box-shadow: 0px 0px 7px -3px rgba(0,0,40,.4);
    bottom:-50px;
  }


  .forumpost.clearfix{
    margin: 0px!important;
    padding: 0px!important;
    padding-bottom: 20px!important;
  }
  .forumpost.clearfix br{
    display:none;
  }


  
    /* The "Add a new question button */
  div.singlebutton.forumaddnew{
    position:fixed;
    top:150px;
    right:62px !important;
    padding:0px !important;
    margin:0px !important;
    z-index:10;
    width:140px;
    height:40px;
  }  
  div.singlebutton.forumaddnew>form >div>input{
    position: absolute;
    top:-50px;
    right:0px;
    width:140px;
    height:26px;
    line-height: 18px;
    border:2px solid rgb(23,34,125);
    color:rgb(23,34,125);
    background: rgba(230,230,255,1);
    border-radius:4px;
    padding-left:6px!important;
    padding:0px !important;
    margin:0px !important;
  }
  div.singlebutton.forumaddnew>form >div>input:hover{
    background-color:rgb(23,34,125);
    color:white;
  }
  /* The message (there are no questions yet in this forum) */
  div.forumnodiscuss{
    border:red solid 2px;
    border-top:none;
    border-radius:4px;
    position:fixed;
    top:126px;
    right:62px !important;
    padding:0px !important;
    margin:0px !important;
    z-index:10;
    width:140px;
    height:72px;
    padding:6px!important;
    font-weight: bold;
    background-color: white;
  }
  
  
  /* The three parts to the search forum form  */
  div.forumsearch{
    position:absolute;
    top:40px;
    right:62px !important;
    padding:0px !important;
    margin:0px !important;
    z-index:10;
    width:140px;
    height:40px;
  }
  div.forumsearch >form >fieldset>input#search{
    position: absolute;
    top:0px;
    right:0px;
    width:140px;
    height:22px;
    line-height:20px;
    border:thin gray solid!important;
    background-color:white!important;
    color:rgb(23,34,125);
    padding:0px !important;
    margin:0px !important;
    padding-left:6px!important;
  }
  div.forumsearch >form >fieldset>input#search:hover{
    color:rgb(23,34,125)!important;
  }
  div.forumsearch >form >fieldset>input#searchforums{
    top:24px;
    right:10px;
    width:120px;
    position: absolute;
    height:20px;
    line-height: 16px;
    border:1px solid rgba(23,34,125,.8)!important;;
    color:rgb(23,34,125);
    background: rgba(230,230,255,1);
    border-radius:4px;
  }
  div.forumsearch >form >fieldset>input#searchforums:hover{
    background-color:rgb(23,34,125);
    color:white;
  }
  div.forumsearch span.helptooltip{
    display:inline-block;
    position: absolute;
    top:24px!important;
    height:20px;
    line-height: 16px;
    right:0px;
    top:30px;
    position:absolute;
  }
  div.forumsearch span.helptooltip:hover{
    border:thin rgba(0,0,0,.2) solid;
  }
 
  /* If do a search, the advanced search option appears */
  .col-md-9 .reportlink a{
    display:inline-block;
    width:100%;
    text-align:left;
  }
  .col-md-9 .reportlink{
    position:absolute;
    right:33px;
    top:38px;
    width:auto;
    height:20px;
    line-height: 18px;
    border:2px solid rgb(23,34,125);
    color:rgb(23,34,125);
    background: rgba(230,230,255,1);
    border-radius:4px;
    padding: 0px 4px 0px 4px;
    font-weight: bold;
  }
  .col-md-9 .reportlink a{
    text-decoration: none;
  }
  .col-md-9 .reportlink:hover, .col-md-9 .reportlink a:hover{
    background-color:rgb(23,34,125);
    color:white;
  }
  
  
  .options.clearfix div{
    position:relative;
    bottom:-16px;
    float:right;
    padding:0px 10px;
  }
  .vericite-report{
    position:absolute;
    bottom:-32px;
  }
  
  /* Bunch it up */
  .forumpost.clearfix{margin-top:4px!important;}
.discussioncontrols.clearfix{
  display:relative;
  height:80px!important;
  text-align:left;
  top:10px;
  }

  .discussioncontrol.exporttoportfolio.noavailable, div.clearfloat, #p450691{
    height:0px;
  }
  .discussioncontrol.displaymode{
    width:240px!important;;
    left:0px;
    top:10px;
    position:relative;
  }
  .discussioncontrol.movediscussion{
    width:480px!important;;
    left:256px;
    top:-34px;
    position:relative;
  }
  .row.side{ /* one week_s post */
    display:none!important;
    height:auto;
  }

  .user-content br{
    display:none!important;
  }
      /*When creating a new discussion question, the "Post" "cancel" buttons */
  #fgroup_id_buttonar{
    position:fixed;
    top:150px;
    right:12px !important;
    padding:4px !important;
    margin:0px !important;
    z-index:10;
    height:auto;
    width:168px!important;
    background-color: white;
    border:2px solid rgb(23,34,125);
    color:rgb(23,34,125);
    background: rgba(230,230,255,1);
    border-radius:4px;
    -webkit-box-shadow: -8px -8px 30px -10px rgba(0,0,0,0.75);
    box-shadow: 2px 2px 30px -5px rgba(0,0,0,0.75);
  }
  #fgroup_id_buttonar .felement.fgroup{
    padding:0px !important;
    margin:0px !important;
  }
  #fgroup_id_buttonar .felement.fgroup input{
    padding:4px !important;
    margin:0px !important;
    float:left;
  }
  #fgroup_id_buttonar .felement.fgroup input:last-child{
    padding:4px !important;
    margin:0 0 0 2px!important;
  }
  #fgroup_id_buttonar #id_cancel{
   position:absolute;
   left:119px;
    top:20px;
  }

  /* created to keep student meeting place images smaller */
  .attachedimages{
    max-width:400px;
    max-height:400px;
  }
}
