css:
@-moz-document url-prefix("https://neets.cc/"), url-prefix("http://neets.cc/") {
/*首页*/
/*
背景色 191919
框内底色 333333
按钮色 4C4C4C
Neets.cc绿色 #5AB7AF
Neets.cc粉色 #E05E53
*/
/*Header*/
.header {
    background: #333333;
}
/*HTML*/
html {
	background-color: #191919;
}

/*大背景*/
.container, body {
    background: #222;
}

/*首页推荐大框底色*/
.diplayBox, .video_list .video_li_box, .category_nav, .my_center_header .my_header_bot, .detail_box .detail_box_bot, .my_center_main .my_center_boxR .ervm2_cen, .my_center_main .my_center_boxR .video_box, .my_center_main .my_center_boxL .my_row,.lot_videos_fff .lot_videos_container,.lot_videos_index .lot_videos_container .lot_video_li.lot_video_fff, .ut_con_main .ut_cm_right .ut_cm_tr, #updateTime_con .ut_con_main .ut_cm_right,#updateTime_con .ut_con_main .ut_cm_right .ut_cm_tab, .tiemTableCon, .topics_hot, .home_art_box {
	background: #333333;
}

/*框顶部文字*/
.diplayBox .sub_title, .my_center_main .my_center_boxL .sub_title, .my_center_main .my_center_boxR .video_box .sub_title, .lot_videos_index .lot_videos_header,.topics_title h4, .home_art_box h1 {
	color: #666666;
	background: #333333;
}

/*推荐里的更多按钮底色*/
.diplayBox .sub_box .sub_li.sub_more,.lot_videos_index .lot_videos_container .col_more .col_more_con {
	background: #4C4C4C;
}

/*框内标题色*/
.diplayBox .sub_box .sub_li .sub_txt .sub_p1 a, .video_list .video_li_box .v_box .v_box1 a,.lot_videos_index .lot_videos_container .lot_video_li .lot_video_rg .lot_video_title a, #updateTime_con .ut_con_main .ut_cm_right .ut_cm_box1 a {
    color: #5AB7AF;
}

/*链接*/
a:hover {
	color: #E05E53 !important;
}
a:visited, .my_center_main .my_center_boxL .sub_title .sub_a,.timeTableBox_txt .sub_p1 a {
	color: #5AB7AF;
}

/*剧目时间表左侧文字颜色*/
#updateTime_con .ut_con_main .ut_cm_left li b, #updateTime_con .ut_con_main .ut_cm_left .ut_nav {
    color: #bbb;
}

#updateTime_con .ut_con_main .ut_cm_left li.active b {
    color: #E05E53;
}

#updateTime_con .ut_con_main .ut_cm_right .ut_cm_box2, #updateTime_con .ut_con_main .ut_cm_right .ut_cm_box1, #updateTime_con .ut_con_main .ut_cm_right .ut_cm_box3, .home_art_li .li3 p,.topics_hot .topics_hot_ul li a {
    color: #8e8e8e;
}

/* tag背景颜色*/
.topics_hot .topics_hot_theme_ul li {
    background: #1f1f1f;
}

/* 不显示下载app的div*/
#downLoadIcon {
    display: none;
}
}

@-moz-document url("https://neets.cc/subcriberlist"), url("https://neets.cc/recyclelist"), url("https://neets.cc/watchedlist"), url("http://neets.cc/subcriberlist"), url("http://neets.cc/recyclelist"), url("http://neets.cc/watchedlist") {
/*我的清单*/

/*主列表*/
#subcriber_center {
    background: #333333;
}
/*主列表标题*/
.my_center_main .my_center_boxL .my_sub_list .my_sub_li .my_sub_li_txt .my_tit a {
	color: #5AB7AF;   
}
/*高亮*/
.my_center_main .my_center_boxL .my_sub_list .my_sub_li .my_sub_li_txt .my_tit a:hover {
	color: #E05E53;
}

/*右侧列表*/
.my_center_main .my_center_boxR .video_box {
    background: #333333;
}
/*右侧列表标题*/
.my_center_main .my_center_boxR .video_box .sub_li .sub_txt .sub_p1 a {
    color: #5AB7AF;  
}

.my_center_main .my_center_boxL .my_sub_list .sub_none .sub_none_txt {
    color: #666666;
}
}

@-moz-document url-prefix("https://neets.cc/detail/"), url-prefix("http://neets.cc/detail/") {
/*详细页面*/

/*简介文字*/
.compilation_box, .my_center_main .my_center_boxR .video_box, .introduce_box, .new_series_con {
    background: #333333;
}

.compilation_box .tit, .introduce_box .tit, .introduce_box .introduce_cen p, .detail_box .detail_box_bot .detail_row, .detail_box .detail_box_bot .detail_row_l, .compilation_box .compilation_theme .theme_tit .a_tit, .current_video .tit {
    color: #999999;
}
.compilation_box .compilation_cen li .tits, .introduce_box .introduce_cen .p1, .state_btns .state_div span {
    color: #666666;
}

.compilation_box .compilation_cen li.bot a, .compilation_box .compilation_theme .theme_tit, .compilation_box .theme_total a, .current_video .tit, .new_com_box, .compilation_box .theme_total2 a {
	background: #4C4C4C    
}

.compilation_box .theme_total a:hover{
	background: #666666;    
}

.compilation_box .compilation_theme .theme_tit a.a_tit, .my_center_main .my_center_boxR .video_box .sub_li .sub_txt .sub_p1 a {
	color: #5AB7AF;
}
}

@-moz-document url-prefix("https://neets.cc/createUrl"), url-prefix("http://neets.cc/createUrl") {
/*添加链接*/
.create_url_box .create_url_row, .create_url_box .create_url_row .c_row_r .input_check  {
	color: #999999;
}
}

@-moz-document url-prefix("https://neets.cc/search"), url-prefix("http://neets.cc/search") {
/*搜索页面*/

.search_main .search_li, .search_main .themes_list_cen, .search_ban {
    background: #333333;
}

.search_main .themes_list_cen .sub_title, .search_main .search_more {
    background: #4C4C4C;
    color: #999999;
}

.search_main .search_li .search_li_r a {
    color: #5AB7AF;
}
}

@-moz-document url-prefix("https://neets.cc/topics") {
.topics_con_main, .topics_con_art, .topics_search p, .customBootstrap {
    background-color: #333333;
}

.topics_li_t span {
    color: #666666;
}



.topics_con_nav, .topic_more {
    background-color: #4C4C4C;
}

.topics_nav span, .topic_more {
    color: #bbb;
}
}
