css:
@-moz-document domain("kiwari.qiscus.com"), domain("web.kiwari.id") {
#app.application--light {
    background-image: linear-gradient(to top, #09203f 0%, #537895 100%);
    background: url(https://d1edrlpyc25xu0.cloudfront.net/kiwari-prod/image/upload/78-D7R2wFJ/1507025465-assasin.jpg) no-repeat center;
    background-size: cover;
}
.qisme-container {
    box-shadow: 0 0 17px rgba(255, 255, 255, .15);
}
.qisme-container:before {
    content: '.';
    position: absolute;
    top: 0;
    left: 0;
    z-index: -1;
    width: 100%;
    height: 100%;
    background: url(https://d1edrlpyc25xu0.cloudfront.net/kiwari-prod/image/upload/78-D7R2wFJ/1507025465-assasin.jpg) no-repeat center;
    background-size: cover;
    filter: grayscale(10);
    transition: filter 0.32s ease;
}
.qisme-container:hover:before {
    filter: grayscale(0);
}
.sidebar,
.sidebar__header {
    background: transparent;
}
.application--light .input-group input,
.application--light .input-group textarea {
    color: #FFF;
}
.sidebar__content ul.list.list--two-line {
    background: rgba(0, 0, 0, .5);
}
.list__tile__sub-title,
.list__tile__title {
    color: #b9b9b9 !important;
}
.sidebar__footer {
    background: #202020;
}
.qcw-app--wide-mode .qcw-container,
.qcw-header {
    background: rgba(0, 0, 0, .3);
    color: #CCC;
}
.qcw-app--wide-mode ul#messages__comments {
    background: rgba(0, 0, 0, .75);
}
.comment--parent .qcw-comment__message:after {
    border: 0;
}
.qcw-comment__message {
    border-radius: 0 5px 5px 0;
    background: rgba(0, 0, 0, .5) !important;
    color: #FFF;
}
.splash-screen img {
    display: none;
}
ul#messages__comments::-webkit-scrollbar,
.sidebar__content::-webkit-scrollbar {
    width: 9px;
    background: rgba(0, 0, 0, .5);
    opacity: 0.3;
}
ul#messages__comments::-webkit-scrollbar:hover,
.sidebar__content::-webkit-scrollbar:hover {
    transition: opacity 0.32s ease;
    opacity: 1;
}
ul#messages__comments::-webkit-scrollbar-thumb,
.sidebar__content::-webkit-scrollbar-thumb {
    border-radius: 10px;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .3);
    background-color: #444;
}
}
