css:
@-moz-document domain("web.kiwari.id") {

::-webkit-scrollbar {
  width: 0px;
  /* remove scrollbar space */
  
  background: transparent;
  /* optional: just make scrollbar invisible */
}
/* optional: show position indicator in red */

::-webkit-scrollbar-thumb {
  background: #FF0000;
}
main {
  background: #333;
}
.qisme-container::before {
  content: ' ';
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 100%;
  /* girl */
  
  background-image: url(https://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-259990.jpg);
  /* frost warframe */
  /* background-image: url(https://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-26977.jpg); */
  /* some dark picture */
  /*   background-image: url(https://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-557746.png); */
  /* landscape */
  
  background-image: url(https://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-556403.jpg);
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
  filter: brightness(0.9) blur(1px);
}
.qcw-container,
.widget-chat-wrapper,
#qcw-app #messages__comments {
  background: transparent;
}
ul#messages__comments::-webkit-scrollbar,
ul#messages__comments::-webkit-scrollbar-thumb {
  width: 0;
}
.splash-screen {
  background: url(https://d1edrlpyc25xu0.cloudfront.net/kiwari-prod/image/upload/tOqvIK5zqt/1507026516-1_GlyphValentinesCupid.png);
  background-repeat: no-repeat;
  background-position: center;
  background-size: 200px;
  background-color: rgba(0, 0, 0, .5);
  height: 100%;
  width: 100%;
}
.splash-screen img,
.splash-screen p {
  display: none;
}
.qcw-header {
  z-index: 1;
  background: rgba(39, 39, 39, 0.7);
  box-shadow: 0 0 5px rgba(0, 0, 0, 0.5);
  color: white;
  border-radius: 0;
}
.qcw-comment__message,
.comment--me .qcw-comment__message {
  background: rgba(0, 0, 0, .6);
  color: #ddd;
}
.comment--parent .qcw-comment__message:after,
.comment--parent.comment--me .qcw-comment__message:after {
  display: none;
  border-color: rgba(0, 0, 0, 0.6);
  background: rgba(0, 0, 0, .6);
}
.qcw-comment__username,
.qcw-comment__time {
  color: #ddd;
}
.load-more-button {
  position: relative;
  background: rgba(0, 0, 0, .4);
  color: #ddd;
}
.qcw-comment-date {
  position: relative;
}
.reply-wrapper {
  background: rgba(123, 123, 123, .7);
  color: #ddd;
}
.reply-wrapper .reply-sender {
  color: #ddd;
}
.qisme-container .sidebar {
  background: rgba(0, 0, 0, .5);
  color: #ddd;
  position: relative;
}
.application--light .list .list__tile:not(.list__tile--active),
.application--light .list .list__tile__sub-title,
.application--light .list .subheader,
.list__tile--active .list__tile__title {
  color: #ddd !important;
}
.sidebar__header {
  z-index: 1;
  background: transparent;
}
.sidebar__footer {
  background: transparent;
  position: relative;
}
.list--group__container .list .list--group,
.application--light .list {
  background: transparent;
}
.qcw-comment-form {
  background: rgba(0, 0, 0, .5);
  color: #ddd;
}
.btn__emoji i {
  color: #ddd;
}
.qc-icon {
  fill: #ddd;
}
}
