css:
@-moz-document domain("student.portal.chalmers.se") {
.navbar {
    min-height: 48px;
}
.navbar.navbar-default {
    background-color: #445;
}
.navbar.navbar-default .navbar-nav a {
    color: white;
    line-height: 48px;
}
.navbar.navbar-default .navbar-nav a:hover {
    color: lightblue;
}
.navbar.navbar-default .navbar-header .brand-logos img {
    margin-top: 10px;
}
.navbar-default .navbar-collapse,
.navbar-default .navbar-form {
    margin: 0;
}
body {
    background: #ccc;
}

h1 {
 font-size: 28px;   
}

.user-meta-bar {
    margin-bottom: 10px;
    padding: 3px;
    background-color: #334;
}
.rte-content,
.box {
    border-radius: 0px;
    box-shadow: 0 0 15px rgba(0, 0, 0, 0.9);
}
.leftnavigation {
    border-radius: 0px;
    box-shadow: 0 0 15px rgba(0, 0, 0, 0.9);
}
#bs-main .row.breadcrumbs ul.breadcrumbs {
    margin-top: 0;
}
.row.breadcrumbs ul.breadcrumbs li.current a {
    font-weight: bold;
    color: black;
}
.leftnavigation .nav-listing .RadTreeView .rtIn {
    padding: 3px 10px 3px 10px;
    font-size: 12px;
    color: gray;
    font-weight: bold;
}
.leftnavigation .nav-listing .RadTreeView .rtUL .rtPlus,
.leftnavigation .nav-listing .RadTreeView .rtUL .rtMinus {
    border-left-color: #303030;
    height: 24px;
    line-height: 24px;
}
.leftnavigation .nav-listing .RadTreeView .rtUL .rtUL .rtPlus,
.leftnavigation .nav-listing .RadTreeView .rtUL .rtUL .rtMinus {
    border-left-color: #383838;
}
.leftnavigation .nav-listing .RadTreeView .rtUL .rtUL .rtUL .rtPlus,
.leftnavigation .nav-listing .RadTreeView .rtUL .rtUL .rtUL .rtMinus {
    border-left-color: #404040;
}
.s4-wpTopTable {
    display: none;
}
footer {
    background: #445;
    position: absolute;
    width: 100%;
}
footer .container {
    background: transparent;
    color: lightgray;
}
footer .container .row.breadcrumbs ul.breadcrumbs li a {
    color: lightgray;
}
footer .container .row.breadcrumbs ul.breadcrumbs li.current a {
    color: white;
}
footer .container ul.list li {
    margin: 0;
    padding: 0;
    border-top: 1px solid #656;
    border-bottom-color: #434;
}
footer .container ul.list li:first-child, footer .container ul.list.split li:nth-child(2) {
    border-top: unset;
}

footer .container ul.list li:last-child {
    border-bottom: unset;
}

footer .container ul.list li a {
    color: lightgray;
 font-size : 12px;   
}

footer .container ul.list li a span.header
{
 font-size : 12px;   
}


}
