/* ------------------------------------------------------------------ DLM START -------------------------------------------- */

/* Change Left Navigation Menu */

#ctl00_navMenu {
	border: 2px solid #5374B5;
	border-radius: 0 5px 5px 0;
	margin-left: -8px;
}

.siteMenu {
	background-color: #6892E1;
	border: none;
	border-left: 6px solid #5374B5;
	-webkit-transition: .2s;
	-o-transition: .2s;
	transition: .2s;
	border-bottom: 1px solid rgba(0, 0, 0, 0.08);
}

.siteMenu:hover {
	background-color: #79A1F1;
	border: none;
	border-left: 4px solid #5374B5;
	border-bottom: 1px solid rgba(0, 0, 0, .2);
}

.siteMenu a {
	display: block;
	padding: 10px 6px;
	color: #fff !important;
	text-decoration: none;
	margin: 0;
	-webkit-transition: .2s;
	-o-transition: .2s;
	transition: .2s;
}

/* Space out Tasks Page */

#ctl00_phMain_ucTasks_gvTasks td {
	padding: 10px;
}

/* DLM - Custom Sit Info Tab Domains Tab Fix */

#tblCustomDomains .row2Large td {
	width: 100%;
	position: relative;
}

#tblCustomDomains .row2Large td:after {
	content: "";
	width: 15px;
	right: -15px;
	height: 100%;
	background-color: #ddd;
	position: absolute;
	top: 0;
}

/* Dealer Details Tab - Site Builder Buttons */

#ctl00_phMain_hplSetupSmartSite,
#ctl00_phMain_hplDynamicSite {
	display: inline-block;
	padding: 10px 20px;
	background-color: #3d6bba;
	color: #fff;
	text-decoration: none;
	margin: 6px;
	float: right;
	-webkit-transition: .2s;
	-o-transition: .2s;
	transition: .2s;
}

#ctl00_phMain_hplSetupSmartSite:hover,
#ctl00_phMain_hplDynamicSite:hover {
	background-color: #1b4998;
}

/* Dealer Details Page - Type of Site Highlight TABs */

#ctl00_phMain_rtsContact a.rtsLink[style="color:Blue;background-color:Blue;border-color:Blue;border-style:Double;font-weight:bold;"],
#ctl00_phMain_rtsContact a.rtsLink[style="color:Red;background-color:Red;border-color:Red;border-style:Double;font-weight:bold;"] {
	border: none !important;
	background-color: transparent !important;
}

#ctl00_phMain_rtsContact a.rtsLink[style="color:Red;background-color:Red;border-color:Red;border-style:Double;font-weight:bold;"] .rtsOut {
	/* Billing Info Tab */
	background-image: none;
	background-color: #3d6bba;
	border-top: 5px solid #a90000;
	-webkit-transition: .2s;
	-o-transition: .2s;
	transition: .2s;
	font-weight: 700;
}

#ctl00_phMain_rtsContact a.rtsLink[style="color:Blue;background-color:Blue;border-color:Blue;border-style:Double;font-weight:bold;"] .rtsOut {
	background-image: none;
	background-color: #c90000;
	border-top: 5px solid #a90000;
	-webkit-transition: .2s;
	-o-transition: .2s;
	transition: .2s;
	font-weight: 700;
}

#ctl00_phMain_rtsContact a.rtsLink[style="color:Blue;background-color:Blue;border-color:Blue;border-style:Double;font-weight:bold;"].rtsSelected .rtsOut {
	background-color: #FFF1A6;
}

#ctl00_phMain_rtsContact a.rtsLink[style="color:Blue;background-color:Blue;border-color:Blue;border-style:Double;font-weight:bold;"].rtsSelected .rtsOut:hover {
	background-color: #FFFFB8;
}

#ctl00_phMain_rtsContact a.rtsLink[style="color:Blue;background-color:Blue;border-color:Blue;border-style:Double;font-weight:bold;"] .rtsOut:hover {
	background-image: none;
	background-color: #de1515;
}

/* DLM Tasks Page - Style Back to Dealer Button */

#ctl00_phMain_hlBack {
	display: inline-block;
	padding: 10px 20px;
	background-color: #3d6bba;
	color: #fff;
	text-decoration: none;
	margin: 6px;
	float: right;
	-webkit-transition: .2s;
	-o-transition: .2s;
	transition: .2s;
}

#ctl00_phMain_hlBack:hover {
