	margin: 10px;
	display: inline-block;
	color: rgba(256, 256, 256, 0.4) !important;
	border: 1px solid rgba(256, 256, 256, 0.4);
	text-decoration: none;
	-webkit-transition: .2s;
	-o-transition: .2s;
	transition: .2s;
}

#ctl00_phMain_hlBackTask:hover,
