}

/* CC Profile - Set Login button bigger for CC Profile Tab */

a .usrLogin {
	width: 90px;
	height: 40px;
	background-color: #6694E3 !important;
	color: #fff;
	border: none;
	border-bottom: 4px solid #315fae;
	padding: 10px;
	-webkit-transition: .1s;
	-o-transition: .1s;
	transition: .1s;
	border-radius: 4px;
	height: 40px;
	cursor: pointer;
}

a .usrLogin:hover {
	background-color: #315fae !important;
	color: #fff;
	border-bottom: 0px solid #315fae;
	padding: 10px;
	cursor: pointer;
	top: 2px;
}

a.usrLogin {
	text-decoration: none !important;
}

/* Task Details - Set Buttons */

#ctl00_phMain_btnTask,
#ctl00_phMain_btnStatus,
#ctl00_phMain_btnCancel,
#ctl00_phMain_btnNote {
	height: 60px;
	display: inline-block;
	background-color: #6694E3 !important;
	color: #fff;
	border: none;
	border-bottom: 4px solid #315fae;
	padding: 10px 19px;
	-webkit-transition: .1s;
	-o-transition: .1s;
	transition: .1s;
	border-radius: 4px;
	height: 40px;
	cursor: pointer;
	margin-right: 1px;
}

#ctl00_phMain_btnTask:hover,
#ctl00_phMain_btnStatus:hover,
#ctl00_phMain_btnCancel:hover,
#ctl00_phMain_btnNote:hover {
	background-color: #315fae !important;
	border-bottom: 0px solid #315fae;
	cursor: pointer;
	top: 2px;
}

#ctl00_phMain_btnStatus,
#ctl00_phMain_btnNote {
	background-color: #ccc !important;
	color: #111;
	border-bottom: 4px solid #444;
}

#ctl00_phMain_btnStatus:hover,
