}

/* Task Details - Crazy Custom SiteBuilder */

#ctl00_phMain_btnToSiteBuilder,
#ctl00_phMain_hlResponsiveSite,
#ctl00_phMain_affiliateDetials_btnToSiteBuilder {
	background-color: #6694E3;
	border: none;
	padding: 10px 40px;
	font-size: 16px;
	font-weight: 600;
	color: #fff !important;
	text-shadow: 1px 1px rgba(0, 0, 0, 0.85), 0px 0px 4px rgba(0, 0, 0, 0.2);
	background: -webkit-linear-gradient(326deg, #ff2400, #e81d1d, #e8b71d, #e3e81d, #1de840, #1ddde8, #2b1de8, #dd00f3, #ff2400);
	background: -o-linear-gradient(326deg, #ff2400, #e81d1d, #e8b71d, #e3e81d, #1de840, #1ddde8, #2b1de8, #dd00f3, #ff2400);
	background: linear-gradient(124deg, #ff2400, #e81d1d, #e8b71d, #e3e81d, #1de840, #1ddde8, #2b1de8, #dd00f3, #ff2400);
	background-size: 1800% 1800%;
	-webkit-animation: rainbow 3s linear infinite;
	-z-animation: rainbow 3s linear infinite;
	animation: rainbow 3s linear infinite;
	cursor: pointer;
	display: inline-block;
	text-decoration: none;
	-webkit-box-shadow: inset 0 0 10px rgba(0, 0, 0, 0.3);
	box-shadow: inset 0 0 10px rgba(0, 0, 0, 0.3);
	border-radius: 5px;
	position: relative;
}

@-webkit-keyframes rainbow {
	0% {
		background-position: 0% 0%;
	}
	100% {
		background-position: -95% 0%;
	}
}

@keyframes rainbow {
	0% {
		background-position: 0% 0%;
	}
	100% {
		background-position: -95% 0%;
	}
}

/* Task Details - Mouse Button Click GIFs */

#ctl00_phMain_hlResponsiveSite:after {
