@namespace url("http://www.w3.org/1999/xhtml");

@-moz-document domain("amoursucre.com"), domain("amordoce.com"), domain("sweetamoris.de"), domain("corazondemelon.es"), domain("flirttistoori.com"), domain("csabitasboljeles.hu"), domain("slodkiflirt.pl"), domain("sweetflirt.ro"), domain("sladkiiflirt.ru"), domain("askito-m.com"), domain("sweetcrush.co.uk"), domain("mycandylove.com") {
   
    index > .connected {
       background-color : #FFFFFF !important;
       /*Fondo Base*/
       background-image : url("https://img00.deviantart.net/cf7e/i/2017/276/9/e/fondobase_by_darknessblack-dbpenyk.jpg") !important ;
       background-repeat : no-repeat !important ;
       background-position : center center !important ;
       background-attachment : fixed !important ;
       background-size : cover !important ;
    }    
        #site-logo > a > img[src*="/logo-as.png"] {
           height : 0 !important ;
           width : 0 !important ;
           /*Logo*/
           background: url("https://orig00.deviantart.net/34d2/f/2017/276/5/7/logo_by_darknessblack-dbpep27.png") no-repeat !important ;
 padding-left : 427px !important ;
           padding-top : 142px !important ;   
             
        }
       .halo {background-image : url("http://img15.hostingpics.net/pics/250980SansHalo.png") !important ;}
       /**********************************************PARTE SUPERIOR************************************************************/
  		/*Fondo de la tarjeta*/
      panel-connected {
          background: #4ebc9e !important;
          border: 2px dashed #ffffff !important;
      }

       #pseudo-container > .blue {
       	 /*fondo de la etiqueta del nombre*/
                background : #b96e5b !important ;
                box-shadow : 0 1px 1px rgba(125,125,125,0.75) !important ;
       }
       .title-container a {
       	/*nombre del avatar*/
                color : #FFFFFF !important ;
                text-shadow : 0 1px 1px rgba(125,125,125,0.75) !important ;
       }
    	/*fondo del episodio*/
        #episode-container {
              background: #B9FCDA !important;
            border: 2px solid #ffffff !important;
        }
        /*color del nombre del episodio*/
        #episode-container span {color : #9B3A00 !important ;}
        /*numero del episodio*/
        #episode-container .number {color : #FFFFFF !important ;}
    	/*clip*/
       .trombone {background-image : url("http://www.amoursucre.com/image/paper-clip/left.png") !important ;}
    
        #currencies-container .values {
        	/*fondo de PA's y dinero*/
           background : #A1C3AE !important ;
           /*borde*/
           border : 2px dashed #FFFFFF !important ;
           /*Color de las letras*/
           color : #FFFFFF !important ;
        }
    	/*menu Perfil-Banco-Foro-ayuda*/
        #panel-menu a {
            background : #b96e5b !important ;
            /*color de letras*/
            color : #FFFFFF !important ;
            /*BORDE*/
            border: 2px solid #ffffff !important;
        }
      
        /*menu Piso-Amigos-Ciudad*/
    navigation-menu > div > .pink, navigation-menu > div > .blue {
        background : #4ebc9e !important ;
        border-color: #FFFFFF;
        /*color de letras*/
        color : #FFFFFF !important ;
        border: 2px dashed #FFFFFF !important;
    }

    navigation-menu > div > .border {
        border : none !important ;
    }

    /*************************************************** PAGINA PRINCIPAL ****************************************************************/
    /*Color en las pestañas izquierdas - Face - twetter */
    side-links > .link-entry {
          background : #b96e5b !important ;
          border: 2px solid #FFFFFF !important;
    }
    /**********************************************  PERFIL DE SUCRETTE *******************************************************************/
    /*Fondo de la etiqueta del nombre en el perfil del usuario*/
    .blue[_nghost-c13]{
          background: #b96e5b;
    }
    
}
