css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://mangafox.me"), url("http://mangafox.me"), domain("mangafox.me"), domain("mangafox.la"), domain("fanfox.net")
{
/* REMOVE ADS */
iframe,
body > a[href],
.mb10,
.tips > div[style],
.c,
#shares,
#advertisment,
#rss,
#footer,
.advertisment,

/* #spotlights, */
#intro > span,
.left > div:nth-child(4),
#mfad600x90,
#ad_top,
.ad,
#ad,
#intro,
#top.topless,
.fb-page.fb_iframe_widget,
.stcpDiv,
#fb-root,
.tab_comment.clearfix li:first-child,
.fb-page,
[id^=ad],
[class^=ad]{
    display: none!important;
}

/* LAYOUT */
a#comments.r,
div.cl #bookmark {
    float: right!important;
}
div.cl ul#menu {
    float: left!important;
    border-bottom: 1px solid #2A2A2E!important;
    padding-right: unset;
}
div.cl ul#menu li a {
    height: 30px;
}
div.cl ul#menu li:last-child a {
    border-top-right-radius: 10px!important;
    padding-right: 8px!important;
    border-right: 5px solid #3F3F48;
    border-top: 5px solid #3F3F48;
}
.widepage ul#menu li a {
    border-top: 5px solid #3F3F48;
    height: 23px;
    border-bottom: 1px solid #3F3F48;
}
ul#menu li:last-child a {
    border-top-right-radius: 10px!important;
    border-right: 5px solid #3F3F48;
}
div.cl ul#menu li:last-child a {
    border-top-right-radius: 10px!important;
    padding-right: 8px!important;
    border-right: 5px solid #3F3F48;
    border-top: 5px solid #3F3F48;
}
ul#menu li a {
    border-top: 5px solid #3F3F48;
}
ul#menu li:first-child a {
    border-top-left-radius: 10px!important;
    border-left: 5px solid #3F3F48;
    border-top: 5px solid #3F3F48;
}
ul#menu {
    margin-right: 5px;
}
ul#menu li.left {
    background-image: none!important;
}
ul#menu li a:hover,
ul#menu li a.selected,
ul#menu li {
    background-image: none!important;
}
#sidebar .topless {
    border: none!important;
}
#sidebar.left,
#manga_genres,
#searchform #searchbar {
    border-radius: 10px;
    border: none;
}
#warning_extra a {
    border: 1px solid black;
    background: linear-gradient(to bottom, crimson, black);
    border-radius: 5px;
    padding: 0 12px 0 10px!important;
    margin: 10px!important;
}

/* #mangalist ul.list li{
	margin: 9px 9px 9px 0!important; 
} */

/* BACKGROUND */

/* HEADER */
#header {
    background: #1E1E20!important;
    background-color: #1E1E20!important;
    background-image: url("https://i.imgur.com/uRYGF7m.png")!important;
    background-size: auto 90% !important;
    background-position: 7% 75% !important;
    background-repeat: no-repeat!important;
}
#menu li a,
.manga {
    background: #2A2A2E!important;
    background-color: #2A2A2E!important;
}
#menu li:hover,
#menu li:focus,
#menu li:visited,
#menu li:selected,
#menu li:any,
#menu li a:hover,
#menu li a:focus,
#menu li a:visited,
#menu li a:active,
#menu li a:any,
a.selected {
    background: black!important;
    background-color: black!important;
    background-image: none!important;
}

/* BODY */
body,
.clear {
    background: #1B1B1D;
    background-color: #1B1B1D;
}
#page {
    border-top-left-radius: 10px!important;
    background: #3F3F48!important;
    background-color: #3F3F48!important;
    background-blend-mode: darken!important;
    background-image: url("https://i.imgur.com/7chXQXm.jpg")!important;
    background-size: 30% !important;
    background-position: top !important;
    background-repeat: no-repeat!important;
}
#tip {
    background: #1E1E20!important;
    background-color: #1E1E20!important;
}
#sidebar,
#manga_genres,
#searchform #searchbar {
    background: #1B1B1D!important;
    background-color: #1B1B1D!important;
    background-image: none!important
}

/* BOOKMARK PAGE */
.avata_main .avata_mainR,
.widscreen.right,
.series_grp {
    background: #3F3F48!important;
    background-color: #3F3F48!important;
}
.avata_mainL .menu_select a,
#bookmark ul.bookmark_nav li {
    background-color: #3F3F48!important;
    background: #3F3F48!important;
}

/* FONT */

/* BODY FONTS */
#menu li a,
#comments,
#bookmark,
#tip,
a,
ol li label,
.manga_text .title,
h1,
#nav,
#nav ul li a,
#bookmark ul {
    color: white!important;
}
p,
.chapter,
h3 {
    color: #a69e9e!important;
}
p a,
.series_preview,
#manga_genres ul li a,
.chlist span.title {
    color: wheat!important;
}

/* VISITED */
#menu a:visited {
    color: white!important;
    background: #2A2A2E!important;
    background-color: #2A2A2E!important;
}

/* HOVER WITH BACKGROUND */
#menu li a:hover,
#nav ul li a:hover {
    color: red!important;
    background: white!important;
    background-color: white!important;
}

/* HOVER */
#manga_genres ul li a:hover,
.nowrap a:hover,
a.tips:hover {
    color: yellow!important;
}

}
