css:
@-moz-document domain(my.fluentcloud.com) {

/* ALL FONTS */
p, a, h1, h2, h3, h4, h5, h6, span, li{
	font-family:'Lato', sans-serif;
}
a{
    color:#97b7f7;
}

/* styles for the login page */
.sign-in-page .sign-in-bg {
    background-image: url(https://www.tellmeagain.xyz/freebies/fc-app-sign-in-bg.png);
    background-size: 100vw!important;
    background-repeat: no-repeat;
    background-position:10% 50%!important;
    -webkit-filter: blur(0px);
}
.sign-in-page .sign-in {
    background-color: rgba(255, 255, 255, 0.0);
    border-radius: 5px;
    border:1px solid #5a77ba;
    top: 65%;
    position: absolute;
}
.sign-in-page .sign-in header {
    background-color: rgba(255, 255, 255, 0.0);
   border:none;
    color: #5a77ba!important;
}
.sign-in-page .sign-in header h1 {
    color: #b5daf7!important;
    text-shadow:
0 0 5px #ed3df2, 
0 0 10px #97b7f7, 
0 0 20px #97b7f7, 
0 0 30px #97b7f7, 
0 0 40px #97b7f7, 
0 0 55px #ed3df2, 
0 0 75px #ed3df2;
}
.sign-in-page form label{
 color:#5a77ba!important;
    font-weight:400;
    
}

.sign-in-page .sign-in a{
    color:#5a77ba;
    text-decoration:none;
    font-weight:500;
    transition: all .2s ease-in-out;
}
.sign-in-page .sign-in a:hover{
    color:#97b7f7;
    text-decoration:none;
    font-weight:500;
    text-shadow:
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84;
}

.sign-in-page form button {
    background: #5a77ba;
    color: #f8f8f8;
    border-radius: 2px;
    
}
.sign-in-page form button:hover {
    background: #97b7f7;
    color: #ffffff;
    border-radius: 3px;
    
}
/* END Styles for the sign in page */
/* Colors for all paragraph text and titles in app */

h1, .h1, h2, .h2, h3, .h3, h4, .h4, h5, .h5{
    color:#5a77ba;
}
p, .p, li{
    color:#5a77ba;
}
/***** START styles for the main top nav bars ****/

.navbar.navbar-inverse.navbar-default {
    height: 80px;
    background-color: transparent;
    color: #5a77ba;
}
.navbar-collapse.collapse.cust-subnav {
    background-color: transparent;
    height: 80px;
    color: #5a77ba;
}
.cust-navbar-header {
    height: 80px;
}
.cust-navbar-header {
    background-color: transparent !important;
}
.navbar-inverse .navbar-nav li a {
    color: #5a77ba;
}
.navbar-inverse .navbar-nav>li>a:hover,
.navbar-inverse .navbar-nav>li>a:focus {
    color: #97b7f7;
}
/* top menu bar when a menu is clicked open -- NOT THE MENU ITSELF */

.navbar-inverse .navbar-nav>.open>a,
.navbar-inverse .navbar-nav>.open>a:hover,
.navbar-inverse .navbar-nav>.open>a:focus,
.navbar-nav>#adminhelper>.open> a:active {
    background-color: #20201a!important;
    color: #5a77ba!important;

}
/* top bar drop down menu items hover background color*/

.dropdown-menu li.fc_header a:hover {
    background-color: #2d2d25;
}
/*color of the text in the top bar drop down menu during hover */

.dropdown-menu li.fc_header a:hover {
    color: #97b7f7;
    text-shadow:
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84;

}
/*dashboard reports widgets styles*/

.dashcard-blue7-brd{
border:solid;
    border-width:2px;
    border-color:#5a77ba;
    
}
.dashcard-blue7-bkg,#dashboardContent #dashcard-filter{
	background:transparent;
    color: #97b7f7;

}
#dashboardContent .btn-default{
 background-color:#5a77ba;   
}


/* END dashboard reports */
.dropdown-menu {
    background-color: #20201a;
}
.dropdown-menu li.fc_header a {
    color: #5a77ba;

}

/* SuperDuperAdmin exlusive menus and icon */

.navbar-nav>li>li>.dropdown-menu {
    background-color: #20201a;
    padding: 0px;
}
.dropdown-menu>.cust-nav-font-white {
    background-color: #20201a;
    color: #5a77ba;
}
.dropdown-menu>.cust-nav-font-white:hover {
    background-color: #20201a;
    color: #97b7f7!important;
        text-shadow:
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84;
}
.navbar-inverse .navbar-nav>.open>a,
.navbar-inverse .navbar-nav>.open>a:hover,
.navbar-inverse .navbar-nav>.open>a:focus,
.navbar-nav>#adminhelper>.open> a {
    background-color: #20201a;
    color: #97b7f7!important;
}
/* normal and hover colors for icons next to each drop down menu item */

.navbar-nav>li>.dropdown-menu i {
    color: #5a77ba;
}
.navbar-nav>li>.dropdown-menu i:hover {
    color: #97b7f7!important;

}
.dropdown-toggle > .fa {
    color: #5a77ba!important;
}
.dropdown-toggle:hover > .fa{
	color:#97b7f7!important;
}
.fa-user{
    color:#5a77ba!important;
}
.navbar-inverse .navbar-nav li a:hover > .fa-user{
    color:#97b7f7!important;
}
.navbar-inverse .navbar-nav li a:hover > .fa{
    color:#97b7f7!important;
}
/* END icons color*/

/* style for user sub menu and it's dropdowns == Also affects extensions dropdown list on right side of nav*/

.cust-subnav ul li ul.dropdown-menu li a {
    background: #20201a !important;
    color: #5a77ba;
}
.cust-subnav ul li ul.dropdown-menu li a:hover {
    background-color: #2d2d25!important;
    color: #97b7f7;
        text-shadow:
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84;
}

/* END styles for sub bar navigation and menus */

/* call recording icon in sub nav bar*/
.fa-circle:before {
    color: #5a77ba;
}
.navbar-inverse .navbar-nav li a:hover > .fa-circle:before {
    color: #97b7f7;
}
/* voicemail drop icon in sub nav bar */
.fa-chevron-circle-down:before {
    color: #5a77ba;
}
.navbar-inverse .navbar-nav li a:hover > .fa-chevron-circle-down:before {
    color: #9fb7f7;
}
/* fluentcloud and user portal buttons text colors and bg */

.navbar-inverse .navbar-brand {
    color: #5a77ba;
}
.navbar-inverse .navbar-brand:hover,
.navbar-inverse .navbar-brand:focus {
    background-color: transparent;
    color: #97b7f7;
}
/***** END styles for the main top nav bars ****/
/***********************************************/


/***** MAIN Background style for entire app *****/

/* Waifu-materiao main bg << CAN BE CHANGED TO ANY IMAGE >> */
.mfc-container {
    z-index: 1;
    background-image: url(https://www.tellmeagain.xyz/freebies/fc-app-main-bg.png);
    background-attachment: fixed;
    background-repeat: no-repeat;
    background-size: cover;
    background-position:center bottom;
    background-color: #11110f!important;
}
/* fix for when modal popups come in. This theme is interfering with the function. This will place the transparent layer below the modal so that it can be used correctly. */

.modal-backdrop.in {
    z-index: -1;
}
/* Right-side bar in admin dashboard - header lines */
.newquickLinkHeader {
    background-color: #20201a;
    color: #5a77ba;
}


.table>thead>tr>th, .form-horizontal .control-label, .help-block, .panel-body{
    color:#5a77ba;
}




/* colors for tabs in various pages -- Also applies to left-side menus like in #extensions page. */
.nav>li>a{
    background-color:transparent;
    color:#5a77ba;
    border:1px solid transparent;
    text-decoration:none;
}
.nav>li>a:hover {
    background-color: #20201a;
    color: #97b7f7;
    border:1px solid #97b7f7;
        text-shadow:
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84, 
0 0 10px #354f84;
}
/*Side Menu items style == like in Extensions left side menu */

.fc_extension a:hover {
    color: #f8f8f8;

}
/* side menu non-hover text */

.nav-pills.nav-stacked>li>a {
    color: #5a77ba;
}
.nav-pills.nav-stacked>li>a:hover {
    color: #97b7f7;
    font-weight: 400;
    background-color: #20201a;
}
.nav-pills>li.active>a{
 	background-color:#20201a!important;
    color:#97b7f7!important;
}

/* side bar text in the #phonenumbers page*/

.label-success {
    background-color: #354f84;
    color: #dddddd;
}
.label-primary {
    background-color: #354f84;
    color: #dddddd;
}
/* large section titles */

.help-title {
    color: #5a77ba!important;
}
/* large section titles sub-description */

.lead,
.jumbotron p {
    color: #5a77ba;
}

/* paragraph headers*/

.col-xs-12 .col-md-6 h2 {
    color: #5a77ba;
}
h1.help-title {
    color: #5a77ba;
}
.fs-legend, legend {
    color: #5a77ba;
}

.col-xs-12.col-sm-11.col-sm-offset-1 h1 {
    color: #5a77ba;
}
.col-md-10 h1 {
    color: #5a77ba;
}
/* paragraph text */

.backgrid td {
    color: #5a77ba;
}
.col-xs-12.col-sm-6.cust-call-hist-dropdown > span {
    color: #5a77ba;
}
#showRecords {
    color: #5a77ba;
}
.form-group label,
.controls label {
    color: #5a77ba;
}
.radio label, .checkbox label{
	color:#5a77ba!important;
}
.form-group h1, .form-group h2{
 color:#5a77ba;   
}
.pager li {
    color: #5a77ba;
}
.description{
    color:#5a77ba;
}
.text-center p{
    color:#5a77ba;
}
.col-sm-12 p, .col-sm-12 span{
    color:#5a77ba;
}
.col-sm-12 a{
    color:#97b7f7;
    text-decoration:underline;
}
.col-sm-12 h4{
    color:#5a77ba;
}
.col-sm-12 a:hover{
    color:#97b7f7;
}

.newquickLink{
    color:#5a77ba;
}
.showoptions{
    color:#5a77ba;
    text-decoration:underline;
}
.showoptions:hover{
color:#97b7f7;
}
#voicemails td{
    color:#5a77ba;
}
.table-bordered th{
    color:#5a77ba;
}
/* styles pertaining to tables which appear striped. Applies to various locations. example #extensions */
.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th{
    background-color:#354f84;
    border:none;
    color:#97b7f7;
}
.table-striped>tbody>tr:nth-child(odd)>td a, .table-striped>tbody>tr:nth-child(odd)>th a{
    background-color:#354f84;
    border:none;
    color:#97b7f7;
}
.table-striped>tbody>tr:nth-child(even)>td, .table-striped>tbody>tr:nth-child(even)>th{
    border:none;
    color:#5a77ba;
}
.table-striped>tbody>tr:nth-child(even)>td a, .table-striped>tbody>tr:nth-child(even)>th a{
    border:none;
    color:#5a77ba;
}
.table>thead>tr>th, .table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>td, .table>tbody>tr>td, .table>tfoot>tr>td{
    color:#5a77ba;
}
/* END table styles */

/* styles for 'form action' where button rest at the bottom of various pages.*/
.form-actions{
	background-color:transparent !important;
    border-color:#5a77ba;
}
.form-horizontal .form-actions{
	background-color:transparent !important;
    border-color:#5a77ba;
}

/* styles for pagination button on left-side menus */
.pager .disabled>a, .pager .disabled>a:focus, .pager .disabled>span{
	background-color:#354f84;
    border:none;
}
.pager .disabled>a:hover{
    background-color:#5a77ba;
}

.pager li>a:hover, .pager li>a{
	background-color:#354f84;
    border:none;
}
.pager li>a:hover, .pager li>a:focus{
	background-color:#5a77ba;
}
#previousPage a span, #nextPage a span{
    color:#97b7f7;
}
/* END Style for pagination button on left-side menus */

/* styles for individual #extensions pages */
/* seetings tab */
#meTab label, #faxTab label, #permissionsTab label, #billingTab label{
    color:#5a77ba;
}
#meTab small{
    color:#5a77ba;
}
/* permissions Tab */
#permissionsTab .row #allowInternalDialCont > label, #permissionsTab .row #allowExternalDialCont > label, #permissionsTab .row #allowInternationalDialCont > label, #permissionsTab .row #allowEmergencyDialCont > label,#permissionsTab .row #allowOverrideDialCont > label, #permissionsTab .row #allowListenWhisperBargeCont > label{
	color:#5a77ba;
}
/* advanced tab */
#advancedTab .row #allowVirtualUseInputCont > label, #advancedTab .row #qualifyCont > label, #advancedTab .row #encryptionCont > label, #advancedTab .row #voicemailDropCont > label,#advancedTab .row #matchCallerIDCont > label{
    color:#5a77ba!important;
}
#advancedTab label{
    color:#5a77ba;
}

#advancedTab p, #editHistoryTab p{
    color:#5a77ba;
}
#advancedTab .help-block{
 	color:#5a77ba;   
}
/* input fields within tabs */
#virtualPassword{
 	color:#5a77ba;   
}

/* styles for "Quick Glance" right-side bar */
#quickGlanceDataCont dt, #quickGlanceDataCont dd{
    color:#5a77ba;
}
dt,dd{ color:#5a77ba;}

/* END styles for individual #extensions pages */

/* styles for various form-action text */
.form-horizontal .form-control-static{
	color:#5a77ba;
}

/* ********************************************** */
/* ***** BUTTONS BUTTONS BUTTONS == These are buttons that are NOT in the menus */

/* submit or update settings buttons */

.btn-primary {
    background-color: #5a77ba;
    color: #f8f8f8!important;
    text-decoration:none!important;
    -webkit-transition: .2s all ease-in-out;
-moz-transition: .2s all ease-in-out;
-transition: .2s all ease-in-out;
}
.btn-primary:hover {
    background-color: #97b7f7;
    border-color: #97b7f7;
    color: #ffffff !important;
}
/* buttons that are deleting various items */
.btn.btn-danger {
    background-color: #5a77ba;
    border-color: #5a77ba;
    color: #f8f8f8;
}
.btn.btn-danger:hover,
.btn.btn-danger:focus,
.btn.btn-danger:active {
    background-color: #97b7f7;
    border-color: #97b7f7;
    color: #ffffff;
}
/* reload configuration button in phones pages */
#reboot{
    background-color:#5a77ba;
    color:#f8f8f8;
}
#reboot:hover{
    background-color:#97b7f7;
    color:#ffffff;
}

/* ***create new*** button on bottom of left-side menus */

.btn.btn-xs.btn-info {
    background-color: #354f84;
    border-color: #354f84;
    color: #dddddd;
    text-decoration:none;
}
.btn.btn-xs.btn-info:hover {
    background-color: #5a77ba;
    border-color: #5a77ba;
    color: #f8f8f8;
}

.icon-plus-sign:before{
 color:#dddddd;
}

/* **how to** setup buttons */

.btn-success {
    background-color: #5a77ba;
    border-color: #5a77ba;
    color: #f8f8f8!important;
    text-decoration:none!important;
}
.btn-success:hover {
    background-color: #97b7f7;
    border-color: #97b7f7;
    color: #ffffff!important;
}
/* #extensions page - buttons on top of the list in main pane */

.button-switch input:checked + label,
.button-switch button:active {
    background-color: #5a77ba;
    color: #f8f8f8;
    font-weight:300;
}
.button-switch label,
.button-switch button {
    background-color: #354f84;
    color: #ffffff;
    font-weight:300;
}
.button-switch label:hover,
.button-switch button:hover {
    background-color: #354f84;
    color: #f8f8f8;
    box-shadow: none;
}
/* dropdown menu + buttons in #routes/ConferenceBridge page */

.dropdown-menu>li>a:hover {
    color: #f8f8f8;
    background-color: #5a77ba;
}
.btn-default {
    background-color: #5a77ba;
    border-color: #5a77ba;
    color: #f8f8f8;
}
.btn-default:hover,
.btn-default:focus,
.btn-default:active,
.btn-default.active,
.open .dropdown-toggle.btn-default {
    background-color: #97b7f7;
    border-color: #97b7f7;
    color: #ffffff;
}

/* Learn More Buttons */


/* ****Learn More*** Button on homepage */

#startUpgrading,
#startUpgrading a {
    background-color: #5a77ba;
    border-color: #5a77ba;
}
#startUpgrading:hover {
    background-color: #97b7f7;
    border-color: #97b7f7;
}
#startUpgrading > a {
    background-color: transparent;
    color: #ffffff;
}
#startUpgrading:hover > a {
    background-color: transparent;
    color: #ffffff;
}
/*custom backround actions--Get rid of bg color*/
.cust-actions-background{
    background-color:transparent;
    border:none;
}
}
