css:
@-moz-document domain("canonrumors.com") {
#time span, h3.catbg, #topnav li a, body, td, th, tr, .sidebar_buckets > li .bucket-top-label, .wpp-list .popular-post .pp-title a, .styles, #basicinfo h4, .styles h1, .styles h2, .styles h3, .styles h4, .styles h5, .styles h6, #buyers-guide-header #buyers-guide-menu a, #product-stories .single-product .product-desc, #product-stories .single-product .product-price-chart li .url, #product-stories .single-product .product-title, #product-stories .single-product .rumors-roundup .rumors-roundup-posts li .topost, textarea, h5, h4, html .styles, html .styles h1, html .styles h2, html .styles h3, html .styles h4, html .styles h5, html .styles h6, .block-container, article.single-post, .block--messages .message, .block--messages .block-row, body .p-body-content .single-post blockquote, body .p-body-content .article-full blockquote
{ color: #909090; }
#main_body, body, .windowbg, .windowbg2, .windowbg span.topslice, .windowbg span.topslice span, .windowbg span.botslice, .windowbg span.botslice span, .windowbg2 span.topslice, .windowbg2 span.topslice span, .windowbg2 span.botslice, .windowbg2 span.botslice span, span.upperframe, span.upperframe span, span.lowerframe, span.lowerframe span, #buyers-guide-header #buyers-guide-menu ul li.current, #product-stories .single-product .rumors-roundup .rumors-roundup-posts li, #buyers-guide-header #buyers-guide-menu ul li:hover, .description, #preview_body, html body, .messageNotice, .bbCodeBlock, .bbCodeBlock-title
{ background: #232323; }
#inferno_tools, #main_content, #main_body .wrapper, .sidebar_buckets > li, #main-content #sidebar > ul, #page-content, .blogroll-ad, #buyers-guide-header, #product-stories .single-product, textarea, .CodeMirror-gutters, html .widget-title, .block-container, article.single-post, .block-footer, .pageNav-page, .pageNav-jump, .block--messages .message, .block--messages .block-row, .message-cell.message-cell--user, .message-cell.message-cell--action
{ background: #171717 }
.plainbox
{ background: #232323; border: #303030; }
select, input, .button_submit, .button_reset
{ color: #909090; background: #000; border: 1px solid #000; }
blockquote.bbc_standard_quote
{ color: #909090; background-color: #171717; border: 1px solid #171717; }
blockquote.bbc_alternate_quote
{ color: #909090; background-color: #202020; border: 1px solid #202020; }
table.table_grid td
{ border-bottom-color: #000; border-right-color: #000; }
#main-content #stories article.single-post, #latest-story, #main-content .load-more, body.single-post .back-to-blog, body.single-post #stories .story, #buyers-guide-header .back-to-rumors
{ color: #909090; background: #171717; }
#main-content #stories article .engage-links > li a, #main-content #stories article .engage-links .source span, #sm-callout, #sm-callout .bucket-top-label, #latest-story article .engage-links > li a, #searchform #s, a.glossaryLink, #bbcBox_message div img, .richedit_resize
{ filter: invert(100%); }
.styles h2 a, #latest-story article h2 a, .styles h1, .archive:not(.category) #main-content #stories article h2 a
{ color: #e82730; }
.sidebar_buckets
{ background: #232323 !important; }
.smf_recent_posts > li a
{ color: #909090 !important; }
.smf_recent_posts > li, .wpp-list .popular-post, body.single-post .back-to-blog, .block[data-widget-id] .block-minorHeader, .p-body-sideNav .block-minorHeader, .p-body-sidebar .block-minorHeader, .uix_extendedFooterRow .block-minorHeader, .block[data-widget-id] .block-header, .p-body-sideNav .block-header, .p-body-sidebar .block-header, .uix_extendedFooterRow .block-header
{ border-bottom-color: #000; }
.roundframe
{ background: #232323; border-left-color: #232323; border-right-color: #232323; }
.dropmenu li a
{ background: #000; color: #909090; }
#main-content #stories article.single-post > .ftrd-video
{ border-color: #171717; }
#buyers-guide-header #buyers-guide-menu ul li + li, #buyers-guide-header #buyers-guide-menu, #buyers-guide-header .back-to-rumors, .editor, .block-footer, .pageNav-page, .pageNav-jump, .pageNav-page:not(:first-child), .message-cell.message-cell--user, .message-cell.message-cell--action, .bbCodeBlock
{ border-color: #232323; }
.buttonlist ul li a, .buttonlist ul li a span
{ background: #000; color: #909090; }
.lockedbg, .lockedbg2
{ background: #272730; }
.stickybg, .stickybg2
{ background: #302520; }
.button_submit:hover
{ background: #303030; }
a:link, a:visited, .bbCodeBlock-expandLink a
{ color: #bb0000; }
html article
{ border: none; }
.blogroll-ad, .p-footer-copyrightRow
{ display: none; }
.bbCodeBlock-expandLink
{ background:linear-gradient(to bottom, #fafafa00 0%, #232323 80%); }
}
