css:
@-moz-document domain("community.wanikani.com") {
div[class*='-lifetime'] article .topic-meta-data::after {
 	content: 'LIFETIME';
    visibility: visible;
    font-size: 8pt;
    font-weight: bold;
    height: initial;
    display: inline;
    background: #d680fa;
    color: #FFFFFF;
    padding: .2rem .4rem;
    border-radius: 2px;
}

div[class*='-paid'] article .topic-meta-data::after {
 	content: 'PAID';
    visibility: visible;
    font-size: 8pt;
    font-weight: bold;
    height: initial;
    display: inline;
    background: #82d5fb;
    color: #FFFFFF;
    padding: .2rem .4rem;
    border-radius: 2px;
}

div[class*='-free'] article .topic-meta-data::after {
 	content: 'FREE';
    visibility: visible;
    font-size: 8pt;
    font-weight: bold;
    height: initial;
    display: inline;
    background: #f1f3f5;
    color: #1d1d1d;
    padding: .2rem .4rem;
    border-radius: 2px;
}
}
