css:
@-moz-document domain("roblox.com"), domain("forum.roblox.com")
{
  html.gr__web_roblox_com
  {
    background-image: url(https://imgur.com/a/wxU46);
    background-size: cover;
    background-repeat: no-repeat;
    background-attachment: fixed;
  }

  .content
  {
    background-image: url(https://imgur.com/a/wxU46);
    background-size: cover;
    background-repeat: no-repeat;
    background-attachment: fixed;
  }

  .container-fluid
  {
    background-color: 161314;
    background-image: url(https://imgur.com/a/wxU46);
    background-size: cover;
    background-attachment: fixed;
    filter: blur(0px) !important;
  }

  footer
  {
    background: url(https://imgur.com/a/wxU46);
    filter: blur(0px) !important;
    background-color: rgba(1000,1000,1000,1) !important;
    background-size: cover;
    background-attachment: fixed;
  }

  #MasterContainer
  {
    background-image: url(https://imgur.com/a/wxU46);
    background-size: cover;
    background-repeat: no-repeat;
    background-attachment: fixed;
  }

  #Body
  {
    width: 970px;
    padding-top: 0px;
    padding-left: 0px;
    padding-right: 0px;
    padding-bottom: 0px;
  }

  #MessagesAdSkyscraper
  {
    padding-top: 0px;
  }

  .container-main
  {
    background-image: url(https://i.imgur.com/iuiH5Cu.jpg);
    background-size: cover;
    background-repeat: no-repeat;
    background-attachment: fixed;
  }

  #navigation
  {
    background: url(https://i.imgur.com/iuiH5Cu.jpg);
    filter: blur(0px) saturate(100%) !important;
    background-color: rgba(1000,1000,1000,1) !important;
    background-size: cover;
    background-attachment: fixed;
  }

  #nav-home, #nav-profile, #nav-message, #nav-friends, #nav-character, #nav-inventory, #nav-trade, #nav-group, #nav-forum, #nav-blog, #nav-shop, a.text-overflow
  {
    color: #fff !important;
  }

  #GamesListContainer1, #GamesListContainer8, #GamesListContainer11, #GamesListContainer3, #GamesListContainer16
  {
    background-color: rgba(1000,1000,1000,0) !important;
  }

  p.text-footer.footer-note
  {
    background: url(https://i.imgur.com/iuiH5Cu.jpg);
    filter: blur(0px) saturate(100%) !important;
    background-color: rgba(1000,1000,1000,1) !important;
    background-size: cover;
    background-attachment: fixed;
  }


/*--------------------------------*/

  span.icon-nav-home ,span.icon-nav-profile ,span.icon-nav-message ,span.icon-nav-friends ,span.icon-nav-charactercustomizer ,span.icon-nav-inventory ,span.icon-nav-trade ,span.icon-nav-group ,span.icon-nav-forum ,span.icon-nav-blog ,span.icon-nav-shop
  {
    filter: brightness(1000%) !important;
  }

  *
  {
    border: none!important;
    border-radius: 0px!important;
  }
/* Upper navbar */

  .rbx-header .rbx-navbar li .nav-menu-title
  {
    transition: 0.5s ease;
  }

  .rbx-header .rbx-navbar li .nav-menu-title:hover,
.rbx-header .rbx-navbar li .nav-menu-title:focus
  {
    transition: 0.5s ease;
  }
/* Getting rid of those pesky ads (CUSTOM OPTION) */

  a.ad img
  {
    display: none;
  }

  .abp .ad-identification-text
  {
    display: none;
  }

  .abp .UpsellAdButton,
.abp .UpsellAdButton:link,
.abp .UpsellAdButton:visited,
.abp .BadAdButton,
.abp .BadAdButton:link,
.abp .BadAdButton:visited
  {
    display: none;
  }

  .catalog-container
  {
    margin-top: 25px;
  }
/* Dropdown tab */

  .dropdown-menu li
  {
    transition: 0.5s ease;
  }

  .dropdown-menu li:hover
  {
    transition: 0.5s ease;
  }
/* Chat Area */

  .chat-main
  {
    transition: 0.5s ease;
    box-shadow: none;
  }

  .dialog-container
  {
    transition: 0.5s ease;
  }

  .dialog-container.dialog-visible
  {
    transition: 0.5s ease;
  }

  .chat-friend-list .chat-friend-container
  {
    transition: 0.5s ease;
  }

  .chat-friend-list .chat-friend-container:hover
  {
    transition: 0.5s ease;
  }

  .dialog-container .dialog-message-container .dialog-message
  {
    box-shadow: none;
  }
/* Chat buttons */

  .btn-control-md
  {
    transition: 0.5s ease;
    background-color: #00A2FF;
    color: white;
  }

  .btn-control-md:hover,
.btn-control-md:focus
  {
    transition: 0.5s ease;
    background-color: #32B5FF;
    box-shadow: 0 4px 0 0 #00A2FF;
    color: white;
  }
/* Game Card */

  .game-card-footer
  {
    display: none!important;
  }

  .vote-counts
  {
    display: none!important;
  }

  .game-card-container
  {
    transition: 0.5s ease;
    box-shadow: none;
  }

  .game-card-container:hover
  {
    transition: 0.5s ease;
    box-shadow: 0 -4px 0 0 #00A2FF inset;
    background-color: #eee;
  }
/* Section Content */

  .section-content
  {
    box-shadow: none;
  }

  .stack .stack-list .stack-row
  {
    box-shadow: none;
  }
/* Nav Things */

  .rbx-divider
  {
    display: none;
  }

  .rbx-left-col .rbx-scrollbar a,
.rbx-left-col .rbx-scrollbar a:hover,
.rbx-left-col .rbx-scrollbar a:focus
  {
    transition: 0.5s ease;
  }

  #nav-home:hover,
#nav-inventory:hover
  {
    transition: 0.5s ease;
    background-color: #eee;
  }

  #nav-profile:hover,
#nav-character:hover
  {
    transition: 0.5s ease;
    background-color: #eee;
  }

  #nav-message:hover,
#nav-friends:hover,
#nav-trade:hover,
#nav-group:hover,
#nav-forum:hover,
#nav-blog:hover,
#nav-shop:hover
  {
    transition: 0.5s ease;
    background-color: #eee;
  }

  .btn-secondary-md
  {
    transition: 0.5s ease;
  }

  .btn-secondary-md:hover,
.btn-secondary-md:focus
  {
    transition: 0.5s ease;
    box-shadow: 0 4px 0 0 #00A2FF;
  }

  .rbx-left-col
  {
    transition: 0.5s ease;
    box-shadow: none;
  }
/* Avatar Pictures */

  .avatar,
.avatar-card-container
  {
    transition: 0.5s ease;
    box-shadow: none;
  }

  .avatar:hover,
.avatar-card-container:hover
  {
    transition: 0.5s ease;
    box-shadow: 4px 4px #00A2FF;
    background-color: #eee;
  }
/* NavTabs */

  .rbx-tab .rbx-tab-heading
  {
    transition: 0.5s ease;
  }

  .rbx-tabs-horizontal .rbx-tab-heading:hover,
.rbx-tabs-horizontal .rbx-tab-heading:focus,
.rbx-tabs-horizontal .rbx-tab-heading:active
  {
    transition: 0.5s ease;
    background-color: #eee;
  }
/* Carousel arrow button */

  .carousel-control:hover,
.carousel-control:focus
  {
    transition: 0.5s ease;
  }

  .carousel-control
  {
    transition: 0.5s ease;
  }
/* Pager */

  .pager .first a,
.pager .last a,
.pager .pager-prev a,
.pager .pager-next a,
.pager .pager-next a span,
.pager .first a span,
.pager .last a span,
.pager .pager-prev a span,
.pager .pager-next a span
  {
    transition: 0.5s ease;
    background-color: #00A2FF;
  }

  .pager .disabled>a span,
.pager .disabled>a:hover span,
.pager .disabled>a:focus span
  {
    transition: 0.5s ease;
    background-color: #00A2FF;
  }

  .pager .first a:hover,
.pager .last a:hover,
.pager .pager-prev a:hover,
.pager .pager-next a:hover
  {
    transition: 0.5s ease;
    background-color: #32B5FF;
    box-shadow: 0 4px 0 0 #00A2FF;
  }

  .pager .first a:hover span,
.pager .last a:hover span,
.pager .pager-prev a:hover span,
.pager .pager-next a:hover span
  {
    transition: 0.5s ease;
    background-color: #32B5FF;
  }
/* Notification Red */

  .notification-red
  {
    background-color: #00A2FF;
  }
/* See all button */

  .btn-secondary-xs
  {
    transition: 0.5s ease;
  }

  .btn-secondary-xs:hover,
.btn-secondary-xs:focus
  {
    transition: 0.5s ease;
    box-shadow: 0 4px 0 0 #00A2FF;
  }
/* Robux buttons */

  .btn-primary,
.btn-primary:link,
.btn-primary:active,
.btn-primary:visited
  {
    transition: 0.5s ease;
    background-image: none!important;
    background-color: #00A2FF;
    box-shadow: none;
  }

  .btn-primary:hover,
.btn-primary:focus,
.btn-primary:active,
.btn-primary.active,
.open>.btn-primary.dropdown-toggle
  {
    transition: 0.5s ease;
    background-image: none!important;
    background-color: #32B5FF;
    box-shadow: 0 4px 0 0 #00A2FF;
  }
/* Search Buttons */

  .btn-control.btn-control-large
  {
    transition: 0.5s ease;
    background-image: none!important;
    background-color: #00A2FF;
    box-shadow: none;
    color: white;
  }

  .btn-control:hover
  {
    transition: 0.5s ease;
    background-image: none!important;
    background-color: #32B5FF;
    box-shadow: 0 4px 0 0 #00A2FF;
    color: white;
  }
/* Search Input */

  .text-box.text-box-small,
input[type=text]
  {
    border: 1px solid #00A2FF!important;
  }

  .rbx-header .rbx-navbar-search .input-field,
.rbx-header .rbx-navbar-search .input-addon-btn
  {
    border: none!important;
  }

  .chat-search .chat-search-input
  {
    border: none!important;
  }
/* 3D buttons */

  .profile-avatar-left .enable-three-dee
  {
    transition: 0.5s ease;
    background-color: #00A2FF;
    color: white;
  }

  .profile-avatar-left .enable-three-dee:hover,
.profile-avatar-left .enable-three-dee:focus
  {
    transition: 0.5s ease;
    background-color: #32B5FF;
    box-shadow: 0 4px 0 0 #00A2FF;
    color: white;
  }
/* Tabs */

  .SquareTabGray
  {
    transition: 0.5s ease;
    background-color: #00A2FF!important;
    border: none;
    border-top-right-radius: 3px;
    border-top-left-radius: 3px;
  }

  .SquareTabGray:hover
  {
    transition: 0.5s ease;
    background-color: #32B5FF!important;
  }

  .SquareTabGray a
  {
    transition: 0.5s ease;
    color: white;
  }

  .SquareTabGray a:hover
  {
    transition: 0.5s ease;
    background-color: #32B5FF!important;
  }

  .redesign .tab_white_31h_container .ajax__tab_active .ajax__tab_inner,
.tab.active,
.tab-container>.tab-active,
.tab-container>.tab-active:hover
  {
    transition: 0.5s ease;
    background-color: #00A2FF!important;
    border: none;
    border-top-right-radius: 3px;
    border-top-left-radius: 3px;
    color: white;
  }

  .redesign .tab_white_31h_container .ajax__tab_inner,
.tab,
.tab-container>div
  {
    transition: 0.5s ease;
    background-color: #00A2FF!important;
    border: none;
    border-top-right-radius: 3px;
    border-top-left-radius: 3px;
    color: white;
  }
\  .redesign .tab_white_31h_container .ajax__tab_hover .ajax__tab_inner,
.tab:hover,
.tab-container>div:hover
  {
    transition: 0.5s ease;
    background-color: #32B5FF!important;
    border: none;
    border-top-right-radius: 3px;
    border-top-left-radius: 3px;
    color: white;
  }
/* Page-specific things */
/* Home page */
/* See more button */

  .btn-control-xs
  {
    transition: 0.5s ease;
    background-color: #00A2FF;
    color: white!important;
  }

  .btn-control-xs:hover,
.btn-control-xs:focus
  {
    transition: 0.5s ease;
    background-color: #32B5FF;
    box-shadow: 0 4px 0 0 #00A2FF;
  }

  .btn-primary-md
  {
    transition: 0.5s ease;
  }

  .btn-primary-md:hover,
.btn-primary-md:focus
  {
    transition: 0.5s ease;
    box-shadow: 0 4px 0 0 #02b757;
  }
/* Profile page */

  .profile-accoutrements-container .profile-accoutrements-page-container .profile-accoutrements-page
  {
    transition: 0.5s ease;
    border: 1px solid #fff!important;
  }

  .profile-accoutrements-container .profile-accoutrements-slider .accoutrement-item .accoutrement-image
  {
    transition: 0.5s ease;
  }

  .profile-accoutrements-container .profile-accoutrements-slider .accoutrement-item .accoutrement-image:hover
  {
    transition: 0.5s ease;
    box-shadow: 4px 4px #00A2FF;
  }

  .profile-container .asset-item
  {
    transition: 0.5s ease;
  }

  .profile-container .asset-item:hover
  {
    transition: 0.5s ease;
    box-shadow: 0 -5px 0 0 #00A2FF inset;
    background-color: #eee;
  }

  .btn-control-md[disabled]
  {
    transition: 0.5s ease;
    background-color: #00A2FF;
  }

  .btn-control-md[disabled]:hover
  {
    transition: 0.5s ease;
    background-color: #32B5FF;
  }
/* Message page */

  .messages .messageDivider:hover
  {
    transition: 0.5s ease;
    background-color: #eee;
  }

  .messages .messageDivider.message-news,
.messages .messageDivider.news-body
  {
    transition: 0.5s ease;
  }
/* Avatar Page */

  .avatar-thumbnail .enable-three-dee
  {
    transition: 0.5s ease;
    background-color: #00A2FF;
    color: white;
  }

  .avatar-thumbnail .enable-three-dee:hover,
.avatar-thumbnail .enable-three-dee:focus
  {
    transition: 0.5s ease;
    background-color: #32B5FF;
    box-shadow: 0 4px 0 0 #00A2FF;
    color: white;
  }

  .pill-toggle
  {
    background-color: transparent;
  }

  .pill-toggle label
  {
    transition: 0.5s ease;
    background-color: rgba(0, 162, 255, 0.5);
    color: white;
  }

  .pill-toggle label:hover
  {
    transition: 0.5s ease;
    background-color: #00A2FF;
  }

  .item-card-container
  {
    transition: 0.5s ease;
    box-shadow: none;
  }

  .item-card-container:hover
  {
    transition: 0.5s ease;
    background-color: #eee;
    box-shadow: 0 -4px 0 0 #00A2FF inset;
  }

  .rbx-tab-horizontal-submenu li
  {
    transition: 0.5s ease;
  }

  .rbx-tab-horizontal-submenu li:hover
  {
    transition: 0.5s ease;
    background-color: #32B5FF;
    box-shadow: 0 4px 0 0 #00A2FF;
  }

  .btn-buy-md:link
  {
    color: white;
  }
/* Inventory */

  .menu-vertical .menu-option
  {
    transition: 0.5s ease;
  }

  .menu-vertical .menu-option:hover,
.menu-vertical .menu-option:focus,
.menu-vertical .menu-option.active
  {
    transition: 0.5s ease;
  }
/* Group page */

  #ctl00_cphRoblox_GroupSearchBar_SearchButton
  {
    background-color: #00A2FF;
    height: 21px;
    color: white;
    cursor: pointer;
  }

  .btn-control.btn-control-medium
  {
    transition: 0.5s ease;
    background-image: none;
    background-color: #00A2FF;
    color: white;
  }

  .btn-control:hover
  {
    background-color: #32B5FF;
  }

  .GroupListItemContainer:hover,
.GroupListItemContainer.selected:hover
  {
    transition: 0.5s ease;
  }

  .GroupListItemContainer
  {
    transition: 0.5s ease;
  }

  .btn-neutral,
.btn-neutral:link,
.btn-neutral:active,
.btn-neutral:visited
  {
    transition: 0.5s ease;
    background-image: none;
    background-color: #00A2FF;
  }

  .btn-large:hover
  {
    transition: 0.5s ease!important;
    background-color: #32B5FF!important;
    box-shadow: 0 4px 0 0 #00A2FF!important;
  }

  .btn-negative,
.btn-negative:link,
.btn-negative:active,
.btn-negative:visited
  {
    transition: 0.5s ease;
    background-image: none;
    background-color: #00A2FF;
  }

  .btn-disabled-primary,
.btn-disabled-primary:link,
.btn-disabled-primary:active,
.btn-disabled-primary:visited
  {
    background-color: #3a86b2;
    border: none;
    background-image: none;
  }
/* Forums */

  .forum-content-background,
.forum-table-row
  {
    transition: 0.5s ease;
    background-color: white;
  }

  .forum-table-row:hover
  {
    transition: 0.5s ease;
  }

  body .forum-table-header,
body .forum-table-header th,
body .forum-table-footer
  {
    background-color: #00A2FF;
  }

  .CenterColumn a.btn-control
  {
    color: white;
  }

  th
  {
    background-color: #00A2FF;
    color: white;
  }

  .table-header th
  {
    background-color: #00A2FF;
    color: white;
  }

  #forum-nav a.menuTextLink,
a.linkSmallBold:visited,
a.linkMenuSink:visited
  {
    transition: 0.5s ease;
    color: #0055B3;
  }

  A.linkMenuSink:hover,
#forum-nav a.menuTextLink:hover
  {
    transition: 0.5s ease;
    color: #32B5FF;
    text-decoration: none;
  }
/* Catalog */

  div#catalog div#dropdown ul li.subcategories
  {
    transition: 0.5s ease;
    background-color: white;
  }

  div#catalog div#dropdown ul.slideOut li
  {
    transition: 0.5s ease;
    background-color: white;
  }

  .thumbnail-holder .enable-three-dee
  {
    transition: 0.5s ease;
    background-color: #00A2FF;
    color: white;
  }

  .thumbnail-holder .enable-three-dee:hover,
.thumbnail-holder .enable-three-dee:focus
  {
    transition: 0.5s ease;
    color: white;
    background-color: #32B5FF;
    box-shadow: 0 4px 0 0 #00A2FF;
  }

  .store-card
  {
    transition: 0.5s ease;
    box-shadow: none;
  }

  .store-card:hover
  {
    transition: 0.5s ease;
    box-shadow: 0 -4px 0 0 #00A2FF inset;
    background-color: #eee;
  }

  .c-version .search-options .menu-link:hover,
.c-version .search-options .menu-link.active
  {
    transition: 0.5s ease;
  }

  .small,
.small-oneline
  {
    transition: 0.5s ease;
  }
/* The page you see when someone gets B E A N E D */

  .btn-control-md:link,
.btn-control-md:visited
  {
    color: white;
  }
/* Play Game Button */

  .btn-primary-lg
  {
    transition: 0.5s ease;
  }

  .btn-primary-lg:hover,
.btn-primary-lg:focus
  {
    transition: 0.5s ease;
    box-shadow: 0 4px 0 0 #02b757;
  }
/* Wide See more button */

  .btn-control-sm
  {
    transition: 0.5s ease;
    background-color: #00A2FF;
    color: white;
  }

  .btn-control-sm:hover,
.btn-control-sm:focus
  {
    transition: 0.5s ease;
    background-color: #32B5FF;
    box-shadow: 0 4px 0 0 #00A2FF;
    color: white;
  }
/* Dropdown again */

  .input-group-btn .input-dropdown-btn
  {
    transition: 0.5s ease;
    color: white;
    background-color: #00A2FF;
  }

  .input-group-btn .input-dropdown-btn:hover,
.input-group-btn .input-dropdown-btn:focus
  {
    transition: 0.5s ease;
    background-color: #32B5FF;
    color: white;
  }

  .input-group-btn .dropdown-menu li a:hover,
.input-group-btn .dropdown-menu li a:focus,
.input-group-btn .dropdown-menu li a.active
  {
    transition: 0.5s ease;
    background-color: #eee;
  }
/* Buy Button */

  .btn-buy-md
  {
    transition: 0.5s ease;
    background-color: #02b757;
    color: white;
  }

  .btn-buy-md:hover,
.btn-buy-md:focus
  {
    transition: 0.5s ease;
    box-shadow: 0 4px 0 0 #02b757;
  }

  .btn-primary-xs
  {
    transition: 0.5s ease;
  }

  .btn-primary-xs:hover,
.btn-primary-xs:focus
  {
    transition: 0.5s ease;
    box-shadow: 0 4px 0 0 #02b757;
  }
/* Store cards */

  .list-item .store-card:hover
  {
    transition: 0.5s ease;
    box-shadow: none;
    background-color: #eee;
  }
/* Upgrade Button */

  .btn-primary-sm
  {
    transition: 0.5s ease;
  }

  .btn-primary-sm:hover
  {
    transition: 0.5s ease;
    box-shadow: 0 4px 0 0 #02b757;
  }
/* Alert Info */

  .alert-info
  {
    background-color: #00A2FF;
  }
/* Siggy */

  .text-footer
  {
    color: transparent;
    background: url(http://i.imgur.com/Q678rvz.png) no-repeat;
    width: 200px;
    height: 50px;
  }
/* Robux page */

  .grid .cell-content
  {
    box-shadow: none;
  }

  .profile-header .header-details .details-actions .btn-unfollow
  {
    background-color: #32B5FF;
  }
/* Links */

  .text-link,
.vlist .list-item .list-body .list-content a,
.text-link:link,
.vlist .list-item .list-body .list-content a:link,
.text-link:hover,
.vlist .list-item .list-body .list-content a:hover,
.text-link:visited,
.vlist .list-item .list-body .list-content a:visited,
.text-link:active,
