css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.sofurry.com"), regexp("(?=.*?\.sofurry.com).*") {
a#sf-userinfo-outer:hover, .sf-story-big, .title.sfTextMedDark, .wide-inactive-sub:hover, #sf-textcontent-controls, .context-menu-root, .sf-market-browse-offer, .bigsubmit:hover, .smallsubmit:hover, .display-button, body, .note.sfTextMedDark, #sfContentBody p span, .popover-content, .popover-arrow
	{background: #111111 !important;}

.topbar-cont, body > .topbar, .sftc-title, .sftc-title a, .sf-footer-breadcrumb, .topbar-cont > div > ul > li > a, .topbar-cont > div > h1 > a, .topbar-cont > div > a, .topbar-cont button, #sf-chat-inputarea, .button.buttonsmall, .sf-tabbar a.button:hover, ul.tabs, ul.tabs a, .button-bar, #nuke_everything, #nuke_current_tab, #nuke_selected:active, #sfCommentReplyContainer input, .pager input, .sf-content a.button, #yt29, #yt31, .topbar-cont #yt0, .sf-tabbar-inner a.current, .button-bar input, .sf-sidebar input:not(#suggestedtags):not(#sidebarsearch), .row.submit input, .thread_actions input, #nuke_selected, #sidebarsearch, .sfBrowseList input, .section-footer .button, #site-updates #yt0
	{background: -moz-linear-gradient(top, #333333 0%, #222222 100%) !important;
	background: -webkit-gradient(linear, center top, center bottom, from(#333333), to(#222222)) !important;
	color: white;
	text-shadow: 0px 1px black !important;
	border-color: #222222 !important;
	color: white !important;}

#yt1, #yt29:hover, #yt31:hover, #site-updates #yt0:hover, .sf-tabbar, .sf-tabbar a.button, ul.tabs a.active, .sffile span, .button-bar input:hover, #sfCommentReplyContainer input:hover, .sf-sidebar input:hover:not(#sidebarsearch):not(#suggestedtags), #nuke_everything:hover, #nuke_current_tab:hover, .sf-content a.button:hover, .row.submit input:hover, .thread_actions input:hover, #suggestedtags, .sfBrowseList input:hover, .section-footer .button:hover
	{background: -moz-linear-gradient(top, #555555 0%, #222222 100%) !important;
	background: -webkit-linear-gradient(top, #555555 0%, #222222 100%);
	color: white;
	text-shadow: 0px 1px black;
	border-color: #333333 !important;
	border-top-color: #333333 !important;}

.topbar-cont button:hover, #headersearch, .sftc-title a:hover, .button.buttonsmall:hover, ul.tabs a:hover:not(.active), #LoginForm_sfLoginUsername, #LoginForm_sfLoginPassword, .topbar-cont #yt0:hover, .button-bar a.button:hover, .bbcode_quote_head
	{background: -moz-linear-gradient(top, #333333 0%, #111111 100%) !important;
	background: -webkit-linear-gradient(top, #333333 0%, #111111 100%);
	border-color: #333333 !important;}

#sf-textcontent-controls li:hover
	{background: -moz-linear-gradient(left, #202020 0%, #333333 100%) !important;
	background: -webkit-linear-gradient(left, #202020 0%, #333333 100%);
	border-style: none !important;}

.sf-footer
	{background-color: #111111;}

table.items tr.active, .sfGeneralInfoMessage, .meterorange, .sf-comment-options, span.note, #adult-profile, #profile, #signature, #sfCommentReplyTextarea, #Post_message, #PrivateMessage_message, #PrivateMessage_toUserName, #PrivateMessage_subject, #UploadForm_description, #UploadForm_P_title, #sf-upload-tags, #UploadForm_newFolderName, #sf-chat-input, .redactor_, .redactor_:focus, .bbcode_quote_body
	{background: #222222 !important;}

.meterorange span
	{background: #AA5500 !important;}

.sfGeneralInfoMessage, .topbar img, #adult-profile, #profile, #signature
	{border-color: #222222 !important;}

#sf-chat-submit, input#addtagsform_submit, #sf-socialize div:hover
	{color: #666666;
	text-shadow: 0px 1px white;
	border-color: #333333;}

div.sf-content, .sfCommentBody, .sf-sidebar, .sf-main, .sf-chat-dynamic td, .sf-content h2:not(.forumtitle), .sf-external-title, fieldset#general, fieldset#notifications, div.banner, div.watchlist, .watchlist-nuke, .date-tile, .watchlist-wrapper form, #sffp-intro, .sf-browse-shortlist-zebra, h4, fieldset, div.grey, .bigsubmit, .smallsubmit
	{background: #111111 !important;}

#sffp-events
	{background:#222222 !important;
	border-color:#222222 !important;}

#sffp-stories, #sffp-art, #sffp-photos, #sffp-music, .totalItems
	{background:#333333 !important;
	border-color:#222222 !important}

.wide-inactive:hover, .sfArtworkSmallWrapper_fit:hover, .sf-carousel-container, .watchlist_item.selected, .sf-browse-displaymodes .display-button:hover, #sf-profile-wide-nav a.wide-inactive:hover, .sf-story:hover, .sf-story-wide:hover
	{background: #111111 !important;}

.sf-watermark, .sf-chat-roomlist-active, .sf-chat-header-text, ul.watchlist_tabs a, .sf-item-h-info-content, .wide-active, .wide-active-sub, .sf-browse-shortlist, .item.even.unread, .display-button-active, .even, .sfpreviewbox, .sfpreviewboxsmall
	{background: #111111 !important;}

.sf-draggable a, .topbar-cont a.avatar, .forumtitle, .forumtitle-background, .sf-content p:not(.user-stats) {background: transparent !important;}

span.user-info, div.user-info
	{background: rgba(0,0,0,0.6) !important;}

.sf-tag, li.page.selected a, .pager a:hover
	{background: #111111 !important;
	box-shadow: 1px 1px #333333 !important;}

.watchlist_item, textarea#sfCommentReplyTextarea, #sf-profile-wide-nav, #sf-accounts, .sf-carousel-container, .sf-watermark, .sfCommentBody, .wide-active-sub, .wide-inactive-sub:hover, .dropdown.wide, .dropdown.wide li, .sf-profile-avatarcontainer, .sf-browse-displaymodes a, .sf-tabbar-inner a, #sf-socialize, .sfArtworkSmallInner.submissiononhold, #sf-profile-wide-nav a.wide-inactive:hover, .sf-story:hover, .sf-story-wide:hover, .sfTextMedDark span span, li.current a, tbody tr, .vertical .row.submit, .topbar-cont ul.dropdown, .bbcode_quote_body, .topbar, #sf-textcontent-controls li, .topbar-cont .button, #Post_message, #PrivateMessage_message, #PrivateMessage_toUserName, #PrivateMessage_subject, #UploadForm_description, #UploadForm_P_title, #sf-upload-tags, #UploadForm_newFolderName, #sf-chat-input, .redactor_box, .popover-content
	{border-color: #333333 !important;}

.sf-story-big, .sf-market-browse-offer, .wide-active
	{border-color: #666666 !important;}

#sf-textcontent-controls li
	{border-style: solid !important;
	border-width: 1px !important;
	text-shadow: none !important;}

.sf-browse-displaymodes, .sf-content form td, .sf-content form th, .watchlist_header, #sf-textcontent-controls li, table.items thead th, .sf-chat-users-levelheader, h4, .sf-story-wide, .even td, .odd td, .forum_forumpost {border-bottom-color: #333333 !important;}

.sf-content form th, .watchlist-nuke, li.current a, #sf-textcontent-controls li, fieldset p.row, .forum_fp_postsignature, .topbar-cont ul.dropdown a
	{border-top-color: #333333 !important;}

.title.sfTextMedDark, #sf-textcontent-controls, .sfCommentChildrenOuter
	{border-style: none !important;}

.sf-story .sf-story-tags a, #sfContentDescription p, #section-favors-footer.section-footer a, .sf-story-big-tags a, .sidebarfilter-inactive a, .sf-chat-rating, .sf-chat-users-levelheader, .sfArtworkSmallWrapper a.sfTextMedLight, #sf-userinfo-outer .sfTextMedLight:not(.sfTextMedium), .sf-story-wide .sf-story-tags, .sf-market-browse-offer .sfTextMedLight, .forum_fp_postsignature, .sftagbrowser-browselink a, .sfTextDark, table.items td, .forumtitle-background tbody tr 
	{color: #666666 !important;
	text-shadow: none !important;}

.sfTextMedDark .sfTextMedLight, .sf-comment-date.sfTextMedium, .sf-story abbr, .sf-story .sfTextMedium, #sf-accounts a, .forum_fp_postbody, table.items td, .sf-profile-content p, h3 a:hover, #site-updates p, .dated-list-item p:not(.date-tile), .sf-content p, .sf-browse-shortlist, .sf-browse-shortlist-main, .sf-browse-shortlist-zebra
	{color: #777777 !important;
	text-shadow: none !important;}

.sfTextMedDark span span p, div.sfCommentBody p, .sfCommentBody, .sf-story:hover .sf-story-tags a, .sf-content-invisible p, .row label, .view legend, .section-title, #sfContentBody.sfTextMedDark p, .sflabel.biglabel, .sf-chat-roomlist-room a, .sf-chat-header-text, #sf-chat-users a, #sf-userinfo-outer .sfTextMedium, .sf-story-wide:hover .sf-story-tags, #sfCommentBody.sfTextMedDark p, .sf-market-browse-offer .sfTextMedium, .section-content, .sf-chat-message, tr.active td, tr.inactive, form, span:not(.sfTextMedLight):not(.sfTextMedDark):not(.number), .sfGeneralInfoMessage, #adult-profile, #profile, #signature, #sfCommentReplyTextarea, #Post_message, #PrivateMessage_toUserName, #PrivateMessage_message, #PrivateMessage_subject, #UploadForm_P_title, #UploadForm_description, #sf-upload-tags, #UploadForm_newFolderName, #sf-chat-input, .watchlist_item .tags a, .bbcode_quote_body
	{color: #AAAAAA !important;
	text-shadow: none !important;}

span.sfTextMedDark span span a, a.wide-inactive, div.sfCommentBody a, .sfTextAttention, .sf-story-headline a, .sf-story-big-headline a, .sidebarfilter-active, .sidebarfilter:not(.sidebarfilter-inactive) a, a.display-button:hover, a.display-button-active:hover, .section-content a, td.link-column a, .sf-chat-username a, .watchlist_tabs a, .js_select_none, .js_select_all, .watchlist_meta a, table.items tr.active a, table.items tr.inactive a, .sf-sidebar .section a, .forumtitle a, .verysmalllabel, #sfContentTitle, .sf-browse-simpleitem:hover a, .sf-browse-shortlist a, .sf-browse-shortlist-zebra a, .sf-profile-avatarcontainer span, .watchlist_item h3 a, .details a, .section-title-highlight, #sf-socialize input, .forum_threads a, .sf-market-browse-offer a, .forum_fp_header a, #sfContentBody a, .bigsubmit, .smallsubmit, .smalllabel a, .sf-avatar-provided a, .sf-content a, .forumtitle-background tbody a, .wide-active, .forum_fp_header input
	{color: #C0C0C0 !important;
	text-shadow: none !important;}

table.items th, .sidebarfilter-label
	{color:#778595 !important;}

.sf-tabbar-inner a
	{color: white !important;
	text-shadow: 0px 1px black !important;}

.sfCommentBody, span.sfTextMedDark span span span span span:not(.sfTextMedLight) 
	{color:#AAAAAA !important;
	text-shadow: none !important;}

.inner, h1
	{text-shadow: none !important;}

.sfwlabel
	{background:rgba(33,33,33,0.7) !important;}
.sticky
	{position: fixed !important;
	width: 100% !important;
	left: 0;
	top: 0;
	z-index: 100;
	border-top: 0 !important;}

.script
	{width: 90px !important;}

.topbar ul.dropdown
	{background: rgba(33,33,33,0.8) !important;}

.popbox {display: block;
	position: fixed;
	top: 50%;
	left: 50%;
	z-index: 99999;
	width: 100px;
	height: 100px;
	padding: 10px;
	background: rgba(34,34,34,0.8);
	color: white;
	border: 1px solid #4D4F53;
	margin: 0px;
	-webkit-box-shadow: -50px 50px 50px -50px rgba(164, 164, 164, 1);
	box-shadow: 0px 0px 5px 0px rgba(100, 100, 100, 1);
	border-radius:25px;}

.sf-story:hover, .wide-inactive:hover, #sf-socialize form:hover, .sfArtworkSmallWrapper_fit:hover, .sfArtworkSmallInner:hover, .sfArtworkSmallWrapper:hover
	{background: none !important;}

}
