css:
@-moz-document regexp("^https?:\/\/docs\.unrealengine\.com\/.*") {
    
body {
    color: #ccc;
    background-color: #333;
}

.top-header .top {
    border-bottom: 1px solid #454545;
    background: #393939;
}

.breadcrumbs li {
    font-variant: all-small-caps;
    font-weight: normal;
}

.breadcrumbs li a {
    color: #ccc;
}

.breadcrumbs li:last-child {
    border-bottom: 1px solid #f90;
}

#wrapper .icon-links .icon-link .inner > div .content .description,
#wrapper .icon-links .icon-link .inner > div .content .description p {
    color: #ccc;
}

#wrapper .icon-links .icon-link {
    background: #393939;
    border: 1px solid #666;
}

#wrapper .topics-wrap > .topics {
    background: #393939;
    border: 1px solid #454545;
}

#wrapper .topics-wrap > .topics .info {
    color: #ccc;
}

#wrapper .topics-wrap > .topics:hover {
    box-shadow: 0 3px 3px rgba(0, 0, 0, 0.1);
    background: #454545;
}

#wrapper .icon-links .icon-link:hover {
    box-shadow: 0 3px 3px rgba(0, 0, 0, 0.1);
    background: #454545;
}

.note, .tip, .warning, .quote,
.todo, .announce, .section, .gallery,
.aux, .prereq, .callout, .alert {
    background: #393939;
    border: 1px solid #666;
}


#wrapper .fancy-links .fancy-link {
	border: 1px solid #454545;
	background: #393939;
}

#wrapper .fancy-links .fancy-link:hover {
	box-shadow: 0 0 3px 3px rgba(0, 0, 0, 0.1);
	background: #454545;
}

#wrapper .fancy-links .fancy-link span.text {
	color: #ccc;
	margin: -3px 0 0 8px;
}



/* search bar */

.top-header .top .search-wrapper-pane #dnn_topSearch_ClassicSearch .search-wrapper,
.top-header .top .search-wrapper-pane #dnn_topSearch_ClassicSearch .searchInputContainer {
	background: none;
}

.top-header .top .search-wrapper-pane #dnn_topSearch_ClassicSearch .search-wrapper input.NormalTextBox,
.top-header .top .search-wrapper-pane #dnn_topSearch_ClassicSearch .searchInputContainer input.NormalTextBox {
	border: 1px solid #454545;
	background: #333;
    transition: background, border 1s;
}

.top-header .top .search-wrapper-pane #dnn_topSearch_ClassicSearch .search-wrapper input.NormalTextBox:focus,
.top-header .top .search-wrapper-pane #dnn_topSearch_ClassicSearch .search-wrapper input.NormalTextBox:hover,
.top-header .top .search-wrapper-pane #dnn_topSearch_ClassicSearch .searchInputContainer input.NormalTextBox:focus,
.top-header .top .search-wrapper-pane #dnn_topSearch_ClassicSearch .searchInputContainer input.NormalTextBox:hover {
	border: 1px solid #0aaff1;
    background: #333;
}

ul.searchSkinObjectPreview > li.searchSkinObjectPreview_group {
    background-color: #333;
	color: #999;
	border-bottom: 1px solid #454545;
}


ul.searchSkinObjectPreview > li.searchSkinObjectPreview_group:hover {
	background-color: #333;
	color: #999;
}


ul.searchSkinObjectPreview {
	background: #333;
	border: 1px solid #454545;
    -webkit-box-shadow: 0 6px 12px rgba(0,0,0,.3);
	box-shadow: 0 6px 12px rgba(0,0,0,.3);
}

ul.searchSkinObjectPreview > li {
	border-bottom: 1px solid #454545;
	background-color: #333;
}

ul.searchSkinObjectPreview > li:hover {
	background-color: #393939;
}



ul.searchSkinObjectPreview > li > p {
	color: #999;
}


ul.searchSkinObjectPreview > li > span {
	color: #ccc;
}

ul.searchSkinObjectPreview > li p b {
	color: #c3aa88;
}


/* search results */

.col-lg-8 {
    width: calc(100% - 260px);
}

#wrapper,
.dnnSearchBoxPanel,
.dnnSearchResultPanel {
    max-width: 100%;
}

.dnnSearchBoxPanel,
.dnnSearchResultPanel {
    background: transparent;
}

.dnnSearchResultAdvancedTip {
    float: none;
    position: absolute;
    margin: 0;
    top: 18px;
    right: 5px;
}

.dnnSearchBox {
    min-height: 35px;
	border: 1px solid #666;
	background: transparent;

}

.dnnSearchBox > input[type="text"],
.dnnSearchBox > input[type="text"]:focus {
	background: #333;
}

.dnnSearchResultContainer > .dnnSearchResultItem {
	background-color: #393939;
    margin: 0 0 10px 0;
    padding: 10px 20px;
    border-radius: 4px;
}

.dnnSearchResultContainer > .dnnSearchResultItem-Subset {
    background-color: #393939;
    margin: -10px 0 10px 0;
    padding: 10px 20px 10px 40px;
    border-radius: 4px;
}

.selectize-control.single .selectize-input {
	background: #333;
    box-shadow: none;
    border-color: #666;
    color: #ccc;
}

.dnnSearchResultPanel .dnnSearchResultSortOptions {
	border: 1px solid #666;
}

.dnnSearchResultPanel .dnnSearchResultSortOptions > li.active > a,
.dnnSearchResultPanel .dnnSearchResultSortOptions > li.active > a:hover {
	background: #333;
	color: #ccc;
}

.dnnSearchResultPanel .dnnSearchResultSortOptions > li > a {
	color: #ccc;
	background: #666;
}

.dnnSearchResultPanel .dnnSearchResultSortOptions > li {
	border-left: 1px solid #666;
}

.dnnSearchResultContainer > .dnnSearchResultItem a {
    color: #808080;
}


.dnnSearchResultContainer > .dnnSearchResultItem a:hover,
.dnnSearchResultContainer > .dnnSearchResultItem-Subset a:hover{
    text-decoration: underline;
}

.dnnSearchResultContainer > .dnnSearchResultItem a:visited {
    color: #666;
}

.dnnSearchResultContainer > .dnnSearchResultItem a.dnnSearchResultItem-tagsLink,
.dnnSearchResultContainer > .dnnSearchResultItem a.dnnSearchResultItem-sourceLink,
.dnnSearchResultContainer > .dnnSearchResultItem-Subset a.dnnSearchResultItem-tagsLink,
.dnnSearchResultContainer > .dnnSearchResultItem-Subset a.dnnSearchResultItem-sourceLink {
    color: #818F72;
}

.dnnSearchResultItem-Link {
    margin: 5px 0;
}

.dnnSearchResultContainer > .dnnSearchResultItem > .dnnSearchResultItem-Title a {
    color: #478aa5;
}

.dnnSearchResultContainer > .dnnSearchResultItem > .dnnSearchResultItem-Description,
.dnnSearchResultContainer > .dnnSearchResultItem-Subset > .dnnSearchResultItem-Description{
	color: #ccc;
}

.dnnSearchResultContainer > .dnnSearchResultItem > .dnnSearchResultItem-Description b,
.dnnSearchResultContainer > .dnnSearchResultItem-Subset > .dnnSearchResultItem-Description b {
	color: #c3aa88;
}

/* mini menus */

.content-bar .os-toggle, .content-bar .version-tag {
	border: 1px solid #666;
	color: #fff;
}

.dropdown-menu {
	background-color: #393939;
	border: 1px solid #454545;
	-webkit-box-shadow: 0 6px 12px rgba(0,0,0,.3);
	box-shadow: 0 6px 12px rgba(0,0,0,.3);
}

.dropdown-menu > li > a {
	color: #ccc;
}

.dropdown-menu > li > a:focus, .dropdown-menu > li > a:hover {
	color: #fff;
}

.content-bar .os-toggle::after {
	border-top: 6px solid #666;
}


/*"on this page" navigation tree*/

.content-wrapper #on_this_page h4:before {
    content: none;
}

.content-wrapper .content-pane .on-this-page h4,
.content-wrapper #on_this_page .on-this-page h4 {
	font-size: 18px;
	color: #ccc;
    font-family: "Brutal_Light", sans-serif, arial;
}


.content-wrapper .content-pane .on-this-page ul li a,
.content-wrapper #on_this_page .on-this-page ul li a {
    border-radius: 4px;
	color: #999;
    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
    display: block;
    padding: 0 8px;
}

.content-wrapper .content-pane .on-this-page ul li a:hover ,
.content-wrapper #on_this_page .on-this-page ul li a:hover {
	background-color: #454545;
}

.content-wrapper .content-pane .on-this-page ul li a.active,
.content-wrapper #on_this_page .on-this-page ul li a.active {
	color: #333;
	background: #999;
}

/*related articles tree*/

.content-wrapper #on_this_page > div h2 {
    font-weight: normal;
	color: #ccc;
}

.content-wrapper #on_this_page > div h2:before {
	content: none;
}


/*documentation navigation tree*/

.content-wrapper #sideNav li {
    background: none;
}

.content-wrapper #sideNav {
	border-right: none;
}

.content-wrapper #sideNav:not(.jspScrollable) {
	border-right: 1px solid #454545;
}

.content-wrapper #sideNav li span {
    color: #ccc !important;
}

.content-wrapper #sideNav li.sidebar_active_parent > a > span {
    font-weight: normal;
    color: #ccc !important;
}

.content-wrapper #sideNav li.sidebar_active_item > a > span {
    font-weight: normal;
    color: #333 !important;
}


.content-wrapper #sideNav li > ul > li ul {
    background: none;
}

.content-wrapper #sideNav li.collapsibleListOpen {
    /*background: none;*/
    background: #ffffff05;
    border-radius: 4px;
}

.content-wrapper #sideNav li a:hover {
	background: #454545;
    border-radius: 4px;
}

.content-wrapper #sideNav li.sidebar_active_item > a {
    /*border-radius: 0px;*/
    background: #999;
    color: #333 !important;
    position: relative;
}

/*
.content-wrapper #sideNav li.sidebar_active_item > a > span:after {
    content: "〈"; 
    position: absolute;
    top: 50%;
    right: 5px;
    transform: translateY(-50%);
    font-size: 20px;
    font-weight: 100;
}*/

.content-wrapper .jspVerticalBar {
	background: #393939;
    transition: background 1s 1s;
}

.content-wrapper .jspVerticalBar:hover {
	background: #454545;
    transition: background 0.5s 0s;
}

.content-wrapper .jspDrag {
    background: #505050;
    border-radius: 10px;
    transition: background 1s 1s;
}

.content-wrapper .jspVerticalBar:hover .jspDrag {
	background: #666;
    transition: background 0.5s 0s;
}


/*bottom*/

.bottom-row {
	border-top: none;
	background: #333;
}


/*Exp*/

a:link {
    color: #8ab;
}
a:visited {
    color: #689;
}
a:hover {
    color: #1bf;
}

table {
    border-color: #666;
}

th {
    background-color: #454545;
    color: #ccc;
    border-color: #666;
}

td {
    background-color: #333;
    border-color: #454545;
    /*padding: 0.25em 0.5em;*/
}

td > p {
    padding: 0.25em 0.5em;
}

td:empty {
    padding: 0;
}

tr:last-child td {
    border-color: #454545;
}

.content-wrapper table > thead > tr > th {
	border-bottom: none;
    background-color: #454545;
}

.content-wrapper table > tbody > tr > td > div.tablesection {
	background: #393939;
	font-weight: bold;
}

.content-wrapper table > tbody > tr > td {
	border-top: none;
}

.content-wrapper table > thead,
.content-wrapper table > tbody > tr {
	border-top: 1px solid #454545;
}

dl dt {
	font-family: Menlo, Monaco, "Courier New", monospace;
	color: #c3aa88;
	font-weight: normal;
	margin-top: 10px;
	margin-left: -10px;
	font-size: 16px;
}


/* tweaks */

h2:before,
h3:before,
h4:before {
    margin-right: 5px;
    color: #666;
    font-family: monospace;
}

h2:before {
    content: "❱";
}

h3:before {
    content: "❱❱";
}

h4:before {
    content: "❱❱❱";
}


.note {
    background-color: #09f2;
    border-color: #09f;
}
    

.note.warning, .note.alert-warning, .tip.warning, .tip.alert-warning, .warning.warning, .warning.alert-warning, .quote.warning, .quote.alert-warning, .todo.warning, .todo.alert-warning, .announce.warning, .announce.alert-warning, .section.warning, .section.alert-warning, .gallery.warning, .gallery.alert-warning, .aux.warning, .aux.alert-warning, .prereq.warning, .prereq.alert-warning, .callout.warning, .callout.alert-warning, .alert.warning, .alert.alert-warning {
    background-color: #f332;
    border-color: #833;
}

.note.tip, .note.alert-info, .tip.tip, .tip.alert-info, .warning.tip, .warning.alert-info, .quote.tip, .quote.alert-info, .todo.tip, .todo.alert-info, .announce.tip, .announce.alert-info, .section.tip, .section.alert-info, .gallery.tip, .gallery.alert-info, .aux.tip, .aux.alert-info, .prereq.tip, .prereq.alert-info, .callout.tip, .callout.alert-info, .alert.tip, .alert.alert-info {
    background-color: #9f31;
    border-color: #693; 
}


.todo {
    background-color: #f901;
    border-color: #f90; 
}

.quote {
    background-color: #454545;
    border-color: #666; 
}    

.blue {
    color: #06f;
}

.topics {
    display: flex;
    flex-wrap: wrap;
}

.topics.item {
    background-color: #393939;
    border: 1px solid #454545;
    vertical-align: top;
    display: flex;
    margin: 0.3em;
}

.topics.item:hover {
    background-color: #454545;
    /*box-shadow: 0 0 0 1px #666;*/
}

a.topics.item,
a.topics.item:hover {
	color: #47AFF0;
}


.content-wrapper .content-pane .block-link-list li a,
.content-wrapper #on_this_page .block-link-list li a {
	border-bottom: 1px solid #454545;
}


/*code blocks*/
    
pre.prettyprint {
	width: auto;
}

pre {
    color: #c3aa88;
    background-color: #333 !important;
    padding: 0;
    border-radius: 4px;
    border-color: #666 !important;
    max-height: none !important;
    white-space: nowrap;
}

pre * {
    font-family: Menlo, Monaco, "Courier New", monospace !important;
  /*  line-height: 16px !important;*/
}

.question-body pre,
.comment-text pre,
.answer-body pre {
   /*max-height: 100%;*/
}

code {
    background-color: transparent;
    color: #c3aa88 !important;
    box-shadow: 0 0 2px 0px rgba(255,255,255, 0.3) inset;
    line-height: 12px;
    padding: 1px 2px !important;
    border-radius: 4px;
}

td code {
    color: #c3aa88;
}

pre > code {
    display: block;
    white-space: pre-wrap;
    word-break: break-all;
    color: #fff;
    border-radius: 0;
    box-shadow: none;
    padding: 4px 15px !important;
}

pre.prettyprint {
	border: 1px solid #666 !important;
	padding: 4px 15px !important;
	border-radius: 4px !important;
	font-size: 14px !important;
}
    
    /*td pre code {
        max-width: 100%;
    }*/
    

.atn, .tag,
.typ {
    color: #FFCD22 !important;
}
    
.atv,    
.str {
    color: #EC7600 !important;
}

.pun {
    color: #FFF !important;
}

.pln {
    color: #b393e9 !important;
}

.opn {
    color: #F00 !important;
}

.clo {
    color: #0F0 !important;
}

.lit {
    color: #999 !important;
}

.kwd {
    color: #8ED54D !important;
}

.com {
    color: #818F72 !important;
}

    /*code blocks end*/ 
}
