css:
@-moz-document domain("web.kiwari.id") {
::-webkit-scrollbar {
  width: 0px;
  background: transparent;
}
::-webkit-scrollbar-thumb {
  background: #FF0000;
}
:root {
  --bg-flower-url: url(https://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-569780.jpg);
  --bg-girl: url(https://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-259990.jpg);
  --bg-frost-warframe: url(https://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-26977.jpg);
  --bg-dark-picture: url(https://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-557746.png);
  --bg-landscape: url(https://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-556403.jpg);
  --bg-green-garden: url(https://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-569771.jpg);
  --bg-lynx: url(https://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-452200.jpg);
  --bg-sakura: url(https://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-551327.jpg);
  --bg-main: var(--bg-sakura);
}
main {
  background: #333;
  background-image: var(--bg-main);
  background-size: 100vw;
  background-position: center;
}
.splash-screen { display: none; }
.qisme-container::before {
  content: ' ';
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 100%;
  /* girl */
  /*   background-color: white; */
  
  background-image: var(--bg-main);
  /* frost warframe */
  /* some dark picture */
  /* landscape */
  
  background-position: center;
  background-size: 100vw;
  background-repeat: no-repeat;
  filter: brightness(0.4) blur(0px);
}
.qcw-container,
.widget-chat-wrapper,
#qcw-app #messages__comments {
  background: transparent;
}
ul#messages__comments::-webkit-scrollbar,
ul#messages__comments::-webkit-scrollbar-thumb {
  width: 0;
}
.qcw-comment .qcw-avatar {
  border-radius: 2px 0 0 2px;
  margin-right: 0;
}
.qcw-comment .qcw-avatar + .qcw-comment__message {
  border-top-left-radius: 0;
}
.qcw-comment__message {
  box-shadow: none;
}
.qcw-comment.comment--me .qcw-avatar {
  border-radius: 0 2px 2px 0;
  margin-left: 0;
}
.qcw-comment.comment--me.comment--parent .qcw-comment__message {
  border-top-right-radius: 0;
}
.qcw-header {
  z-index: 1;
  background: rgba(0, 0, 0, 0.5);
  color: white;
  border-radius: 0;
}
.qcw-comment__message,
.comment--me .qcw-comment__message {
  background: rgba(0, 0, 0, .6);
  color: #ddd;
}
.comment--parent .qcw-comment__message:after,
.comment--parent.comment--me .qcw-comment__message:after {
  display: none;
  border-color: rgba(0, 0, 0, 0.6);
  background: rgba(0, 0, 0, .6);
}
.qcw-comment__username,
.qcw-comment__time {
  color: #ddd;
}
.load-more-button {
  position: relative;
  background: rgba(0, 0, 0, .4);
  color: #ddd;
}
.qcw-comment-date {
  position: relative;
}
.reply-wrapper {
  background: rgba(123, 123, 123, .7);
  color: #ddd;
}
.reply-wrapper .reply-sender {
  color: #ddd;
}
.qisme-container .sidebar {
  background: rgba(0, 0, 0, .5);
  color: #ddd;
  position: relative;
}
.application--light .list .list__tile:not(.list__tile--active),
.application--light .list .list__tile__sub-title,
.application--light .list .subheader,
.list__tile--active .list__tile__title {
  color: #ddd !important;
}
.sidebar__header {
  z-index: 1;
  background: transparent;
}
.sidebar__footer {
  background: transparent;
  position: relative;
}
.list--group__container .list .list--group,
.application--light .list {
  background: transparent;
}
.qcw-comment-form {
  background: rgba(0, 0, 0, .5);
  color: #ddd;
}
.btn__emoji i {
  color: #ddd;
}
.qc-icon {
  fill: #ddd;
}
}
