css:
@-moz-document domain("www.blognone.com") {
a:link, a:visited { color: #fff; }
a:hover { color: #bbb; text-decoration: bold; }
a:active, li a.active { color: #999; }
body, #wrapper { background: #111; color: #aaa;font-size:14px; }
#navi-menu, #footer { background: #333; }
#header, .node, #rightbottom_sidebar, #block-user-login, #above_comments, #comment-area, .user-block, .profile, #user-profile-form, .content-container .wrapper, #node-form, #forum, .views-edit-view, #user-pass, #user-register, .view-empty, .node-type-list, #tracker { border: 0; background-color: #222; }
.node { margin-bottom: 1.5em; }
.content-title-box, #comments .title { background: #333; border: 0; }
.submitted { background-color: #222 !important; border: 0; border-radius: 0; padding: 0; }
.content .node-image { margin: 10px 1.5em; padding: 0; line-height: 0; }
.news-info { border-top: 0; padding: 0 1em .5em; text-align: right; }
blockquote { border: 2px dashed #666; background: transparent; padding: .5em 1em; }
/* Comment */

.comment-info { background: #444; }
#comments .indented { margin-bottom: 1em; }
#comments, .comment-odd-depth ~ .indented { background-color: #222; }
.comment-even-depth ~ .indented { background: #333; }
.comment-signature { border-bottom: 1px solid #444; }
.item-list .pager li, .item-list .pager li.pager-current { background: transparent; }
/* Table */

tr, tr.even, tr.odd { background: transparent; border: 0 !important; }
thead th { background: #333 !important; border: 0; }
/* Tabs */

ul.primary { border-bottom: 1px solid #444; }
ul.primary li a { background-color: transparent; border-color: #444; display: inline-block; padding: .5em 1em; }
ul.primary li.active a { background-color: #444; border-color: #444; color: #ccc; }
ul.primary li a:hover { background-color: #222; border-color: #444; }
/* Form */

form input[type=submit], .signout { background-color: #555 !important; border: 0 !important; border-radius: 0 !important; color: white !important; padding: .5em 1em !important; margin: 0 1em 1em 0 !important; }
form input[type=text], form input[type=password], form textarea { background-color: #555 !important; border: 0 !important; color: white !important; padding: .5em 1em; margin: 0 1em 1em 0; }
/* Display None */

#above_comments, .social-sharing, #page-sharing { display: none; }
}
