css:
@-moz-document url-prefix("https://todoist.com/app") {
#editor,
#content {
    background-color: #333333;
}
#left_menu {
    background-color: #404040;
    border-right: 1px solid #4c4c4c;
}
.theme_graphite #top_bar {
    background-color: #404040;
    border-color: #404040;
}
li.current table, #todoist_app .panel_summary, .reactist.panel_summary .panel_summary__icon {
    background-color: #404040 !important;
}
body,
.filter,
.generic_left_list .name,
.section_header a,
.project_link,
#editor .items li .text,
.date,
.subsection_header a,
a.ex_link,
.task_item a.ex_link,
div#completed_today .heading,
.panel_summary__content h5
{
    color: #AAAAAA !important;
}
.on_menu_show {
    background-color: #504f4f;
}
.task_item .task_content_item,
.subsection_header,
.reactist.expansion_panel,
.reactist.panel_summary.panel_summary--expanded{
    border-bottom: 1px solid #4c4c4c;
}
.theme_graphite a.action,
.theme_graphite .note_extra_info a,
.theme_graphite #item_commander a,
.theme_graphite .blank_screen a,
.theme_graphite .premium_only a.sel_premium,
.theme_graphite .completed_quick_viz a {
    color: #7b9ab7 !important;
}
.amicheckbox {
    background-color: transparent !important;
}
.filter:hover table,
#left_menu .items li:hover table,
#top_filters li.current,
#top_filters li:hover,
#left_menu .FilterListItem:hover table,
.reactist.panel_summary:hover{
    background-color: #504f4f !important;
}

.FilterListItemSortable.is_selected td {
    position: initial;
    background-color: #504f4f !important;
}

#page_background {
    background-color: #404040 !important
                                            ;
}
.ist_checkbox {
    background-color: #424141 !important;
}

.task_item td.checker, .task_item .task_content_item {
    border-bottom: 1px solid #4c4c4c !important;
}

.FilterListItem:hover table {
    background-color: #fff;
    border-radius: 2px;
}

.filter:hover table, #left_menu .items li:hover table, #top_filters li.current, #top_filters li:hover {
    background-color: #504f4f !important;
}

.drag_and_drop_handler img{
	filter: brightness(0.35);
}

#completed_today .illustration {
    filter: brightness(1.4);
}

#todoist_app .panel_summary .adder_icon{
	fill: #AAA;
}
}
