css:
@-moz-document url(https://tweetdeck.twitter.com/), url(https://tweetdeck.twitter.com/#)  {
/** I do not own, or claim to own any of the University of Utah's logos, designs, or any associated content. This is just a tribute **/


/* i really want this to be global */


/** GLOBAL STYLES **/

html {
  color: #b7b7b7;
}

body {
  background-image: url(https://upload.wikimedia.org/wikipedia/commons/thumb/9/92/Utah_Utes_-_U_logo.svg/1200px-Utah_Utes_-_U_logo.svg.png);
  background-color: #000;
  background-repeat: no-repeat;
  background-size: contain;
  background-position: center;
}

a:hover {
  color: rgb(204, 0, 0);
}

input:focus, select:focus, textarea:focus, .focus {
  border-color: rgba(230, 80, 80, 0.8);
  box-shadow: inset 0 1px 3px rgba(17, 17, 17, 0.1), 0 0 8px rgba(230, 80, 80, 0.6);
}

.column.is-focused {
  box-shadow: 0 0 0 6px #9c5f5f;
}

.app-title {
  background-color: #222426;
  left: -4px;
}

.nav-user-info {
  left: -4px;
}


/*** SIZING ***/

.is-condensed .app-header {
  width: 40px;
}

.app-header-inner {
  min-width: 40px;
}

.app-header.is-condensed .app-header-inner {
  width: 40px;
}

.list-placeholder {
  text-shadow: 1px 1px 1px #000;
  color: #fff;
}

.column-nav-item {
  height: 35px;
  background-color: #292f33;
  padding: 2px;
}


/** TWEET BUTTON **/

.tweet-button {
  height: 34px;
  font-size: 16px;
}

.tweet-button .icon-compose, .tweet-button .icon-compose-dm {
  font-size: 20px;
  margin-left: -4px;
}


/** Main Styles **/

.txt-mute {
  color: rgba(128, 128, 128, 1);
}

.icon-large {
  font-size: 20px;
}

.icon-medium {
  font-size: 18px;
}

.app-nav-link {
  margin-left: -2px;
}

.txt-size--16 {
  font-size: 14px !important;
}

.js-user-actions-menu.btn.btn-on-dark.btn-square {
  border: 1px solid #808080;
  color: #808080;
}

.bg-seamful-faint-gray {
  background-color: #000;
}

.bg-r-white {
  background-color: #efefef;
}

.btn {
  color: #CC0000;
}
.block.column-type-icon.icon.txt-r-medium-blue{
color: #cc0000;
}

.btn.js-account-action {
  border: 1px solid #cc0000;
}

.is-inverted-dark .btn:hover {
  background-color: #dadada;
  color: #000;
  border: 1px solid #CC0000;
}

.lst-group .selected {
  background-color: #cc0000;
}

.is-inverted-dark a, .is-inverted-dark a:hover, .is-inverted-dark a:focus, .is-inverted-dark a:active, .txt-twitter-blue {
  color: #cc0000;
}

.contributor-manager .txt-r-medium-gray {
  color: #fff;
}

.column-header-link {
  color: #7f6666;
}

.compose {
  background-color: #222426;
  color: #fff;
}

.js-send-button.js-spinner-button.js-show-tip.is-disabled.btn.btn-positive {
  background-color: #f21d1d;
  border-color: #f21d1d;
}

.btn-on-blue {
  color: #fff !important;
  border-color: transparent;
  background-color: #cc0000;
}

.btn-positive, .btn-positive-alt, .btn-fav.s-favorited, .s-following .follow-btn {
  color: #fff !important;
  background-color: #cc0000;
  border: 1px solid #cc0000;
  box-shadow: none;
}

.btn-positive:hover, .btn-positive-alt:hover, .btn-fav.s-favorited:hover, .s-following .follow-btn:hover {
  color: #fff !important;
  border-color: transparent;
  background-color: #66757f;
  box-shadow: none;
}

.app-columns-container {
  background-color: #000000;
}

.app-nav-link, .nav-user-info .username, .app-nav-tab, .column-nav-link .attribution {
  color: #808080;
}

.app-nav-tab.is-selected, .app-nav-tab.is-selected:hover {
    color: #cc0000;
}

input.on-blue:focus {
  box-shadow: 0 0 2px 3px #cc0000;
}

.btn[disabled], .btn[disabled]:hover, .btn[disabled]:active, .btn.is-disabled, .btn.is-disabled:hover, .btn.is-disabled:focus, .btn.is-disabled:active {
  border: 1px solid #f21d1d;
  color: #f21d1d;
background-color: darkgrey;
}

.attach-compose-buttons .tweet-button {
  background-color: #222426;
  box-shadow: none;
  max-width: 100%;
}

.txt-r-text-link {
  color: #cc0000;
}

.list-item:hover, .list-item:active, .list-item.is-selected {
  background-color: #cc0000;
  color: #fff;
}

.txt-mute a:not(:hover):not(:focus) {
  color: #b1b1b1;
}

.account-bio {
  color: rgb(130, 116, 116);
}


/** Compose Button **/

.btn-compose {
  color: #fff;
  background-color: rgb(204, 0, 0);
}

.btn-compose:hover {
  color: #fff;
  background-color: #cc0000;
  border: none;
  box-shadow: inset 0px 0px 12px 2px #5d0202;
}


/** App Search **/

.app-search-fake {
  color: rgba(204, 0, 0, 1);
  background-color: #222426;
}

.app-search-fake .icon, .app-search-fake .Icon {
  margin-top: -4px;
  margin-left: -3px;
}


/** Header **/
.is-condensed .app-content {
    left: 40px;
}

.app-header, .app-navigator, .column-nav-item {
  background-color: #1d1d1d;
}

/** Menu Drawer **/
.drawer-header-title {
color: #cc0000;
}

.drawer-header {
border-bottom: 0;
}

.settings-modal .bg-seamful-faint-gray {
    background-color: #fff;
}

li.is-selectable:hover {
    background-color: #cc0000;
}

li.is-selectable:hover a{
    color: #fff;
}

.dropdown-menu .is-selected {
    background: #cc0000;
    color: #fff;
}

.Button:hover, .Button.is-hover {
    background-color: #eaf5fd;
    color: #cc0000;
    text-decoration: none;
    border: 1px solid #cc0000;
}

.Button, .Button:visited, .Button.is-visited {
    border: 1px solid #cc0000;
    color: #cc0000;
}
.hover-bg-r-faint-blue:hover, .hover-bg-r-faint-blue:focus {
    background-color: #fff2f2;
}

/** Columns **/

.accordion .is-active {
  color: #cc0100;
}

.app-columns {
  padding: 0 0 0 3px;
}

.btn-options-tray:hover, .btn-options-tray:focus {
  color: #cc0100;
}

.column-nav-link .column-head-title {
  font-size: 14px;
}

.column-nav-updates {
  color: #cc0100;
  z-index: 10;
  right: 8px;
  top: -0;
}

.column-nav-link.is-selected, .column-nav-link:hover {
  background-color: #410e0c;
}

.column {
  margin: 0 3px 0 0;
  background-color: #000;
  background-image: url(https://upload.wikimedia.org/wikipedia/commons/thumb/b/be/Utah_Utes_logo.svg/1200px-Utah_Utes_logo.svg.png);
  background-size: contain;
}

.column-header, .column-header-temp {
  background-color: #14171A;
}

.column-header-link:hover, .column-header-link:focus, .column-header-link:active {
  color: #cc0100;
}

.column-header .column-type-icon, .column-header-temp .column-type-icon {
  color: #cc0100;
}

.column-message {
  background-color: #000;
}

.column-message, .column-options .button-tray {
  background-color: #1d1d1d;
}

.column-nav-link .icon, .column-nav-link .Icon {
  color: #fff;
  padding-left: 0;
  margin-left: -2px;
}

.column-title-edit-box {
color: #b7b7b7
}

.facet-content {
  color: #cc0100;
}

.facet-subtitle {
  color: rgba(128, 128, 128, 1);
}

.facet-type.is-active {
  background-color: rgb(41, 48, 52);
}

.is-options-open .column-settings-link {
  color: #cc0100;
  outline: none;
}

.is-options-open .column-settings-link:hover {
  color: #cc0100;
  outline: none;
}

.is-options-open .column-settings-link {
  border-width: 2px;
}

/** EXTRA **/
.follow-btn.btn {
border: 1px solid #cc0000;
}
button.js-user-actions-menu.btn.btn-square {
    border: 1px solid #cc0000;
}
header.js-column-header.js-action-header.flex-shrink--0.column-header-temp {
    background-color: #fff;
}
.btn-on-dark:focus {
    box-shadow: 0 0 0 1px #292F33, 0 0 0 3px rgba(204, 0, 0, 0.53);
    outline: none;
}
span.js-tooltip-target.link-complex-target {
    color: #808080;
}
i.icon.icon-arrow-l {
    color: #cc0000;
}
}
