css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://earth.ispras.ru/cgi-bin/"), url-prefix("http://contest.solutions/cgi-bin/") {
  body {
    background-color: #272727;
    color: #AAA;
  }

  #l11 {
    background: none!important;
  }
  tr, td, th, table, li {
    border-color: #666!important;
  }
  p > img {
    background-color:white;
  }
  tr[bgcolor="#ddffdd"] {
    background-color: #2E9448;
  }
  .probNav > tbody > tr > td {
    background-color: #272727!important;
  }
  .user_actions {
    background-color: #077FB7!important;
  }
  .contest_actions {
    background-color: #077FB7!important;
  }
  .server_status_on {
    background-color: #2E9448!important;
    color: #DDD!important;
    text-shadow: 1px 1px #555;
  }
  .server_status_alarm {
    background-color: #BCBC34!important;
    color: #DDD!important;
    text-shadow: 1px 1px #555;
  }
  .server_status_error {
    background-color:#B63E3E!important;
    color: #DDD!important;
    text-shadow: 1px 1px #555;
  }
  .nProbOk {
    background-color: #2E9448!important;
  }
  .nProbCurrent {
    background-color: #999!important;
  }
  .nProbEmpty {
    background-color:#666!important;
  }
  .nProbBad {
    background-color:#B63E3E!important;
  }
  a, .b1, .menu {
    color: #DDD!important;
    text-shadow: 1px 1px #555;
  }
  a.menu:hover {
    background:none!important;
  }
  a:hover {
    color:#FFF!important;
  }
  .st_prob[bgcolor="#ffff88"] {
    background-color: #484848;
    font-weight:bold;
  }
  #probNavTaskArea > h3 {
    display:none;
  }
  .main_phrase {
    font-size: 18px!important;
  }
  h2 {
    font-size: 22px;
  }
  .nTopNavList > li, .nBottomNavList > li {
    margin: 0 0 0 0.5em!important;
  }
}
