}

.table-striped > tbody > tr:nth-child(even) > td, .table-striped > tbody > tr:nth-child(even) > th {
    background-color:#e6ced6;
}

.nav-pills > li.active > a, .nav-pills > li.active > a:hover, .nav-pills > li.active > a:focus {
    background-color:#ce396b;
}

.btn-primary, .input-group-addon.clickable.btn-primary, .btn.btn-primary, .btn.input-group-addon.clickable.btn-primary {
 	background-color:#ce396b;   
}

.nav-tabs-2ndlevel > li.active > a, .nav-tabs-2ndlevel > li.active > a:hover, .nav-tabs-2ndlevel > li.active > a:focus {
 	border-color:#ce396b;   
}

.nav-tabs-2ndlevel > li > a:hover {
    color: #ce396b;
}

a.list-group-item.active {
