}

.nav-tabs > li:not(.not-a-tab).active > a, .nav-tabs > li:not(.not-a-tab).active > a:hover, .nav-tabs > li:not(.not-a-tab).active > a:focus, .nav-tabs > li:not(.not-a-tab).active > div > a, .nav-tabs > li:not(.not-a-tab).active > div > a:hover, .nav-tabs > li:not(.not-a-tab).active > div > a:focus {
