}

.caseHeader {
 	background: linear-gradient(#e0c1c3, #000000); 
}

.caseHeader > .nav-tabs-2ndlevel > li > a {
 	color: #ffffff;   
    text-shadow: none;
}

.caseHeader > .nav-tabs-2ndlevel > li.active > a, .caseHeader > .nav-tabs-2ndlevel > li.active > a:hover, .caseHeader > .nav-tabs-2ndlevel > li.active > a:focus {
 	color: #ffffff;
    text-shadow: none;
}

.icon-ja {
 	mix-blend-mode: normal;   
}

.icon-ja::after {
 	color: #000000;   
}

.nav.nav-tabs.navbar.navbar-fixed-top.container, .navbar-extension {
 	background-color: #000000;   
}

.navbar-fixed-top, .navbar-fixed-bottom {
 	background-color: #000000;   
}

.navbar-collapse.collapse {
 	color: white;   
}

