css:
@-moz-document domain(gingkoapp.com) {
.card h1,
.card h2,
.card h3,
.card h4 {
    font-weight: bold;
}
.columns,
.columns-wrapper {
    width: 100%!important;
}
.columns .column {
    width: 33.33%!important;
}
#c1 {
    margin-left: 0!important;
}
*,
h1,
h2,
h3,
h4,
p {
    font-family: "Open Sans";
}
.card a.taglink {
    padding: 0 2px;
    border-radius: 0;
    line-height: 26px;
    border: none;
}
.card a.taglink:hover {
    border: none;
}

input[type=checkbox]::after {
    content: "";
    width: 16px;
    height: 16px;
    border: 1px solid #808080;
    border: 1px solid rgba(113, 124, 146, 0.2);
    background: #FFF;
    margin: 0;
    float:left;
   	margin-right: 5px;
    margin-top: -2px;
    margin-bottom: -4px;
    margin-left: -6px;
}
.checked input[type=checkbox]::after {
        content: "";
    background: #3D454B;
    background: #a5abb7;

}
.card .todo-list .checked {
    text-decoration: none;
    color: gray;
}
}
