css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.broadcastify.com") {
  body {
    background: #222;
    color: #dedede;
  }
  h1, h2, h3, .pageTitleNormal, .filterHeader {
    color: #dedede;
  }
  h2 {
    text-transform: none;
    border-bottom: 0;
    padding-bottom: 0;
  }
  .layout td.border {
    border: 0;
  }
  .subHeadListen {
    border: 1px solid #444;
    border-right: 0;
    border-left: 0;
    background: #222;
    height: auto;
    padding: .5em 0;
  }
  .subHeadListen table.layout td[style="width: 250px;"] {
    display: none;
  }
  .webPlayerHeader,
  .subHeadBg {
    background: #333;
    width: auto;
  }
  .playerContentBox, 
  .contentBox {
    background: #333;
    border: 0;
  }
  .playerContentBox a,
  .contentBox a,
  .btable td a {
    color: #ffcccc!important;
  }
  #bcfyplayer {
    border: 1px solid #444;
  }
  .messageBoxWhite {
    background: #444;
    border: 0;
    color: #dedede;
    font-size: .8em;
  }
  .messageBoxWhite .button-upgrade-now {
    background: transparent;
    height: auto;
  }
  .messageBoxWhite .button-upgrade-now:after {
    content: "Upgrade Now";
  }
  #footer {
    background: transparent;
  }
  .homeBottom {
    background: transparent;
    border: 0;
  }
.bodyCopy .c img {
    opacity: .2;
    border: 1px solid #555;
  }
  
  /*broadcast tables*/
  .btable td {
    background: transparent;
    border: 1px solid #222;
  }
  .btable td.online {
    background: #1F9034;
  }
  .btable td.offline {
    background: #594F6B;
  }
  
  /*make map less white*/
  #mapContainer {
    opacity: .5;
    width: auto;
  }
  
  /*replace login buttons*/
  a.button-login,
  a.button-signup {
    background: #333;
    height: auto;
    text-align: center;
    border: 1px solid #111;
    border-radius: .3em;
    box-shadow: 1px 1px 1px #111;
    text-decoration: none;
    color: #dedede;
    font-size: .8em;
  }
a.button-login:after {
    content: "Log in";
  }
  a.button-signup:after {
    content: "Sign up";
  }
  
  /*replace main navigation buttons*/
  .button-menu-listen, 
  #bml,
  .button-menu-home,
  .button-menu-broadcast,
  .button-menu-premium,
  .button-menu-home-hover {
    background: transparent;
    color: #8A8A8A;
    text-decoration: none;
    height: 100%!important;
    width: auto!important;
  }
  .button-menu-listen:after, #bml:after {
    content: "Listen";
  }
  .button-menu-home:after,
  .button-menu-home-hover:after{
    content: "Home";
  }
  .button-menu-broadcast:after {
    content: "Broadcast";
  }
  .button-menu-premium:after {
    content: "Premium";
  }
  
  /*feed navigation*/
  .subHeadContentc a, .subHeadContent a:hover {
    color: #88B7FF;
  }

  /*icons with white backgrounds*/
  .redStar {
    border-radius: .6em;
    opacity: .5;
  }
  .icon-currentlistener,
  .icon-livefeeds,
  .icon-topfeed {
    opacity: .5;
  }
  
  /*forms*/
  select, input[type="text"], input[type="search"], input[type="submit"], button {
    border: 1px solid #111;
    background: #222;
    color: #dedede;
    border-radius: .3em;
    padding: .3em;
    box-shadow: 1px 1px 1px #222;
  }
  
  /*remove clutter*/
  #hero, hr, #headerSpacer {
    display: none;
  }
  
  /*header*/
  #header table {
    position: relative!important;
    padding: 0 0 0 200px!important;
    height: 100%;
  }
  #header table tr td {
    vertical-align: top;
    padding-top: 20px;
    padding-right: 20px;
  }
  #header table tr td[style="width: 20px"] {
    display: none;
  }
/*base64 logo*/
  #header {
    height: 50px;
    overflow: auto;
    background-color: transparent;
    background:  url('data:image/png;base64,') top left no-repeat;
  }
}
