css:
@-moz-document url-prefix("https://togethertube.com/"), url-prefix("https://www.togethertube.com/"), url-prefix("http://togethertube.com/"), url-prefix("http://www.togethertube.com/"){
  body {
      font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
      font-size: 14px;
      line-height: 1.428571429;
      color: #9d9d9d;
      background-color: #3a3838;
  }

ol.user-list>li:nth-child(even) {
    background-color: #3a3838;
}

  .navbar-inverse {
      background-color: #222;
      border-color: #080808;
  }

  .navbar-default {
      background-color: #313131;
      border-color: #000000;
  }

  ._2tga {
      background: #313131;
      border: 1px solid #000000;
      color: #fff;
      cursor: pointer;
      font-family: Helvetica, Arial, sans-serif;
      -webkit-font-smoothing: antialiased;
      margin: 0;
      -webkit-user-select: none;
      white-space: nowrap;
  }

  .navbar-text {
      color: #9d9d9d;
  }

  .player-overlay-wrapper .player-overlay {
      overflow: hidden;
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      padding: 30px;
      background-color: #313131;
      text-align: center;
  }

  .player-overlay-wrapper .player-overlay .player-overlay-textbox {
      padding: 15px;
      margin: 0 100px;
      background-color: #4c4a4a;
      border-radius: 4px;
  }

  h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {
      font-family: inherit;
      font-weight: 500;
      line-height: 1.1;
      color: #9d9d9d;
  }
  
  #player #positionSlider {
    border-color: #000000;
}

.player-overlay-wrapper .player-overlay i.fa {
    color: #4c4a4a;
}

  .slider .track {
      position: absolute;
      left: 0;
      background-color: #222222;
      width: 100%;
      height: 15px;
  }

  .slider .bubble {
      position: relative;
      left: 0;
      top: 0;
      width: 15px;
      height: 15px;
      border-radius: 4px;
      background-color: #656565;
      border-radius: 4px;
  }

  .slider .statusTrack {
      position: absolute;
      left: 0;
      top: 0;
      background-color: #2f2f2f;
      width: 0;
      height: 15px;
  }

  .slider .leftTrack {
      position: absolute;
      left: 0;
      top: 0;
      background-color: #464343;
      border-radius: 4px;
      width: 0;
      height: 15px;
  }

    .panel-default {
      border-color: #000;
  }

    .panel {
      margin-bottom: 20px;
      background-color: #313131;
      border: 1px solid rgb(0, 0, 0);
      border-radius: 4px;
      -webkit-box-shadow: 0 1px 1px rgba(0,0,0,.05);
      box-shadow: 0 1px 1px rgba(0,0,0,.05);
  }

   .panel-footer {
      background-color: #313131;
      border-top: 1px solid #000;
  }

    .form-control {
      background-color: #3a3838;
      border: 1px solid #000;
  }

  a {
      color: #9d9d9d;
      text-decoration: none;
  }


.panel-chat .chatlog {
    overflow-y: auto;
}

  .btn-default {
      color: #9d9d9d;
      background-color: rgb(49, 49, 49);
      border-color: #000000;
  }

  .nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {
      color: #9d9d9d;
      background-color: #313131;
      border: 1px solid #000;
  }

  ol.videoList>li.videoListEntry .content {
      border: 1px solid #000;
      border-radius: 0 0 4px 4px;
  }

  ol.videoList>li.videoListEntry .header {
      background-color: #313131;
      border: 1px solid #000;
      border-radius: 4px 4px 0 0;
  }

  ol.videoList>li.videoListEntry .content>.list-container:nth-child(3) {
      background-color: #313131;
      border: 1px solid #000000;
      border-radius: 4px;
  }

  .btn-success {
      color: #9d9d9d;
      background-color: #232323;
      border-color: #000000;
  }

  .btn-danger {
      color: #9d9d9d;
      background-color: #232323;
      border-color: #000000;
  }

  ol.user-list>li:first-child {
      border-top: 1px solid #000;
  }

  ol.user-list>li {
      border-bottom: 1px solid #000;
  }

  .btn-primary {
      color: #9d9d9d;
      background-color: #212121;
      border-color: #000000;
  }

  .footer {
      border-top: 1px solid #000;
      color: #9d9d9d;
  }
  
  .table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #222222;
}
  
  .pagination>.disabled>span, .pagination>.disabled>span:hover, .pagination>.disabled>span:focus, .pagination>.disabled>a, .pagination>.disabled>a:hover, .pagination>.disabled>a:focus {
    background-color: #313131;
    border-color: #000;
}
  
  .pagination>.active>a, .pagination>.active>span, .pagination>.active>a:hover, .pagination>.active>span:hover, .pagination>.active>a:focus, .pagination>.active>span:focus {
    background-color: #191919;
    border-color: #000000;
}

.form-control {
    background-color: #4e4c4c;
    color: #989898
}

.list-group-item {
    background-color: #222;
    border: 1px solid #000;
}
  
  .list-group-item.active, .list-group-item.active:hover, .list-group-item.active:focus {
    background-color: #1d1c1c;
    border-color: #000000;
}
  
 .list-group-item {
    background-color: #4e4c4c;
    border: 1px solid #000;
} 

a.list-group-item .list-group-item-heading, button.list-group-item .list-group-item-heading {
    color: #c5c4c4;
}

a.list-group-item, button.list-group-item {
    color: #9d9d9d;
}

.nav-tabs {
    border-bottom: 1px solid #000;
}

.container-non-responsive {
    margin-left: 20px;
    margin-right: 20px;
    width: 80%;
    margin: 0 auto;
}

.panel-chat .chatlog {
    overflow-y: scroll;
    overflow-wrap: break-word;
    height: 581px;
}  

}

@-moz-document url(https://togethertube.com), url(https://www.togethertube.com), url(http://togethertube.com), url(http://www.togethertube.com) {
  h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {
      color: #ffffff;
  }
  
  .use-cases-icon-box li p.description {
    color: #cacaca;
   }
  
  .features-icon-box li p.description {
    color: #cacaca;
  }
}
