css:
@-moz-document domain("qq.com"){
/* 去广告 */
.qq_gameRec,
.gj-nav.gj-style-blue,
.qq_topAd.clearfix,
.article_ad,
.main3-ad,
.mar-b-10,
.ad-box,
#gdt_72058763092022479,
#taobaoADJS,
.article_ad,
.adLeft,
.adRight,
.adLeft700,
.adRight280,
#form_survey{
	display:none;
}

/* */
}
@-moz-document regexp("(http://).*(.qq.com/a/).+"){
body,
button,
input,
select,
textarea{
    background-color: #f3f2ee;
}
.qq_hotChoice,
#content{
	background-color: #f3f2ee;
}
.qq_hotChoice .bd li:hover,
.qq_essence li a:hover{
	background-color: #e8e6dd;
}
.a_author,
.a_author a,
.a_catalog,
.a_catalog a,
.a_source,
.a_source a,
.qq_editor{
	color:#999;
}
a:active,
a:hover,
.a_author a:hover,
.a_catalog a:hover,
.a_source a:hover,
.qq_essence li a:hover{
    color: #0061ad;
    text-decoration: none;
}
.Cnt-Main-Article-QQ .titdd-Article{
	background:#e8e6dd;
}
.qq_mainSide{
    transition: opacity 350ms ease-in-out 10000ms;
	-moz-opacity:0;
    opacity: 0;
}
.qq_mainSide:hover{
    transition: opacity 350ms ease-in-out 0ms;
	-moz-opacity:1;
    opacity: 1;
}
.qq_leftSide,
.side{
    transition: opacity 350ms ease-in-out 10000ms;
	-moz-opacity:0;
    opacity: 0;
}
.qq_leftSide:hover,
.side:hover{
    transition: opacity 350ms ease-in-out 0ms;
	-moz-opacity:1;
    opacity: 1;
}
.qq_article .hd h1,
.fontSize2 .title{
    color: #212121;
    font-family:Microsoft Yahei,Helvetica,sans-serif;
    font-size: 26px;
	text-shadow: 4px 4px 8px #E4E4E4;
    margin-bottom: 55px;
    margin-top:25px;
}
.qq_article .hd{
    margin-bottom: 25px;
}
.Cnt-Main-Article-QQ,
#Cnt-Main-Article-QQ p,
.fontSize2 .text{
    font-family:Microsoft Yahei,Helvetica,sans-serif;
	font-size:18px;
    line-height: 35px;
    color: #212121;
}
.qq_toolWrap .a_share .shareTitle, .qq_bar .a_share .qq_toolWrap .shareTitle, .qq_toolWrap .a_commentNum a, .qq_bar .a_commentNum .qq_toolWrap a, .qq_toolWrap .a_ilike>a, .qq_toolWrap .qq_bar .a_ilike>a, .qq_toolWrap .a_share .qq_bar .shareTitle, .qq_bar .a_share .shareTitle, .qq_toolWrap .a_commentNum .qq_bar a, .qq_bar .a_commentNum a, .qq_bar .qq_toolWrap .a_ilike>a, .qq_bar .a_ilike>a{
	color: #999;
}
.qq_share,
.r.all-number-comment{
	-moz-opacity:0.5;
    opacity: 0.5;
}
.top-autoTab-mod{
	background:none;
}
}
