css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("habitica.com")
{


h1, h2, h3, h4, h5, h6, body, .btn
  {
    font-family: 'Roboto', 'EmojiOne', sans serif !important;
  }

#app-header {
    background: #000000 !important;
    height: 155px !important;
}
div.class-badge {
    left: -10px !important;
    bottom: 120px !important;
}

::selection {
    background: #000; /* WebKit/Blink Browsers */
    color: #00adee;
}
::-moz-selection {
    background: #000; /* Gecko Browsers */
    color: #00adee;
}
body, .modal-content {
    background: #0f0f0f !important;
}
.tasks-list {
    background: #181818 !important;
}
.form-control, .group .items, .tier-list li[data-v-47c12a3e], #task-modal .task-modal-content input {
    background-color: #191919;
}
.nav-link:hover {
    background: #222222 !important;
}
.task-content, .btn-secondary, .dropdown > .btn-secondary, .dropdown-menu, textarea {
    background: #282828 !important;
}
.btn-show-more {
    background: #303030 !important;
}
.form-control:focus, #task-modal .task-modal-content input:focus {
    background-color: #323232;
}
.secondary-menu, .filter-panel {
    background: #3c3c3c  !important;
    border: 1px solid #000;
}
.task-habit-disabled, .standard-sidebar, .sidebar {
    background: #444444 !important;
}
    
/* blue colors */
.drawer-title, .popover {
    background: #272b35;
}
.drawer-content, code, .item.item-empty {
    background: #393f4c;
}
.slider-button, .card, .item-with-icon[data-v-c38c7d6e], .spell, .item, #task-modal .option-item-box, .category-select {
    background-color: #4c5666 !important;
}
  
/* border */    
button.btn.btn-secondary.filter-button, input, textarea, input.form-control, textarea.form-control,
button.btn.btn-secondary.filter-button[data-v-7ad1109e] {
    border: 1px solid #000 !important;
}
.standard-sidebar {
    border-left: 1px solid #000 !important;
}
    
/* text */
button.btn.btn-secondary.filter-button:hover, button.btn.btn-secondary.filter-button:active, button.btn.btn-secondary.filter-button:focus, button.btn.btn-secondary.filter-button.open,.item:hover,.task:hover,
button.btn.btn-secondary.filter-button[data-v-7ad1109e]:hover, button.btn.btn-secondary.filter-button[data-v-7ad1109e]:active, button.btn.btn-secondary.filter-button[data-v-7ad1109e]:focus, button.btn.btn-secondary.filter-button.open[data-v-7ad1109e]{
    border-color: #00adee !important;
    color: #00adee !important;
    box-shadow: 0px 0px 5px 0px !important;
}
input:active:not(:disabled), input:focus:not(:disabled), textarea:active:not(:disabled), textarea:focus:not(:disabled), input.form-control:active:not(:disabled), input.form-control:focus:not(:disabled), textarea.form-control:active:not(:disabled), textarea.form-control:focus:not(:disabled), .markdown > p, .close, .card-link[data-v-6804d287], .spell, #task-modal .task-modal-header h1, #task-modal .task-modal-content input:focus {
    color: #FFF !important;
}
.custom-control .custom-control-indicator {
    border: 2px solid #00adee;
}
.spell .mana, .filter-panel .tags-category {
    color: #00adee !important;
}
.filter.active, .drawer-tab-text-active {
    color: #00adee !important;
    border-bottom: 2px solid #00adee !important;
}
.filter {
    color: #444 !important;
}
body, .task-title, .task-notes, .checklist-item, h1, h2, h3, h4, h5, h6, .secondary-menu .nav-link, .dropdown-label, .btn-secondary, .dropdown > .btn-secondary, .text[data-v-09e46526], .dropdown-item {
    color: #9c9c9c !important;
}
.nav-link.active, code, .close:focus, .close:hover {
    color: #00adee !important;
    box-shadow: 0px 4px 0px #00adee !important; 
}
.filter:hover, .btn-secondary:hover:not(:disabled):not(.disabled), .btn-secondary:active, .btn-secondary.active, .btn-secondary:focus, .dropdown > .btn-secondary:hover:not(:disabled):not(.disabled), .dropdown > .btn-secondary:active, .dropdown > .btn-secondary.active, .dropdown > .btn-secondary:focus, .dropdown-item:hover, .dropdown-icon-item .svg-icon:hover, .dropdown-icon-item:hover .svg-icon, .nav-link:hover {
    color: #00adee !important;
    text-shadow: 0px 0px 5px #00adee !important;
}

/*
div.member-stats {
    margin-top: 5px !important;
    margin-bottom: 10px !important;
    left: -50px !important;
}
*/

div.view-party {
    right: 80px !important;
    bottom: 20px !important;
	width: 10px !important;
	height: 10px !important;
}

div.member-details {
    margin-top: 5px !important;
    margin-bottom: 10px !important;
}
div.no-party > div {
    display: none !important;
}
  
.task-control {
width: 10px !important;
height: 10px !important;
}

.check.svg-icon {
margin: auto !important;
}

.habit-control .positive {
margin-top: auto !important;
}

.habit-control .negative {
margin-top: 4px !important;
}

.left-control, .right-control {
width: 20px !important;
}

/* Monthis's Collapsed Task Notes Code */

.task-notes {
display: none;
}

.task:hover .task-notes {
display: block;
}

/* End of Monthis's Code */

.checklist {
display: none;
}

.task:hover .checklist {
display: block;
}

.reward-control .svg-icon {
width: 15px !important;
height: 15px !important;
}

.task-content {
padding: 4px !important;
}

h3 {
font-size: 14px !important;
}

.custom-control-description {
padding-top: 3 !important;
}

.checklist-item {
font-size: 12px !important;
margin-bottom: auto !important;
}

.checklist {
margin-bottom: auto !important;
margin-top: auto !important;
}

.task-title {
padding-bottom: 1 !important;
}

ul, menu, dir {
-webkit-padding-start: 15px !important;
-webkit-margin-before: 0px !important;
-webkit-margin-after: 0px !important;
}

.left-control {
min-height: 10 !important;
}

.daily-todo-control {
margin-top: 5px !important;
}

.form-control.input-search {
display: block !important;
}

.user-tasks-page {
padding-top: 5px !important;
}

  div.tasks-navigation
  {
    margin-top: 10px !important;
    margin-bottom: 10px !important;
     height: 40px !important;
  }

  div.tasks-navigation > .offset-3
  {
    margin-left: 0% !important;
  }

  div.tasks-navigation > .offset-4
  {
    margin-left: auto !important;
  }
/*
  .filter-panel
  {
    left: 0vw !important;
  }
*/
.offset-3 {
margin-left: -26% !important;
}

.btn.dropdown-toggle.btn-success {
padding: 8.5px 10px !important;
}

.offset-4 {
margin-left: auto !important;
}

.filter-panel {
left: 50% !important;
top: auto !important;
position: fixed !important;
}

.col-6 {
flex: auto !important;
max-width: 100% !important;
}

.col-1, .col-2, .col-3, .col-4, .col-5, .col-6, .col-7, .col-8, .col-9, .col-10, .col-11, .col-12, .col, .col-sm-1, .col-sm-2, .col-sm-3, .col-sm-4, .col-sm-5, .col-sm-6, .col-sm-7, .col-sm-8, .col-sm-9, .col-sm-10, .col-sm-11, .col-sm-12, .col-sm, .col-md-1, .col-md-2, .col-md-3, .col-md-4, .col-md-5, .col-md-6, .col-md-7, .col-md-8, .col-md-9, .col-md-10, .col-md-11, .col-md-12, .col-md, .col-lg-1, .col-lg-2, .col-lg-3, .col-lg-4, .col-lg-5, .col-lg-6, .col-lg-7, .col-lg-8, .col-lg-9, .col-lg-10, .col-lg-11, .col-lg-12, .col-lg, .col-xl-1, .col-xl-2, .col-xl-3, .col-xl-4, .col-xl-5, .col-xl-6, .col-xl-7, .col-xl-8, .col-xl-9, .col-xl-10, .col-xl-11, .col-xl-12, .col-xl {
width: auto !important;
}

.drawer-container:not(:hover)
{
  opacity: 0.5;
  height: 34px !important;
}

.drawer-toggle-icon
{
  display: none;
}

.card-body .text[data-v-88260846] {
 font-size:14px;
 color:#FFFFFF;
 text-align:left!important;
 min-height:0;
 margin-bottom:-.5rem
}

a.no-tier[data-v-e895429a] {
 color:#EFDFff;
}

.at-text {
 color:#DFBFff;
}

pre {
 display:block;
 font-size:87.5%;
 color:#7f7f7f;
}

/* Custom changes, courtesy Robert Kanzler */

.tags-popup {
background: #343434 !important;
}
.category-label {
background: #343434 !important;
width: 100%!important;
white-space: normal;
font-weight: 700;
font-size:14px;
}


.vdp-datepicker > .input-group > span,
.vdp-datepicker__calendar,
.vdp-datepicker__calendar header span,
.streak-addon {
background: #343434 !important;
}

.vdp-datepicker__calendar, .vdp-datepicker__calendar header span:hover{
background: #4c5666 !important;
}

.input-group-text {
font-weight: bold;
background: #232323 !important;
color: #919191 !important;
border: 1px solid #373F4B !important;
}

.advanced-settings, .grey-row {
background: #232323 !important;
}

.challenge, .challenge .challenge-prize {
background: #212529 !important;
}

#create-party-modal___BV_modal_body_ {
background: #2D2D2D !important;
}

.well-wrapper .well {
background: #2D2D2D !important;
}

.footer-row footer {
background: #111111 !important;
}

.footer-row footer .btn{
background: #2D2D2D !important;
}

.footer-row footer .social a{
background: #2D2D2D !important;
} 

}
