css:
@-moz-document url-prefix("https://pogoda.yandex.ru"), url-prefix("https://yandex.ru/pogoda") {
body, .b-page__body, .b-page__container, .content,
.b-page_align_center .b-page__container
{
    background: #222222;
  	color: cadetblue;
}

.b-page__container,
.b-page_align_center .b-page__container /* убирает выравнивание основной колонки по центру*/
{
	max-width: none;
}

.header2__main {
    background: #121212;
}

/* .header2__umbrella, .header2__logo, .service-name  {
    background: #222222;
} */

.header2 .search2 {
    padding-left: 18px;
}

.head-menu .link {
  	color: cadetblue;
}

.forecast-details__day-info .weather-table__head-cell, .forecast-fields__label {
    color: burlywood;    
}

.weather-table__body, .weather-table__daypart {
    color: #CCCCCC;
}

.i-ua_svg_yes .weather-table__body-cell_type_wind .icon {
    background-image: url(https://yastatic.net/weather/i/wind/arrow.svg);
	filter: hue-rotate(301deg) saturate(20);
}
.weather-table__body-cell_type_wind .icon {
    opacity: 1;
}

.forecast-details__weekday {
    color: #CCCCCC;
}

.forecast-details__day-number {
    color: cadetblue;
}

.weather-table__temp, .weather-table__body-cell_type_feels-like {
	font-size: 22px;
    color: cadetblue;
}

/* отступы в таблице дней */
.forecast-details__day-info .weather-table__row:first-of-type .weather-table__body-cell {
    padding-top: 30px;
}

.weather-table__body-cell {
    padding: 4px 0;
}
 

.forecast-details__day-info .weather-table__row:last-of-type .weather-table__body-cell {
    padding-bottom: 15px;
}




}
