css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.nicovideo.jp/watch/){

/*   少し動画のバーを太くする */
  .SeekBar{
    height: 6px !important;
  }
  .SeekBar > .XSlider >.ProgressBar{
    height: 6px !important;
  }
/*   バーの玉をnyancatに */
  .SeekBarHandle-handle{
   background: url("data:image/gif;base64,") !important;
    visibility: visible;
   width: 34px !important;
   height: 21px !important;
   border: none !important;
   margin-left: -18px !important;
   margin-top: 0px !important;
   transform: scale(0.8);
-webkit-transform: scale(0.8);
-moz-transform: scale(0.8);
-ms-transform: scale(0.8);
  }
  .ProgressBar-inner.SeekBar-hover{
    background-color: rgba(255,255,255,0.6) !important;
/*     buffedの上にポインタ持っていったときに白をのせる */
    z-index: 1 !important;
    display:inline;
  }
/*   再生済み */
  .ProgressBar-inner.SeekBar-played{
       background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAGCAIAAACNcmNmAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAbSURBVBhXY/jPwMDwfyYQAxnGIM7M/wxpxv8BaOII+E6X0GMAAAAASUVORK5CYII=") repeat-x !important; 
/*     buffedの上にポインタ持っていったときに白をのせないように */
    z-index: 1 !important;
  }
/*   バッファー */
  .ProgressBar-inner.SeekBar-buffered{
   background:  url("data:image/gif;base64,") !important;
  }
/*   ボリューム  */
  .ProgressBar-inner.VolumeBar-progress{
   background:  url("data:image/gif;base64,")  repeat-x !important; 
  }
}
