css:
@-moz-document domain("hooktube.com") {
/* HookTube Blacked +
 * Description: an expannsion of the HookTube Blacked Theme
 * By: Desu
 * Version: 0.6
 */

body {
    background-color: #151515;
    color: rgba(255,255,255,.7);
}
a, a:hover {
	color: rgb(120,180,231);   
}
h4 {
	font-size: 1em;
    padding: 0;
}
.alert {
	font-size: .75em;
}
#search-form input.form-control {
	padding: .2em .3em;   
}
#search-form button.btn {
    padding: .2em .3em;  
}
.alert-warning {
	background-color: rgba(180,132,132,.1);
    border-color: rgba(255,238,186,.1);
    color: rgba(255,238,186,.8);
}
.alert-error {
    background-color: rgba(245,57,57,.1);
    border-color: rgba(245,57,57,.3);
    color: rgba(255,179,179,.6);
}
button {
	background: #555;
    border: 1px solid #111!important;
    font-size: .9em!important;
    box-shadow: 1px 1px 1px #111;
    color: #dadada;
    text-shadow: 2px 2px 2px #111;
}
.btn-secondary {
	background-color: rgba(0,0,0,.8);
    border: none!important;
}
.btn-secondary:hover, .show>.btn-secondary.dropdown-toggle {
    background-color: rgba(0,0,0,1);
    box-shadow: none;
}
h1.card-title {
	font-size: 1.3em;   
}
.list-channel-title {
    color: #dadada;
}

/*homepage intro*/
.jumbotron {
	background-color: rgba(0,0,0,.5)!important;
    padding: .5em;
    border-radius: .3em;
}
	.jumbotron p {
        font-size: .9em!important;
    }

/*homepage categories*/
#category-list {
	text-align: left!important;
    line-height: 1em;
}

/*video page*/
p#video-channel-name {
	font-size: .75em;   
}
#video-description .card-body {
    background-color: #111;
	border: 1px solid #222;
    font-size: .75em;
    padding: .5em;
}
#video-description .card {
 	border: none;   
}
#video-keywords .badge {
    background-color: rgba(0,0,0,.5);
}
#comments .row {
    border-bottom: 1px solid #222;
    padding: .5em 0;
}

/*catalog list*/
#video-list img {
	border: 1px solid #000;   
}

/*popups*/
.modal-content {
	background-color: rgba(39,39,39,1);   
    border-color: rgba(0,0,0,1);
}
.modal-content .modal-header {  
	border-bottom-color: rgba(0,0,0,.3);
}

/*search bar*/
input[type="text"] {
    background: #444!important;
    border: 1px solid #333!important;
    color: #e0e0e0!important;
    font-size: .8em;
}
input[type="text"]::placeholder {
    color: #e0e0e0!important;
}
#search-form button.btn {
	background: #555;
    font-size: .8em;
}

/*footer*/
#main-footer {
	border-top: 0;
    background: #111;
    border: 1px solid #222;
    margin-bottom: 1em;
    padding: .5em;
}
#main-footer p {
	margin-bottom: .25em;   
}

/*navbar*/
nav.navbar {
	background-color: rgba(0,0,0,.8);
    border: none;
}
nav.navbar a.navbar-brand img {
	width: 75%;   
}
nav.navbar ul li a.nav-link {
	color: rgba(255,255,255,.5)!important;
    font-size: .8em;
    padding-top: .85em;
}
nav.navbar .navbar-brand img {/*remove original logo image*/
    display: none;
}
nav.navbar a.navbar-brand {
    width: 130px;
    height: 32px;
    margin: 0;
    padding: 0;
    /*begin base64 logo below*/
	background: url('data:image/png;base64,') center left no-repeat;    
}
}
