@-moz-document url-prefix("https://gitter.im/ExtensionDev/stylus/"), domain("add0n.com"), regexp("https?://disqus\\.com/embed/comments/.*add0n.com.*") {
@font-face {
    font-family: 'FontAwesome';
    src: url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.eot?v=4.7.0');
