    font-family: FontAwesome!important;
    font-size: 16px!important;
    margin: 0 4px 0 2px!important;
}

.goog-te-gadget-simple {
    padding-top: 2px!important;
}

.idc-v-up,
.idc-v-down {
    font-family: FontAwesome!important;
    background-image: none!important;
    background-color: transparent!important;
    border-style: none!important;
    font-size: 18px!important;
    height: 18px!important;
    width: 18px!important;
    line-height: 18px!important;
    text-align: center!important;
}

.idc-v-down:hover,
.idc-disabled .idc-v-down {
    color: hsl(0, 100%, 66%)!important;
}

.firefox32,
.opera32,
.chrome32,
.safari32,
.edge32 {
    font-family: FontAwesome!important;
    background-image: none!important;
    background-color: transparent!important;
    border-style: none!important;
    font-size: 28px!important;
    height: 28px!important;
    width: 28px!important;
    line-height: 28px!important;
    text-align: center!important;
}

.android24,
.chrome24,
.edge24,
.firefox24,
.opera24,
.safari24 {
    background-image: url(https://i.imgur.com/QFGjAm2.png)!important;
}

.download > a {
    background-color: transparent!important;
    opacity: 1!important;
    transition: opacity .3s!important
}
.download > a:hover {
    opacity: .8!important;
}
.gitter-chat-embed {
    z-index: 2147483647!important;
    transition: none!important;
}

.gitter-open-chat-button {
    right: 4px!important;
    padding: 12px 2px 6px 12px!important;
    font-size: 0!important;
    background: hsl(180, 20%, 20%)!important;
    border-top-left-radius: 8px!important;
    border-top-right-radius: 8px!important;
    text-transform: none!important;
}

#conversation .btn,
.notice .btn {
    border: 0!important;
    background: hsl(180, 20%, 20%)!important;
    box-shadow: inset 0 1px 0 hsla(180, 28%, 40%, .5), 0 1px 2px hsla(180, 28%, 40%, .4)!important;
}

#conversation .btn:hover,
.notice .btn:hover {
    background: hsl(180, 20%, 26%)!important;
}

#backtotop a {
    filter: hue-rotate(130deg);
}

.gitter-open-chat-button:hover {
    background: hsl(180, 20%, 26%)!important;
}

.gitter-open-chat-button:before {
    content: "Gitter"!important;
    font-size: 14px!important;
}

.rec-box,
.rec-component {
    background: hsl(180, 9%, 14%)!important;
    transition: background .3s;
    position: relative!important;
    cursor: pointer!important;
}

.rec-box:hover,
.rec-component:hover {
    background: hsl(180, 9%, 20%)!important;
}

.rec-component a,
.rec-button {
    background: none!important;
    font-size: 0!important;
}

.rec-component a:before {
    content: ""!important;
    position: absolute;
    left: 0;
    top: 0;
    height: 100%;
    width: 100%;
    cursor: pointer!important;
}

#header {
    top: 7px!important;
    width: 800px!important;
    left: 0;
    right: 0;
    margin: auto;
    background: none!important;
    box-shadow: none!important;
    position: fixed;
    z-index: 2147483646!important;
}

.gitter-chat-embed-action-bar-item-pop-out,
.gitter-chat-embed-action-bar-item-collapse-chat,
input[type="checkbox"],
.gcsc-branding-clickable img {
    -webkit-filter: invert(1)grayscale(1)!important;
    filter: invert(1)grayscale(1)!important;
}

#top div[type=about] {
    flex: unset!important;
    max-width: calc(((100% - 800px) * .5) - 100px)!important
}

#top div[type=about] span {
    font-size: 14px!important;
    font-weight: 600!important
}

#top div[type=about] span[type="description"],
#top div[type=links] span,
#header td[type="icon"],
#header td[type="name"] {
    display: none!important;
}

#header td[type=search] input {
    background: hsl(180, 8%, 11%)!important;
    border-color: hsl(180, 28%, 22%)!important;
    opacity: 1!important;
    text-indent: 6px;
    width: 250px!important;
    transition: background .3s, border-color .3s;
}

#header td[type=search] input:focus {
    background: hsl(180, 8%, 14%)!important;
    border-color: hsl(180, 28%, 28%)!important;
}

#find input,
.guest input,
.textarea,
body #idc-container input[type=text],
body #idc-container input[type=password],
body #idc-container .idc-c-t-inner textarea {
    background: hsl(180, 8%, 14%)!important;
    border-color: hsl(180, 28%, 24%)!important;
    border-width: 1px!important;
    border-style: solid!important;
    opacity: 1!important;
    text-indent: 6px;
    transition: background .3s, border-color .3s;
}

#find input:focus,
.guest input:focus,
.textarea:focus,
body #idc-container.idc textarea.idc-focus,
body #idc-container.idc input.idc-focus {
    background: hsl(180, 8%, 13%)!important;
    border-width: 1px!important;
    border-color: hsl(180, 28%, 28%)!important;
}

#reviews [type=button][title] {
    background: hsl(180, 8%, 17%)!important;
    border-color: hsl(180, 28%, 34%)!important;
    border-width: 1px!important;
    border-style: solid!important;
    opacity: 1!important;
    text-indent: 6px;
    transition: background .3s, border-color .3s;
}

#reviews [type=button][title]:hover,
#reviews [type=button][title][data-selected=true] {
    background: hsl(180, 8%, 20%)!important;
    border-width: 1px!important;
    border-color: hsl(180, 28%, 40%)!important;
}

.textarea-wrapper,
.post-actions {
    background: none!important;
    border-style: none!important;
}

#header td[type=search] {
    width: 250px!important;
}

.tab-conversation.active > a {
    pointer-events: none!important;
}

.github-bugs > tbody > tr:nth-child(even) {
    background: hsla(180, 8%, 14%, .5)!important;
}

.faq > li:before {
    background: hsl(180, 8%, 16%)!important;
    border-color: hsl(180, 28%, 36%)!important;
    color: hsl(0, 0%, 86%)!important;
    border-radius: 50%!important;
}

#footer td[type=google-search] {
    width: 238px!important;
}

.dropdown-menu {
    border-width: 1px!important;
}

.post-action__button {
    margin: -1px -2px 0 0!important;
}

select,
.goog-te-gadget-simple,
.goog-te-menu2,
#reviews ul[type=popup],
.trpContentPanel,
.tooltip,
.dropdown-menu,
.gitter-chat-embed,
