    border-color: hsl(180, 28%, 24%)!important;
}

.caret {
    border-color: hsl(180, 100%, 50%) transparent transparent!important;
    opacity: 1!important;
    transition: border-color .3s;
}

a:hover .caret,
.open a .caret {
    border-color: hsl(190, 100%, 90%) transparent transparent!important;
    opacity: 1!important;
}

h2 span {
