}

.github-bugs {
    border: 1px solid hsl(180, 28%, 24%)!important;
}

.github-bugs > tbody > tr {
    border-bottom: 1px solid hsl(180, 28%, 24%)!important;
}

a,
a *,
[onclick],
[onclick] *,
body .dropdown-toggle,
body .post-content .post-menu .dropdown-menu li a,
body .github-bugs td[type="more"] a,
#conversation .btn,
.gsc-option-menu-item,
.gsc-option-menu-item *,
