#translate,
#translate ul > li {
    color: hsl(180, 100%, 50%)!important;
    transition: border-color .3s, background-color .3s, background .3s, color .3s !important;
}

#conversation .btn * {
    color: hsl(180, 100%, 50%)!important;
    transition: color .3s !important;
}

#conversation .btn:hover * {
    color: hsl(190, 100%, 90%)!important;
}

a:hover,
a:hover *,
[onclick]:hover,
[onclick]:hover *,
#idc-container .idc-share-tabs .idc-sel a span,
