css:
@-moz-document domain("localhost") {
.CodeMirror.cm-s-graphiql {
    background: #282828;
}
.graphiql-container .doc-explorer-contents,
.graphiql-container .history-contents {
    background-color: #282828;
    border-top: 1px solid #d6d6d6;
    color: #e0e0e0;
}
.graphiql-container .history-contents p {
    font-size: 12px;
    border-bottom: 1px solid rgba(255, 255, 255, 0.14);
}
.graphiql-container .docExplorerResizer {
    background: rgba(255, 255, 255, 0.06);
}
.graphiql-container .search-box:before {
    content: none;
}
.cm-property {
    color: #a3e175;
}
.cm-attribute {
    color: #c678dd;
}
.cm-keyword {
    color: #ff9d00;
}
.cm-atom {
    color: #ffe407;
}
.cm-punctuation {
    color: #b3b3b3;
}
.cm-variable {
    color: #30b7c7;
}
.cm-string {
    color: #b4b4b4;
}
.cm-def {
    color: #c54747;
}
.cm-ws {
    color: #b3b3b3
}
.graphiql-container .field-name {
    color: #488aca;
}
.graphiql-container .arg-name {
    color: #984ebb;
}
.graphiql-container .doc-category-item {
    margin: 12px 0;
    color: #d6d6d6;
}
.graphiql-container .variable-editor-title {
    background: #383838;
    border-bottom: 1px solid #000000;
    border-top: 1px solid #000000;
}
.CodeMirror-matchingbracket {
    color: #ff00f3 !important;
    text-decoration: none !important;
    font-style: italic;
}
.CodeMirror-gutters {
    background-color: #333333;
    border-color: #000000;
}
.CodeMirror-search-label {
    color: #ffffff;
}
.CodeMirror-search-field {
    color: #ff9d00 !important;
}
.graphiql-container .result-window .CodeMirror-gutters {
    background-color: #333333;
    border-color: #000000;
}
.graphiql-container .resultWrap {
    border-left: solid 1px #000000;
}
.CodeMirror-cursor {
    border-left: 2px solid #ffef00 !important;
}
/* Hide Scroll bar */

.CodeMirror-vscrollbar {
    display: none !important;
}
.CodeMirror-hscrollbar {
    display: none !important;
}
.CodeMirror-scrollbar-filler {
    display: none !important;
}
/* Selection Highlight */

.CodeMirror-selected {
    background: rgba(177, 177, 177, 0.3);
}
.CodeMirror-focused .CodeMirror-selected {
    background: rgba(177, 177, 177, 0.2);
}
}
