css:
@-moz-document domain("tl.rulate.ru") {
/*AGENT_SHEET*/
*
{
    margin: 0;
    padding: 0;
    border: 0;
    outline: 0;
    font-size: 100%;
    vertical-align: baseline;
    background: transparent;
    -webkit-box-sizing: border-box;
    box-sizing: border-box
}

div#readpage *
{
    font-family: inherit !important;
    font-size: inherit !important;
    line-height: inherit !important;
}

html,
body,
html > body > footer
{
    background-color: #EBE5D7!important;
    border: 0 solid transparent!important
}

footer,
header,
nav
{
    font-family: menu!important
}

[class="span4 sr"] *,
[class="pagination"],
[class="span4"]
{
    font-family: menu!important
}

#up,
[class="visible-desktop"]
{
    display: none!important;
    width: 125px!important;
    height: 125px!important;
    position: fixed!important;
    left: 20px!important;
    top: 180px!important;
    margin: 0!important;
    padding: 0!important
}

input *,
[class="btn btn-primary"] *,
[class="btn btn-default"] *,
[class="btn btn-info"] *,
[class="btn btn-success"] *,
[class="btn btn-warning"] *,
[class="btn btn-info dis"] *,
[class="btn btn-small btn-info"] *
{
    font-family: caption!important
}

form *
{
    font-family: menu!important
}

#readpage *
{
    /*AGENT_SHEET*/
    /* font-family: 'PT Serif', 'Liberation Serif', 'Bookman Old Style', 'Times New Roman', 'Lora', Georgia, 'Palatino Linotype', 'Book Antiqua', Palatino, Times, serif!important;
    */
    font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
    font-weight: 400 !important;
    text-align: justify !important;
    word-wrap: break-word!important;
    line-height: 1.25 !important;
    text-indent: 20px!important;
}

html > body > *
{
    background-color: #EBE5D7!important
}

div.container[style="background-color:#fff;"]
{
    background-color: #EBE5D7!important
}

[class="C_paginationSelector input-mini"]
{
    width: auto!important
}

[class="modal-body"] > ul > li > div
{
    border: solid 1px #000!important;
    margin: 5px!important;
    padding: 5px!important
}

#bookmarks-list > * > *
{
    font-family: 'PT Mono', 'Liberation Mono', Menlo, Monaco, Consolas, 'Courier New', Courier, monospace!important;
    font-size: 14px!important;
    line-height: 2!important;
    text-align: left!important;
    -webkit-hyphens: none!important;
    -moz-hyphens: none!important;
    -ms-hyphens: none!important
}

#Notices *
{
    font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
    line-height: 1.11!important
}

li.new
{
    -webkit-box-shadow: 0 5px 20px 0 rgba(0, 0, 0, 0.25)!important;
    box-shadow: 0 5px 20px 0 rgba(0, 0, 0, 0.25)!important
}

[style="text-align: center; margin-bottom: 20px;"],
[style="text-align: center; margin-bottom: 20px;"] > a,
[style="text-align: center;"],
[style="text-align: center;"] > a
{
    font-family: menu!important;
    color: green!important
}

[style="text-align: center; margin-bottom: 20px;"] > select
{
    font-family: menu!important;
    height: 100%!important;
    min-width: 300px!important
}

#Info *
{
    font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
    line-height: 1.22!important;
    text-align: justify!important;
    -webkit-hyphens: auto!important;
    -moz-hyphens: auto!important;
    -ms-hyphens: auto!important
}

tr > td,
tr > td > a
{
    font-family: 'PT Mono', 'Liberation Mono', Menlo, Monaco, Consolas, 'Courier New', Courier, monospace!important;
    line-height: .8!important;
    text-align: justify!important;
    -webkit-hyphens: auto!important;
    -moz-hyphens: auto!important;
    -ms-hyphens: auto!important
}

[value="»"]
{
    font-size: 25px!important;
    font-family: caption!important
}

[class="comments"] *
{
    font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
}

[class="text"],
[class="meta"]
{
    border-left: solid 0 #666!important;
    border-top: dotted 0 #666!important;
    margin: 0!important;
    padding: 0!important;
    border-radius: 0!important
}

[class="thread"]
{
    border-left: solid 1px green;
    border-radius: 10px!important;
    padding: 0!important;
    margin: 0!important;
    padding-left: 10px!important
}

h1,
h2
{
    font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
    font-size: 24px!important;
    text-shadow: 1px 2px 2px #555, 0 0 5px #aaa!important;
    line-height: 2!important;
    text-align: center!important
}

h3,
h4,
h5,
h6
{
    font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
    text-shadow: 1px 2px 2px #555, 0 0 5px #aaa!important
}

hr
{
    margin: 20px 0!important;
    height: 1px!important;
    border: none!important;
    background: -webkit-gradient(linear, left top, right top, from(transparent), color-stop(rgba(0, 0, 0, 0.87)), to(transparent))!important;
    background: -webkit-linear-gradient(left, transparent, rgba(0, 0, 0, 0.87), transparent)!important;
    background: -o-linear-gradient(left, transparent, rgba(0, 0, 0, 0.87), transparent)!important;
    background: linear-gradient(to right, transparent, rgba(0, 0, 0, 0.87), transparent)!important
}
}
