css:
@-moz-document domain("rerererarara.net") {
body { 
    background-color: #bdbdbd;
}
html.no-touch #sol-frame-ic { 
    background-color: #bdbdbd;
}
entry-form { 
    background-color: #9e9e9e;
    border: 1px solid darkred;
}
#entry-textarea, #entry-duzenle-textarea{
    background-color: lightgray;
}
a {
    color: #640001; 
}

#entry-form {
    background-color: #9e9e9e;
}

#baslik-alt-menu ul li {
    text-shadow: 0 0px 0 transparent;
}

sol-frame ul li {
     border-bottom: 0px solid transparent; 
}

#sol-frame ul li a:hover, #sol-frame-yenile:hover {
    background-color: #9e9e9e;
}
#baslik {
    text-shadow: 0 0px 0 transparent;
}
.vurgu {
    background-color: transparent;
}

#sol-frame ul li a .sayi {
    background-color: transparent;
    box-shadow: 0 0 1px #a80002;
}
#sol-frame ul li {
    border-bottom: 0px solid transparent;
}
#sol-frame ul li a {
    color: #141414!important;
}

.sayfalama-deaktif {
    color: #7a7a7a!important;
}

.sayfalama-onceki {
    background-color: transparent!important;
}

.sayfalama-onceki:hover, .sayfalama-sonraki:hover, .sayfalama-toplamsayfa:hover {
    background-color: #aaaaaa;
}
.sayfalama-toplamsayfa {
    background-color: transparent;
}

.sayfalama-sonraki {
    background-color: transparent;
}
input[type="text"], input[type="email"], input[type="search"], input[type="url"], input[type="datetime"], input[type="date"], input[type="time"], input[type="password"], input[type="number"], textarea, button, input[type="submit"], input[type="button"], select {
    background-color: transparent;
}

.mesajlar-tablo tr.vurgu {
    background-color: #b2b2b2;
}
.mesajlar-tablo tr:hover {
    background-color: #9e9e9e;
}

.buton {
    border: 1px solid;
    border-color: darkgray;
    border-radius: 3px;
    padding: 2px 5px;
    color: #000;
    text-shadow: 0 0 0 transparent;
    background-image: none!important;
    background-color: transparent;
}

.buton:hover {
    background-image: none;
    background-color: #aaaaaa;
}

button, input[type="submit"], input[type="button"] {
    border-color: lightgray;
}

.mesajlar-oku-tablo tr.vurgu {
    background-color: #b2b2b2;
}

.mesajlar-oku-tablo tr:hover {
    background-color: #a0a0a0;
}

.buton:hover {
    background-color: #aaaaaa!important;
}

#entry-textarea, #entry-duzenle-textarea {
    background-color: #bdbdbd;
}

.entry-bkz-buton {
    background: transparent;
}

.mesajlar-oku-tablo tr.baslik td {
    background-color: #aaaaaa;
}

html>body>header {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    transition: top .25s ease-in-out,box-shadow .25s linear;
    margin: 0 auto;
    min-width: 240px;
    box-shadow: 0 0 3px #aaa;
    background: #444;
    /* -webkit-font-smoothing: subpixel-antialiased; */
    /*background: -moz-linear-gradient(top,#242424 0,#000 100%);
    background: -webkit-gradient(left top,left bottom,color-stop(0%,#242424),color-stop(100%,#000));*/
    /* background: -webkit-linear-gradient(top,#242424 0,#000 100%); */
    /*background: -o-linear-gradient(top,#242424 0,#000 100%);
    background: -ms-linear-gradient(top,#242424 0,#000 100%);*/
    /* background: linear-gradient(to bottom,#242424 0,#000 100%); */
    /*filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#242424',endColorstr='#000000',GradientType=0);*/
    z-index: 10000;
}

html>body>header #ust-menu-cerceve {
    background-color: transparent;
    height: 26px;
    border-bottom: 2px solid #ffb300;
    box-shadow: inset 0 0 0 #d76565!important;
    z-index: 99999;
}

#ust-getir-btn {
    float: left;
    padding: 4px 8px;
    height: 26px;
    border: 1px solid;
    border-color: #c9a239 #ca8a21;
    border-left: 1px solid #ffcc1a!important;
    font-size: 12px;
    color: white;
    text-shadow: none!important;
    border-radius: 0;
    /*background-image: -moz-linear-gradient(top,#ffcc1a,#ffb300);
    background-image: -ms-linear-gradient(top,#ffcc1a,#ffb300);*/
    /* background-image: -webkit-gradient(linear,0 0,0 100%,from(#ffcc1a),to(#ffb300)); */
    /* background-image: -webkit-linear-gradient(top,#ffcc1a,#ffb300); */
   /* background-image: -o-linear-gradient(top,#ffcc1a,#ffb300);
    background-image: linear-gradient(top,#ffcc1a,#ffb300);*/
    /* background-repeat: repeat-x; */
    background-color: transparent!important;
    background-image: none!important;
    cursor: pointer;
}

#ust-menu-sol>li {
    text-shadow: 0 0 0 transparent!important;
    border-right: 1px solid #ffb300!important;
}

#ust-menu-sag>li {
    text-shadow: 0 0 0 transparent!important;
    border-left: 1px solid #ffb300!important;
}    

#ust-ara-btn {
    color: white;
    /* text-shadow: 0 1px 0 #ffcc1a; */
    border-bottom-right-radius: 3px;
    border-top-right-radius: 3px;
    /*background-image: -moz-linear-gradient(top,#ffcc1a,#ffb300);
    background-image: -ms-linear-gradient(top,#ffcc1a,#ffb300);*/
    /* background-image: -webkit-gradient(linear,0 0,0 100%,from(#ffcc1a),to(#ffb300)); */
    /* background-image: -webkit-linear-gradient(top,#ffcc1a,#ffb300); */
    /*background-image: -o-linear-gradient(top,#ffcc1a,#ffb300);
    background-image: linear-gradient(top,#ffcc1a,#ffb300);*/
    /* background-repeat: repeat-x; */
    background-image: none!important;
    background-color: transparent!important;
}

@media handheld, only screen and (max-width: 850px) {
    #ust-menu-sol>li>a:hover {
    background: #ffb300;
 }}


#ust-ara-btn:after {
    border-top: 4px solid white!important;
}

#ust-ara-input {
    color:white!important;
}
}
