css:
@-moz-document url("http://www.lci.fr/jeux/solitaire/"), url-prefix("http://jeux.metronews-compute-plateform.appspot.com/solitaire") {
html {
    overflow: hidden!important;
}
.grid {
    margin-top: 0px;
    margin-left: 0px;
    margin-right: 0px;
}
.grid.filled-block {
    background: -webkit-linear-gradient(right, #010938, #021650 27%, #021344 50%, #540533 73%, #6d0418);
    position: fixed!important;
    top: 0!important;
    padding-top: 90px!important;
    left: 0!important;
    width: 100%!important;
    height: 100%!important;
    z-index: 1!important;
}
.grid.filled-block > iframe {
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23)!important;
    width: 855px!important;
    height: 638px!important;
    margin-left: calc(50% - 427.5px)!important;
}
body#jqt.supports3d.portrait {
    background: url(http://image.noelshack.com/fichiers/2016/25/1466617405-bkgd-90.png) no-repeat!important;
    background-position: 0 0!important;
    background-size: 855px 638px!important;
}
div#main.current {
    background: none!important;
}
.card {
    -webkit-box-shadow: none!important;
    -moz-box-shadow: none!important;
    box-shadow: none!important;
}
.card .back {
    background: url(http://image.noelshack.com/fichiers/2016/25/1466618186-card-back-moltoncore.gif) no-repeat!important;
    background-position: -11px -21px!important;
    background-size: 97px 142px!important;
}
.unecarte {
    background: url(http://image.noelshack.com/fichiers/2016/25/1466620682-1carte.png)!important;
    background-position: 0 0!important;
}
.unecarte:hover {
    background-position: 0 -81px!important;
}
.troiscarte {
    background: url(http://image.noelshack.com/fichiers/2016/25/1466621144-3carte.png)!important;
    background-position: 0 0!important;
}
.troiscarte:hover {
    background-position: 0 -60px!important;
}
.card .front {
    background-image: url(http://image.noelshack.com/fichiers/2016/25/1466623125-cardfront-medium.png)!important;
}
#endgamepicture {
    background: url(http://image.noelshack.com/fichiers/2016/25/1466626845-findepartie.png)!important;
}
}
