css:
@-moz-document domain("rerererarara.net") {
body {
    /*font: 10pt Georgia,sans-serif;*/
    color: #ffd571;
    background-color: #851114;
}

input[type="text"], input[type="email"], input[type="search"], input[type="url"], input[type="datetime"], input[type="date"], input[type="time"], input[type="password"], input[type="number"], textarea, button, input[type="submit"], input[type="button"], select{
    border: 0px solid #ccc;
    color: #ffd571;
    background-color: #9d4042;
}

.buton {
    border: 1px solid;
    border-color: #c9a239 #ca8a21;
    border-radius: 0px;
    padding: 2px 5px;
    color: #ffd571;
    text-shadow: 0 0 0 #ffcc1a;
    background-image: -webkit-gradient(linear,0 0,0 100%,from(#851114),to(#851114));
    background-image: -webkit-linear-gradient(top,#851114,#851114);
    /* background-repeat: repeat-x; */
    /* background-color: #ffc600; */
    background-image: -moz-linear-gradient(top,#851114,#851114);
    background-image: -ms-linear-gradient(top,#851114,#851114);
    background-image: -o-linear-gradient(top,#851114,#851114);
    background-image: linear-gradient(top,#851114,#851114);
}

a {
    text-decoration: none;
    color: #ffba1a;
}

.entry-alt-tarih {
    white-space: nowrap;
    font-size: 10px;
    color: #ffd571;
}

.entry-alt-tarih a {
    color: #ffd571;
}

#baslik {
    text-shadow: 0 0 0 transparent;
}

.entry-alt-paylas, .entry-alt-islem {
    color: #ffba1a;
}

.entry-alt-id {
    font-size: 10px;
    color: #ffba1a;
}

#entry-form {
    background-color: #91282b;
	border: 0px solid #eee;
}

.no-touch .entry-alt-on:hover, .no-touch .entry-alt-of:hover {
    opacity: 1;
    border-radius: 3px;
    background-color: #770f12;
}

.entry-alt-on-aktif, .entry-alt-of-aktif {
    opacity: 1;
    border-radius: 3px;
    text-shadow: 0 1px 0 #660003;
    background: #770f12;
    background: -moz-linear-gradient(top,#770f12 0,#6a0d10 100%)!important;
    background: -webkit-gradient(left top,left bottom,color-stop(0%,#770f12),color-stop(100%,#6a0d10))!important;
    background: -webkit-linear-gradient(top,#770f12 0,#6a0d10 100%)!important;
    background: -o-linear-gradient(top,#770f12 0,#6a0d10 100%)!important;
    background: -ms-linear-gradient(top,#770f12 0,#6a0d10 100%)!important;
    /* background: linear-gradient(to bottom,rgba(248,80,50,1) 0,rgba(168,0,3,1) 100%)!important; */
    color: #fff!important;
}

.entry-alt-on, .entry-alt-of {
    color: #ffd571!important;
    background-color: transparent;
}

a.entry-alt-fav-aktif {
    color: #ffba1a!important;
}

.entry-bkz-buton {
    border: 0px solid #ffd571!important;
    background: transparent;
}
.entry-bkz-buton:hover {
    background: #8d393b;
}

#entry-listesi .entry-no {
    border-bottom: 0px solid #eee;
    font-size: 11px;
    color: #ffd571;
}

#baslik-alt-menu ul li {
    text-shadow: 0 0px 0 #f5f5f5;
}

#baslik-alt-menu ul li a {
    color: #ffd571;
}

.dropdown-open {
    color: #e5bf65!important;
}

.dropdown .dropdown-menu, .dropdown .dropdown-panel{
	background: #9d4042;
    border: solid 1px #8d393b;
    /* easter egg yok buralarda :( */
}

.dropdown .dropdown-menu LI>A, .dropdown .dropdown-menu LABEL {
    color: #ffba1a;
}

.dropdown .dropdown-menu LI {
    border-bottom: 0px solid #f5f5f5;
}
.buton:hover {
    background-color: #770f12;
}

#sol-frame {
    background-color: #cc8e14;
    border: 0px solid #d2d2d2;
}

#sol-frame>#sol-frame-ic>#sol-frame-baslik{
    text-shadow: 0 0 0 transparent;
    color: #851114;
}

#sol-frame-yenile {
    border: 0px solid #eee;
}
#sol-frame-yenile a{
    color: #851114;
}
#sol-frame ul li {
    border-bottom: 0px solid #eee;
}

#sol-frame ul li a {
    padding: 4px 10px 4px 3px;
    color: #851114;
	font-weight: bold;
}

#sol-frame ul li a .sayi {
    display: inline;
    color: black;
    font-size: 10px;
    padding: 1px 2px;
    margin-left: 0px;
    border-radius: 3px;
    background-color: transparent;
    box-shadow: 0 0 1px rgba(133, 17, 20, 0.3);
}

#sol-frame>#sol-frame-ic>#sol-frame-baslik-dun{
    text-shadow: 0 0px 0 transparent;
    color: #851114;
}

#sol-frame ul li a:hover, #sol-frame-yenile:hover {
    background-color: #b77f12;
}

.vurgu {
    background-color: #b77f12;
}

.mesajlar-tablo tr.vurgu {
    background-color: #770f12;
}

.mesajlar-tablo tr:hover {
    background-color: #6a0d10;
    transition: background-color .2s;
}

.sayfalama-sonraki{
	background-color: transparent;
    border: 0px solid #e5a717!important
}

.kontrol-merkezi-tablo .alt-baslik{
	 color: #ffd571;
}

html>body>header #ust-menu-cerceve {
    background-color: #851114;
    border-bottom: 6px solid #CC8E14;
    box-shadow: inset 0 1px 5px #91282b;
}

#sol-frame ul li a div.alt-yazar {
	color:lightgray;
}

.kimvar-konu-baslik a{
    color: #ffd571!important;
}

.kimvar-cerceve {
    text-shadow: 0 0 0 transparent!important;
}

.kimvar-cerceve .sayi{
    background-color: transparent!important;
    border: 0px solid #f1f1f1!important;
}
fieldset.fieldset-sol-frame {
    color: #851114;
}

.kimvar-ileti {
    color: #ffd571!important;
}

.kimvar-cerceve ul li {
    text-shadow: 0px 0px 0 transparent!important;
    border-bottom: 0px solid #f5f5f5!important;
}

.mesajlar-oku-tablo .tarih{
    color: #ffd571;
}

.mesajlar-oku-tablo tr.vurgu {
    background-color: #6a0d10;
}

.mesajlar-oku-tablo tr td {
    border-bottom: 0px solid transparent!important;
}

.mesajlar-oku-tablo tr.baslik td {
    background-color: transparent;
    border-bottom: 1px solid #ddd;
}

.mesajlar-oku-tablo tr:hover {
    background-color: #5d0b0e;
}

.mesajlar-oku-tablo tr.vurgu:hover {
    background-color: #5d0b0e;
}

.sayfalama-onceki, .sayfalama-toplamsayfa{
    background-color: transparent;
    border: 0px solid #e5a717!important;
}

.sayfalama-input{
color: #ffba1a !important;
border: 0px solid #e5a717!important;
}

.sayfalama-onceki:hover, .sayfalama-sonraki:hover, .sayfalama-toplamsayfa:hover {
    background-color: #6a0d10 !important;
}

.kimvar-cerceve ul li:hover {
    background-color: #6a0d10;
}

html.no-touch #sol-frame-ic::-webkit-scrollbar-thumb {
    background-color: #b77f12;
}

/*  PLACEHOLDERS START  */
/* -------------------- */

input#ust-ara-input::-webkit-input-placeholder {
  color: #b2954f;
}

textarea#entry-textarea::-webkit-input-placeholder {
  color: #ceab5a;
}

textarea#entry-textarea::-moz-placeholder { /* Firefox 19+ */
  color: #ceab5a;
  opacity: 1;
}
textarea#entry-textarea:-ms-input-placeholder { /* IE 10+ */
  color: #ceab5a;
}
textarea#entry-textarea:-moz-placeholder { /* Firefox 18- */
  color: #ceab5a;
  opacity: 1;
}

input#ust-ara-input::-moz-placeholder { /* Firefox 19+ */
  color: #b2954f;
  opacity: 1;
}
input#ust-ara-input:-ms-input-placeholder { /* IE 10+ */
  color: #b2954f;
}
input#ust-ara-input:-moz-placeholder { /* Firefox 18- */
  color: #b2954f;
  opacity: 1;
}

/* ----------------- */
/*  PLACEHOLDERS END  */

.sayfalama-deaktif {
    color: #666666!important;
}

.sol-frame-devami-btn a{
    color: #851114;
}
}
