css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("canadiantire.ca") {
  
  /* remove store navigation flyout bar from between header and product */
  #global-header__nav-secondary-bar
  {
    display: none !important;
  }
  
  /* remove empty space after removing flyout bar*/
  .general-header.general-header-block .global-header
  {
    min-height: 125px !important;
  }
  
  /* remove empty space after removing flyout bar*/
  .general-header.general-header-block
  {
    min-height: 125px !important;
  }
  
  /* make header (CT logo, search box, current store, etc.) scroll offscreen */
  .global-header--sticky
  {
    position: static !important;
  }
  
  /* advert for credit */
  .assortment-brand-header
  {
    display: none !important;
  }
  
  /* poorly placed website usage links */
  .column__1506716477874
  {
    display: none !important;
  }
  
  /* excessive space above and below */
  .pdp-product-image-and-buy-box
  {
    padding-top: 0px !important;
    padding-bottom: 0px !important;
  }
  
  /* more wasted space */
  .pdp-product-image-and-buy-box__inner
  {
    margin-bottom: 0px !important;
  }
  
  /* related products should not come between photo+price and description */
  div.parbase.section.recommendations
  {
    display: none !important;
  }
  
  /* remove wasted space */
  div.column-control.parbase.section
  {
    display: none !important;
  }
  
  /* reduce excessive space above the word 'Details' */
  .section-header---1481559436590
  {
    padding-top: 5px !important;
  }
  
  /* reduce excessive space below the word 'Details' */
  .section-header-paddings
  {
    margin-bottom: 5px !important;
  }
  
  /* reduce excessive space above the word 'Features' list */
  .pdp-details-features
  {
    padding-top: 5px !important;
  }
  
}
